import React, { useState, useEffect } from 'react';
import { formatCurrency, formatDateTime } from '../../utils/formatters';

interface MarginStatement {
  id: number;
  statementId: string;
  ccpName: string;
  memberFirm: string;
  accountNumber: string;
  statementDate: string;
  currency: string;
  format: string;
  fileName: string;
  fileSize: number;
  status: string;
  createdAt: string;
  updatedAt: string;
  processedAt: string | null;
  errorMessage: string;
  retryCount: number;
  variationMargin?: number;
  initialMargin?: number;
  totalPositions?: number;
}

interface MarginPosition {
  id: number;
  positionId: string;
  ccpAccount: string;
  product: string;
  variationMargin: number;
  initialMargin: number;
  currency: string;
}

interface MarginDashboardProps {
  asOfDate: string;
  onRefresh: () => void;
}

const MarginDashboard: React.FC<MarginDashboardProps> = ({
  asOfDate,
  onRefresh
}) => {
  const [statements, setStatements] = useState<MarginStatement[]>([]);
  const [selectedStatement, setSelectedStatement] = useState<MarginStatement | null>(null);
  const [positions, setPositions] = useState<MarginPosition[]>([]);
  const [loading, setLoading] = useState(true);
  const [loadingPositions, setLoadingPositions] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [filter, setFilter] = useState<string>('ALL');

  const statusFilters = ['ALL', 'PROCESSED', 'PENDING', 'FAILED'];

  useEffect(() => {
    loadStatements();
  }, [asOfDate]); // eslint-disable-line react-hooks/exhaustive-deps

  const loadStatements = async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await fetch(`/api/margin-statements?asOfDate=${asOfDate}`);
      if (!response.ok) {
        throw new Error('Failed to load statements');
      }

      const data = await response.json();
      setStatements(data);
    } catch (err: any) {
      setError(err.message || 'Failed to load statements');
    } finally {
      setLoading(false);
    }
  };

  const loadPositions = async (statementId: number) => {
    try {
      setLoadingPositions(true);
      const response = await fetch(`/api/margin-statements/${statementId}/positions`);
      if (!response.ok) {
        throw new Error('Failed to load positions');
      }
      const data = await response.json();
      setPositions(data);
    } catch (err: any) {
      console.error('Failed to load positions:', err);
      setPositions([]);
    } finally {
      setLoadingPositions(false);
    }
  };

  const formatFileSize = (bytes: number): string => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'PENDING':
        return 'text-yellow-400';
      case 'PROCESSING':
        return 'text-blue-400';
      case 'PROCESSED':
        return 'text-green-400';
      case 'FAILED':
        return 'text-red-400';
      case 'DISPUTED':
        return 'text-orange-400';
      default:
        return 'text-fd-text-muted';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'PENDING':
        return (
          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z"></path>
          </svg>
        );
      case 'PROCESSING':
        return (
          <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-current"></div>
        );
      case 'PROCESSED':
        return (
          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z"></path>
          </svg>
        );
      case 'FAILED':
        return (
          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path>
          </svg>
        );
      default:
        return null;
    }
  };

  const filteredStatements = statements.filter(statement => 
    filter === 'ALL' || statement.status === filter
  );

  const handleStatementClick = (statement: MarginStatement) => {
    setSelectedStatement(statement);
    if (statement.status === 'PROCESSED') {
      loadPositions(statement.id);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-400"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-500/20 border border-red-500/50 rounded-lg p-4">
        <div className="flex items-center">
          <svg className="w-5 h-5 text-red-400 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"></path>
          </svg>
          <div>
            <h3 className="text-sm font-medium text-red-400">Error Loading Statements</h3>
            <p className="text-sm text-red-300 mt-1">{error}</p>
          </div>
        </div>
        <button
          onClick={loadStatements}
          className="mt-3 px-3 py-1 bg-red-500/30 text-red-400 rounded text-sm hover:bg-red-500/40 transition-colors"
        >
          Retry
        </button>
      </div>
    );
  }

  return (
    <div className="bg-fd-darker rounded-lg border border-fd-border">
      {/* Header */}
      <div className="px-6 py-4 border-b border-fd-border">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <svg className="w-8 h-8 text-blue-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 17v-2m3 2v-4m3 4v-6m2 10H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"></path>
            </svg>
            <div>
              <h3 className="text-xl font-semibold text-fd-text">Margin Statements</h3>
              <p className="text-sm text-fd-text-muted">Daily VM/IM positions from CCPs</p>
            </div>
          </div>

          <div className="flex items-center space-x-3">
            <span className="text-sm text-fd-text-muted">As of {asOfDate}</span>
            <button
              onClick={() => { loadStatements(); onRefresh(); }}
              className="p-2 text-fd-text-muted hover:text-fd-text hover:bg-fd-border/50 rounded-md transition-colors"
              title="Refresh statements"
            >
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15"></path>
              </svg>
            </button>
          </div>
        </div>

        {/* Filter Tabs */}
        <div className="flex space-x-1 bg-fd-dark rounded-lg p-1 mt-4">
          {statusFilters.map(status => (
            <button
              key={status}
              onClick={() => setFilter(status)}
              className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                filter === status
                  ? 'bg-blue-500 text-white'
                  : 'text-fd-text-muted hover:text-fd-text hover:bg-fd-border/50'
              }`}
            >
              {status}
              <span className="ml-1 text-xs">
                ({status === 'ALL' ? statements.length : statements.filter(s => s.status === status).length})
              </span>
            </button>
          ))}
        </div>
      </div>

      {/* Statements Grid */}
      <div className="p-6">
        {filteredStatements.length === 0 ? (
          <div className="text-center py-12">
            <svg className="mx-auto h-12 w-12 text-fd-text-muted" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 17v-2m3 2v-4m3 4v-6m2 10H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"></path>
            </svg>
            <h3 className="mt-2 text-sm font-medium text-fd-text">No margin statements found</h3>
            <p className="mt-1 text-sm text-fd-text-muted">
              {filter === 'ALL' ? `No statements available for ${asOfDate}` : `No statements with status: ${filter}`}
            </p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {filteredStatements.map((statement) => (
              <div
                key={statement.id}
                className="bg-fd-dark rounded-lg p-4 border border-fd-border hover:border-blue-500/50 cursor-pointer transition-all duration-200 hover:shadow-lg"
                onClick={() => handleStatementClick(statement)}
              >
                {/* Statement Header */}
                <div className="flex items-center justify-between mb-3">
                  <div className="flex items-center space-x-2">
                    <div className={`flex items-center space-x-1 ${getStatusColor(statement.status)}`}>
                      {getStatusIcon(statement.status)}
                      <span className="text-xs font-medium uppercase">{statement.status}</span>
                    </div>
                  </div>
                  <span className="text-xs text-fd-text-muted">
                    {new Date(statement.statementDate).toLocaleDateString()}
                  </span>
                </div>

                {/* Statement Details */}
                <div className="space-y-2">
                  <div>
                    <h4 className="font-medium text-fd-text truncate">{statement.statementId}</h4>
                    <p className="text-sm text-fd-text-muted">{statement.ccpName}</p>
                  </div>

                  <div className="flex justify-between text-sm">
                    <span className="text-fd-text-muted">Account:</span>
                    <span className="text-fd-text font-mono">{statement.accountNumber}</span>
                  </div>

                  {statement.status === 'PROCESSED' && (
                    <>
                      {statement.variationMargin !== undefined && (
                        <div className="flex justify-between text-sm">
                          <span className="text-fd-text-muted">VM:</span>
                          <span className={`font-medium ${statement.variationMargin >= 0 ? 'text-green-400' : 'text-red-400'}`}>
                            {formatCurrency(statement.variationMargin)}
                          </span>
                        </div>
                      )}
                      {statement.initialMargin !== undefined && (
                        <div className="flex justify-between text-sm">
                          <span className="text-fd-text-muted">IM:</span>
                          <span className="text-fd-text font-medium">
                            {formatCurrency(statement.initialMargin)}
                          </span>
                        </div>
                      )}
                      {statement.totalPositions !== undefined && (
                        <div className="flex justify-between text-sm">
                          <span className="text-fd-text-muted">Positions:</span>
                          <span className="text-fd-text">{statement.totalPositions}</span>
                        </div>
                      )}
                    </>
                  )}

                  <div className="flex justify-between text-xs text-fd-text-muted">
                    <span>{formatFileSize(statement.fileSize)} {statement.format}</span>
                    <span>{formatDateTime(statement.createdAt)}</span>
                  </div>
                </div>

                {statement.errorMessage && (
                  <div className="mt-2 p-2 bg-red-500/20 border border-red-500/50 rounded text-xs text-red-400">
                    {statement.errorMessage.substring(0, 100)}...
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Statement Detail Modal */}
      {selectedStatement && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-fd-darker rounded-lg shadow-lg border border-fd-border p-6 max-w-4xl w-full mx-4 max-h-[90vh] overflow-y-auto">
            <div className="flex items-center justify-between mb-6">
              <h3 className="text-xl font-bold text-fd-text">{selectedStatement.statementId}</h3>
              <button 
                onClick={() => setSelectedStatement(null)}
                className="text-fd-text-muted hover:text-fd-text transition-colors"
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path>
                </svg>
              </button>
            </div>

            {/* Statement Info */}
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6 text-sm">
              <div>
                <span className="text-fd-text-muted">CCP:</span>
                <div className="text-fd-text font-medium">{selectedStatement.ccpName}</div>
              </div>
              <div>
                <span className="text-fd-text-muted">Account:</span>
                <div className="text-fd-text font-mono">{selectedStatement.accountNumber}</div>
              </div>
              <div>
                <span className="text-fd-text-muted">Date:</span>
                <div className="text-fd-text">{new Date(selectedStatement.statementDate).toLocaleDateString()}</div>
              </div>
              <div>
                <span className="text-fd-text-muted">Status:</span>
                <div className={`flex items-center space-x-1 ${getStatusColor(selectedStatement.status)}`}>
                  {getStatusIcon(selectedStatement.status)}
                  <span className="font-medium">{selectedStatement.status}</span>
                </div>
              </div>
            </div>

            {/* Positions Table */}
            {selectedStatement.status === 'PROCESSED' && (
              <div className="border border-fd-border rounded-lg">
                <div className="px-4 py-3 border-b border-fd-border bg-fd-dark">
                  <h4 className="font-medium text-fd-text">Margin Positions</h4>
                </div>

                {loadingPositions ? (
                  <div className="flex items-center justify-center py-8">
                    <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-400"></div>
                  </div>
                ) : positions.length > 0 ? (
                  <div className="overflow-x-auto">
                    <table className="w-full">
                      <thead className="bg-fd-dark text-xs">
                        <tr>
                          <th className="px-4 py-2 text-left text-fd-text-muted">Position</th>
                          <th className="px-4 py-2 text-left text-fd-text-muted">Account</th>
                          <th className="px-4 py-2 text-left text-fd-text-muted">Product</th>
                          <th className="px-4 py-2 text-right text-fd-text-muted">VM</th>
                          <th className="px-4 py-2 text-right text-fd-text-muted">IM</th>
                          <th className="px-4 py-2 text-right text-fd-text-muted">Currency</th>
                        </tr>
                      </thead>
                      <tbody className="text-sm">
                        {positions.map((position) => (
                          <tr key={position.id} className="border-t border-fd-border hover:bg-fd-dark/50">
                            <td className="px-4 py-2 text-fd-text font-mono text-xs">{position.positionId}</td>
                            <td className="px-4 py-2 text-fd-text">{position.ccpAccount}</td>
                            <td className="px-4 py-2 text-fd-text">{position.product}</td>
                            <td className={`px-4 py-2 text-right font-medium ${position.variationMargin >= 0 ? 'text-green-400' : 'text-red-400'}`}>
                              {formatCurrency(position.variationMargin)}
                            </td>
                            <td className="px-4 py-2 text-right text-fd-text font-medium">
                              {formatCurrency(position.initialMargin)}
                            </td>
                            <td className="px-4 py-2 text-right text-fd-text-muted">{position.currency}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                ) : (
                  <div className="text-center py-8 text-fd-text-muted">
                    No positions found
                  </div>
                )}
              </div>
            )}

            {selectedStatement.errorMessage && (
              <div className="mt-4 bg-red-500/20 border border-red-500/50 rounded-lg p-3">
                <div className="text-red-400 text-sm font-medium mb-1">Error Message:</div>
                <div className="text-red-300 text-sm">{selectedStatement.errorMessage}</div>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default MarginDashboard;

import React, { useState } from 'react';
import { formatCurrency, formatNumber } from '../../utils/formatters';

interface SimmBucketData {
  bucketNumber: string;
  assetClass: string;
  initialMargin: number;
  sensitivities: number;
  delta: number;
  vega: number;
  curvature: number;
}

interface SimmCalculation {
  id: string;
  portfolioId: string;
  calculationDate: string;
  totalInitialMargin: number;
  currency: string;
  buckets: SimmBucketData[];
  parametersVersion: string;
  calculationStatus: 'COMPLETED' | 'FAILED' | 'PENDING';
}

interface SimmDashboardProps {
  simmCalculations: SimmCalculation[];
  asOfDate: string;
  onRefresh: () => void;
}

const SimmDashboard: React.FC<SimmDashboardProps> = ({
  simmCalculations,
  asOfDate,
  onRefresh
}) => {
  const [selectedCalculation, setSelectedCalculation] = useState<SimmCalculation | null>(
    simmCalculations.length > 0 ? simmCalculations[0] : null
  );
  const [expandedBuckets, setExpandedBuckets] = useState<Set<string>>(new Set());
  const [groupBy, setGroupBy] = useState<'assetClass' | 'bucket'>('assetClass');

  const toggleBucketExpansion = (bucketId: string) => {
    const newExpanded = new Set(expandedBuckets);
    if (newExpanded.has(bucketId)) {
      newExpanded.delete(bucketId);
    } else {
      newExpanded.add(bucketId);
    }
    setExpandedBuckets(newExpanded);
  };

  const getGroupedBuckets = () => {
    if (!selectedCalculation) return {};

    return selectedCalculation.buckets.reduce((groups, bucket) => {
      const key = groupBy === 'assetClass' ? bucket.assetClass : bucket.bucketNumber;
      if (!groups[key]) {
        groups[key] = [];
      }
      groups[key].push(bucket);
      return groups;
    }, {} as Record<string, SimmBucketData[]>);
  };

  const getAssetClassTotals = () => {
    if (!selectedCalculation) return {};

    return selectedCalculation.buckets.reduce((totals, bucket) => {
      const assetClass = bucket.assetClass;
      if (!totals[assetClass]) {
        totals[assetClass] = {
          initialMargin: 0,
          sensitivities: 0,
          bucketCount: 0
        };
      }
      totals[assetClass].initialMargin += bucket.initialMargin;
      totals[assetClass].sensitivities += bucket.sensitivities;
      totals[assetClass].bucketCount += 1;
      return totals;
    }, {} as Record<string, { initialMargin: number; sensitivities: number; bucketCount: number }>);
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'COMPLETED': return 'text-green-400';
      case 'FAILED': return 'text-red-400';
      case 'PENDING': return 'text-yellow-400';
      default: return 'text-fd-text-muted';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'COMPLETED':
        return (
          <svg className="w-4 h-4 text-green-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7"></path>
          </svg>
        );
      case 'FAILED':
        return (
          <svg className="w-4 h-4 text-red-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path>
          </svg>
        );
      case 'PENDING':
        return (
          <svg className="w-4 h-4 text-yellow-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z"></path>
          </svg>
        );
      default:
        return null;
    }
  };

  return (
    <div className="bg-fd-darker rounded-lg border border-fd-border p-6">
      {/* Header */}
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center space-x-3">
          <svg className="w-8 h-8 text-purple-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 7h6m0 10v-3m-3 3h.01M9 17h.01M9 14h.01M12 14h.01M15 11h.01M12 11h.01M9 11h.01M7 21h10a2 2 0 002-2V5a2 2 0 00-2-2H7a2 2 0 00-2 2v14a2 2 0 002 2z"></path>
          </svg>
          <div>
            <h3 className="text-xl font-semibold text-fd-text">SIMM Calculations</h3>
            <p className="text-sm text-fd-text-muted">Initial Margin Model</p>
          </div>
        </div>
        <div className="flex items-center space-x-3">
          <span className="text-sm text-fd-text-muted">As of {asOfDate}</span>
          <button
            onClick={onRefresh}
            className="p-2 text-fd-text-muted hover:text-fd-text hover:bg-fd-border/50 rounded-md transition-colors"
            title="Refresh SIMM data"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15"></path>
            </svg>
          </button>
        </div>
      </div>

      {simmCalculations.length === 0 ? (
        <div className="text-center py-8">
          <svg className="w-12 h-12 text-fd-text-muted mx-auto mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"></path>
          </svg>
          <p className="text-fd-text-muted">No SIMM calculations available</p>
          <p className="text-sm text-fd-text-muted mt-1">Upload CRIF files to generate calculations</p>
        </div>
      ) : (
        <>
          {/* Calculation Selector */}
          <div className="flex items-center space-x-4 mb-6">
            <label className="text-sm font-medium text-fd-text">Calculation:</label>
            <select
              value={selectedCalculation?.id || ''}
              onChange={(e) => {
                const calc = simmCalculations.find(c => c.id === e.target.value);
                setSelectedCalculation(calc || null);
              }}
              className="bg-fd-dark border border-fd-border rounded-md px-3 py-2 text-fd-text focus:outline-none focus:ring-2 focus:ring-purple-500"
            >
              {simmCalculations.map((calc) => (
                <option key={calc.id} value={calc.id}>
                  {calc.portfolioId} - {calc.calculationDate} ({calc.parametersVersion})
                </option>
              ))}
            </select>
          </div>

          {selectedCalculation && (
            <>
              {/* Summary Card */}
              <div className="bg-fd-dark rounded-lg p-6 border border-fd-border mb-6">
                <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
                  <div>
                    <p className="text-sm font-medium text-fd-text-muted">Total Initial Margin</p>
                    <p className="text-2xl font-bold text-fd-text mt-1">
                      {formatCurrency(selectedCalculation.totalInitialMargin)}
                    </p>
                  </div>
                  <div>
                    <p className="text-sm font-medium text-fd-text-muted">Portfolio</p>
                    <p className="text-lg font-semibold text-fd-text mt-1">
                      {selectedCalculation.portfolioId}
                    </p>
                  </div>
                  <div>
                    <p className="text-sm font-medium text-fd-text-muted">Parameters Version</p>
                    <p className="text-lg font-semibold text-fd-text mt-1">
                      {selectedCalculation.parametersVersion}
                    </p>
                  </div>
                  <div>
                    <p className="text-sm font-medium text-fd-text-muted">Status</p>
                    <div className="flex items-center space-x-2 mt-1">
                      {getStatusIcon(selectedCalculation.calculationStatus)}
                      <span className={`text-lg font-semibold ${getStatusColor(selectedCalculation.calculationStatus)}`}>
                        {selectedCalculation.calculationStatus}
                      </span>
                    </div>
                  </div>
                </div>
              </div>

              {/* Asset Class Summary */}
              <div className="bg-fd-dark rounded-lg p-6 border border-fd-border mb-6">
                <h4 className="text-lg font-medium text-fd-text mb-4">Asset Class Breakdown</h4>
                <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-5 gap-4">
                  {Object.entries(getAssetClassTotals()).map(([assetClass, totals]) => (
                    <div key={assetClass} className="bg-fd-darker rounded-lg p-4">
                      <p className="text-sm font-medium text-fd-text-muted">{assetClass}</p>
                      <p className="text-lg font-bold text-fd-text mt-1">
                        {formatCurrency(totals.initialMargin)}
                      </p>
                      <p className="text-xs text-fd-text-muted mt-1">
                        {totals.bucketCount} bucket{totals.bucketCount !== 1 ? 's' : ''}
                      </p>
                    </div>
                  ))}
                </div>
              </div>

              {/* Bucket Details */}
              <div className="bg-fd-dark rounded-lg border border-fd-border">
                <div className="px-6 py-4 border-b border-fd-border">
                  <div className="flex items-center justify-between">
                    <h4 className="text-lg font-medium text-fd-text">
                      Bucket Details ({selectedCalculation.buckets.length})
                    </h4>
                    <div className="flex items-center space-x-2">
                      <label className="text-sm text-fd-text-muted">Group by:</label>
                      <select
                        value={groupBy}
                        onChange={(e) => setGroupBy(e.target.value as 'assetClass' | 'bucket')}
                        className="bg-fd-darker border border-fd-border rounded-md px-3 py-1 text-sm text-fd-text focus:outline-none focus:ring-2 focus:ring-purple-500"
                      >
                        <option value="assetClass">Asset Class</option>
                        <option value="bucket">Bucket</option>
                      </select>
                    </div>
                  </div>
                </div>

                <div className="overflow-hidden">
                  {Object.entries(getGroupedBuckets()).map(([groupKey, buckets]) => (
                    <div key={groupKey} className="border-b border-fd-border last:border-b-0">
                      {/* Group Header */}
                      <div className="px-6 py-3 bg-fd-darker/50">
                        <h5 className="font-medium text-fd-text">
                          {groupBy === 'assetClass' ? `${groupKey} Asset Class` : `Bucket ${groupKey}`}
                        </h5>
                      </div>

                      {/* Bucket Rows */}
                      {buckets.map((bucket) => (
                        <div key={`${bucket.assetClass}-${bucket.bucketNumber}`}>
                          <div
                            className="px-6 py-4 hover:bg-fd-darker/30 cursor-pointer transition-colors"
                            onClick={() => toggleBucketExpansion(`${bucket.assetClass}-${bucket.bucketNumber}`)}
                          >
                            <div className="flex items-center justify-between">
                              <div className="flex items-center space-x-4">
                                <svg 
                                  className={`w-4 h-4 text-fd-text-muted transition-transform ${
                                    expandedBuckets.has(`${bucket.assetClass}-${bucket.bucketNumber}`) ? 'rotate-90' : ''
                                  }`} 
                                  fill="none" 
                                  stroke="currentColor" 
                                  viewBox="0 0 24 24"
                                >
                                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 5l7 7-7 7"></path>
                                </svg>
                                <div>
                                  <p className="font-medium text-fd-text">
                                    {groupBy === 'assetClass' ? `Bucket ${bucket.bucketNumber}` : bucket.assetClass}
                                  </p>
                                  <p className="text-sm text-fd-text-muted">
                                    {formatNumber(bucket.sensitivities)} sensitivities
                                  </p>
                                </div>
                              </div>
                              <div className="text-right">
                                <p className="font-medium text-fd-text">
                                  {formatCurrency(bucket.initialMargin)}
                                </p>
                                <p className="text-sm text-fd-text-muted">
                                  {formatNumber((bucket.initialMargin / selectedCalculation.totalInitialMargin) * 100, 1)}%
                                </p>
                              </div>
                            </div>
                          </div>

                          {/* Expanded Risk Details */}
                          {expandedBuckets.has(`${bucket.assetClass}-${bucket.bucketNumber}`) && (
                            <div className="px-6 pb-4 bg-fd-darker/20">
                              <div className="grid grid-cols-3 gap-4 pt-4">
                                <div className="bg-fd-dark rounded-md p-3">
                                  <p className="text-xs text-fd-text-muted uppercase tracking-wide">Delta</p>
                                  <p className="text-lg font-semibold text-fd-text mt-1">
                                    {formatCurrency(bucket.delta)}
                                  </p>
                                </div>
                                <div className="bg-fd-dark rounded-md p-3">
                                  <p className="text-xs text-fd-text-muted uppercase tracking-wide">Vega</p>
                                  <p className="text-lg font-semibold text-fd-text mt-1">
                                    {formatCurrency(bucket.vega)}
                                  </p>
                                </div>
                                <div className="bg-fd-dark rounded-md p-3">
                                  <p className="text-xs text-fd-text-muted uppercase tracking-wide">Curvature</p>
                                  <p className="text-lg font-semibold text-fd-text mt-1">
                                    {formatCurrency(bucket.curvature)}
                                  </p>
                                </div>
                              </div>
                            </div>
                          )}
                        </div>
                      ))}
                    </div>
                  ))}
                </div>
              </div>
            </>
          )}
        </>
      )}
    </div>
  );
};

export default SimmDashboard;

import React, { useEffect, useState } from 'react';
import { lifecycleService } from '../../services/lifecycleService';
import { AccrualEvent } from '../../types/lifecycle';

interface Props {
  tradeId: number;
}

export const AccrualHistoryPanel: React.FC<Props> = ({ tradeId }) => {
  const [accruals, setAccruals] = useState<AccrualEvent[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [range, setRange] = useState({ start: '', end: '' });
  const [cumulative, setCumulative] = useState<number | null>(null);

  const loadAccruals = async () => {
    if (!range.start || !range.end) return;
    setLoading(true);
    setError(null);
    try {
      const data = await lifecycleService.getAccrualEvents(tradeId, range.start, range.end);
      setAccruals(data);
    } catch (e: any) {
      setError(e.message || 'Failed to load accrual events');
    } finally {
      setLoading(false);
    }
  };

  const loadCumulative = async () => {
    try {
      const cum = await lifecycleService.getCumulativeAccrual(tradeId);
      setCumulative(cum);
    } catch {}
  };

  useEffect(() => {
    loadCumulative();
  }, [tradeId, loadCumulative]);

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <h4 className="text-fd-text font-semibold">Accrual History</h4>
        <div className="flex items-center space-x-2">
          <input
            type="date"
            value={range.start}
            onChange={(e) => setRange((r) => ({ ...r, start: e.target.value }))}
            className="bg-fd-dark border border-fd-border rounded px-2 py-1 text-xs text-fd-text"
          />
          <span className="text-fd-text-muted text-xs">to</span>
          <input
            type="date"
            value={range.end}
            onChange={(e) => setRange((r) => ({ ...r, end: e.target.value }))}
            className="bg-fd-dark border border-fd-border rounded px-2 py-1 text-xs text-fd-text"
          />
          <button
            onClick={loadAccruals}
            disabled={!range.start || !range.end}
            className="px-3 py-1 text-xs bg-fd-green text-fd-dark rounded font-medium disabled:opacity-40"
          >
            Load
          </button>
        </div>
      </div>
      {cumulative !== null && (
        <div className="bg-fd-dark border border-fd-border rounded p-3 flex items-center justify-between">
          <span className="text-fd-text-muted text-xs uppercase tracking-wide">
            Cumulative Accrual
          </span>
          <span className="text-fd-green font-semibold">
            {cumulative.toLocaleString(undefined, {
              minimumFractionDigits: 2,
              maximumFractionDigits: 2,
            })}
          </span>
        </div>
      )}
      {loading && <div className="text-fd-text-muted text-sm">Loading accruals...</div>}
      {error && <div className="text-red-400 text-sm">{error}</div>}
      {!loading && accruals.length === 0 && !error && range.start && range.end && (
        <div className="text-fd-text-muted text-sm">No accruals in range.</div>
      )}
      {accruals.length > 0 && (
        <div className="overflow-x-auto border border-fd-border rounded max-h-72 overflow-y-auto">
          <table className="min-w-full text-xs">
            <thead className="bg-fd-dark sticky top-0">
              <tr className="text-fd-text-muted">
                <th className="px-3 py-2 text-left font-medium">Date</th>
                <th className="px-3 py-2 text-left font-medium">Accrual</th>
                <th className="px-3 py-2 text-left font-medium">Cumulative</th>
                <th className="px-3 py-2 text-left font-medium">Notional</th>
                <th className="px-3 py-2 text-left font-medium">Version</th>
              </tr>
            </thead>
            <tbody>
              {accruals.map((a) => (
                <tr key={a.id} className="border-t border-fd-border hover:bg-fd-darker/40">
                  <td className="px-3 py-1 text-fd-text font-mono">{a.accrualDate}</td>
                  <td className="px-3 py-1 text-fd-green font-medium">
                    {a.accrualAmount.toLocaleString(undefined, { minimumFractionDigits: 2 })}
                  </td>
                  <td className="px-3 py-1 text-fd-text-muted">
                    {a.cumulativeAccrual.toLocaleString(undefined, { minimumFractionDigits: 2 })}
                  </td>
                  <td className="px-3 py-1 text-fd-text-muted">
                    {a.notionalAmount.toLocaleString()}
                  </td>
                  <td className="px-3 py-1 text-fd-text-muted">{a.tradeVersion}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { apiUrl } from '../../config/api';
import MarginPositionsModal from './MarginPositionsModal';

interface MarginStatement {
  id: number;
  statementId: string;
  ccpName: string;
  memberFirm: string;
  accountNumber: string;
  statementDate: string;
  currency: string;
  format: string;
  fileName: string;
  fileSize: number;
  status: string;
  createdAt: string;
  updatedAt: string;
  processedAt: string | null;
  errorMessage: string;
  retryCount: number;
}

interface StatementListProps {
  refreshTrigger?: number;
}

const StatementList: React.FC<StatementListProps> = ({ refreshTrigger }) => {
  const [statements, setStatements] = useState<MarginStatement[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [filter, setFilter] = useState<string>('ALL');
  const [selectedStatement, setSelectedStatement] = useState<MarginStatement | null>(null);
  const [showPositionsModal, setShowPositionsModal] = useState(false);

  const statusFilters = ['ALL', 'PENDING', 'PROCESSING', 'PROCESSED', 'FAILED', 'DISPUTED', 'RETRYING'];

  useEffect(() => {
    loadStatements();
  }, [refreshTrigger]);

  const loadStatements = async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await fetch('/api/margin-statements');
      if (!response.ok) {
        throw new Error('Failed to load statements');
      }

      const data = await response.json();
      setStatements(data);
    } catch (err: any) {
      setError(err.message || 'Failed to load statements');
    } finally {
      setLoading(false);
    }
  };

  const handleRetryFailed = async () => {
    try {
      const response = await fetch('/api/margin-statements/retry-failed', {
        method: 'POST'
      });

      if (!response.ok) {
        throw new Error('Failed to retry statements');
      }

      // Refresh the list after retry
      setTimeout(() => {
        loadStatements();
      }, 1000);

    } catch (err: any) {
      setError(err.message || 'Failed to retry statements');
    }
  };

  const formatDateTime = (dateString: string) => {
    if (!dateString) return '-';
    return new Date(dateString).toLocaleString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const formatFileSize = (bytes: number): string => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'PENDING':
        return 'bg-yellow-100 text-yellow-800';
      case 'PROCESSING':
        return 'bg-blue-100 text-blue-800';
      case 'PROCESSED':
        return 'bg-green-100 text-green-800';
      case 'FAILED':
        return 'bg-red-100 text-red-800';
      case 'DISPUTED':
        return 'bg-orange-100 text-orange-800';
      case 'RETRYING':
        return 'bg-purple-100 text-purple-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'PENDING':
        return (
          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z"></path>
          </svg>
        );
      case 'PROCESSING':
        return (
          <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-current"></div>
        );
      case 'PROCESSED':
        return (
          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z"></path>
          </svg>
        );
      case 'FAILED':
        return (
          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path>
          </svg>
        );
      case 'DISPUTED':
        return (
          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z"></path>
          </svg>
        );
      case 'RETRYING':
        return (
          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15"></path>
          </svg>
        );
      default:
        return null;
    }
  };

  const filteredStatements = statements.filter(statement => 
    filter === 'ALL' || statement.status === filter
  );

  const failedCount = statements.filter(s => s.status === 'FAILED').length;

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-fd-green"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 border border-red-200 rounded-md p-4">
        <div className="flex">
          <div className="flex-shrink-0">
            <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
            </svg>
          </div>
          <div className="ml-3">
            <h3 className="text-sm font-medium text-red-800">Error Loading Statements</h3>
            <div className="mt-2 text-sm text-red-700">
              <p>{error}</p>
            </div>
            <div className="mt-4">
              <button
                onClick={loadStatements}
                className="bg-red-100 px-3 py-2 rounded-md text-sm font-medium text-red-800 hover:bg-red-200"
              >
                Retry
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-fd-darker rounded-lg border border-fd-border">
      {/* Header */}
      <div className="p-6 border-b border-fd-border">
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center space-x-3">
            <div className="w-8 h-8 bg-green-500/20 rounded-full flex items-center justify-center">
              <svg className="w-5 h-5 text-green-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 17v-2m3 2v-4m3 4v-6m2 10H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"></path>
              </svg>
            </div>
            <h2 className="text-2xl font-bold text-fd-text">Margin Statements</h2>
          </div>

          <div className="flex items-center space-x-4">
            {failedCount > 0 && (
              <button
                onClick={handleRetryFailed}
                className="px-4 py-2 bg-orange-500 text-white rounded hover:bg-orange-600 transition-colors flex items-center space-x-2"
              >
                <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15"></path>
                </svg>
                <span>Retry Failed ({failedCount})</span>
              </button>
            )}

            <button
              onClick={loadStatements}
              className="px-4 py-2 bg-fd-green text-fd-dark rounded hover:bg-fd-green/80 transition-colors flex items-center space-x-2"
            >
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15"></path>
              </svg>
              <span>Refresh</span>
            </button>
          </div>
        </div>

        {/* Filter Tabs */}
        <div className="flex space-x-1 bg-fd-dark rounded-lg p-1">
          {statusFilters.map(status => (
            <button
              key={status}
              onClick={() => setFilter(status)}
              className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                filter === status
                  ? 'bg-fd-green text-fd-dark'
                  : 'text-fd-text-muted hover:text-fd-text hover:bg-fd-border/50'
              }`}
            >
              {status}
              {status !== 'ALL' && (
                <span className="ml-1 text-xs">
                  ({statements.filter(s => s.status === status).length})
                </span>
              )}
            </button>
          ))}
        </div>
      </div>

      {/* Statements Table */}
      <div className="overflow-hidden">
        <table className="min-w-full">
          <thead className="bg-fd-dark">
            <tr>
              <th className="px-4 py-3 text-left text-xs font-medium text-fd-text-muted uppercase tracking-wider">
                Statement
              </th>
              <th className="px-4 py-3 text-left text-xs font-medium text-fd-text-muted uppercase tracking-wider">
                CCP
              </th>
              <th className="px-4 py-3 text-left text-xs font-medium text-fd-text-muted uppercase tracking-wider">
                Date
              </th>
              <th className="px-4 py-3 text-left text-xs font-medium text-fd-text-muted uppercase tracking-wider">
                Status
              </th>
              <th className="px-4 py-3 text-left text-xs font-medium text-fd-text-muted uppercase tracking-wider">
                Created
              </th>
              <th className="px-4 py-3 text-left text-xs font-medium text-fd-text-muted uppercase tracking-wider">
                Actions
              </th>
            </tr>
          </thead>
          <tbody className="bg-fd-darker divide-y divide-fd-border">
            {filteredStatements.map((statement) => (
              <tr
                key={statement.id}
                className="hover:bg-fd-dark transition-colors cursor-pointer"
                onClick={() => setSelectedStatement(statement)}
              >
                <td className="px-4 py-4">
                  <div className="flex flex-col max-w-xs">
                    <div className="text-sm font-medium text-fd-text truncate">
                      {statement.statementId}
                    </div>
                    <div className="text-xs text-fd-text-muted truncate">
                      {statement.accountNumber}
                    </div>
                  </div>
                </td>
                <td className="px-4 py-4 whitespace-nowrap">
                  <div className="text-sm font-medium text-fd-text">
                    {statement.ccpName}
                  </div>
                  <div className="text-xs text-fd-text-muted">
                    {statement.currency}
                  </div>
                </td>
                <td className="px-4 py-4 whitespace-nowrap">
                  <div className="text-sm text-fd-text">
                    {new Date(statement.statementDate).toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: 'numeric' })}
                  </div>
                </td>
                <td className="px-4 py-4 whitespace-nowrap">
                  <span className={`inline-flex items-center px-2 py-0.5 rounded-full text-xs font-medium ${getStatusColor(statement.status)}`}>
                    <span className="mr-1">{getStatusIcon(statement.status)}</span>
                    {statement.status}
                  </span>
                </td>
                <td className="px-4 py-4 whitespace-nowrap text-sm text-fd-text-muted">
                  {formatDateTime(statement.createdAt).replace(', ', '\n').split('\n')[0]}
                </td>
                <td className="px-4 py-4 whitespace-nowrap text-sm">
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      setSelectedStatement(statement);
                    }}
                    className="text-fd-green hover:text-fd-green/80 font-medium"
                  >
                    View
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {filteredStatements.length === 0 && (
        <div className="text-center py-12">
          <svg
            className="mx-auto h-12 w-12 text-fd-text-muted"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M9 17v-2m3 2v-4m3 4v-6m2 10H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"
            />
          </svg>
          <h3 className="mt-2 text-sm font-medium text-fd-text">No statements found</h3>
          <p className="mt-1 text-sm text-fd-text-muted">
            {filter === 'ALL' ? 'Upload your first margin statement to get started.' : `No statements with status: ${filter}`}
          </p>
        </div>
      )}

      {/* Statement Detail Modal */}
      {selectedStatement && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-fd-darker rounded-lg shadow-fd border border-fd-border p-6 max-w-2xl w-full mx-4 max-h-[90vh] overflow-y-auto">
            <div className="flex items-center justify-between mb-6">
              <h3 className="text-xl font-bold text-fd-text">Statement Details</h3>
              <button 
                onClick={() => setSelectedStatement(null)}
                className="text-fd-text-muted hover:text-fd-text transition-colors"
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path>
                </svg>
              </button>
            </div>

            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4 text-sm">
                <div>
                  <span className="text-fd-text-muted">Statement ID:</span>
                  <span className="text-fd-text ml-2 font-mono">{selectedStatement.statementId}</span>
                </div>
                <div>
                  <span className="text-fd-text-muted">CCP:</span>
                  <span className="text-fd-text ml-2">{selectedStatement.ccpName}</span>
                </div>
                <div>
                  <span className="text-fd-text-muted">Member Firm:</span>
                  <span className="text-fd-text ml-2">{selectedStatement.memberFirm}</span>
                </div>
                <div>
                  <span className="text-fd-text-muted">Account:</span>
                  <span className="text-fd-text ml-2">{selectedStatement.accountNumber}</span>
                </div>
                <div>
                  <span className="text-fd-text-muted">Date:</span>
                  <span className="text-fd-text ml-2">{new Date(selectedStatement.statementDate).toLocaleDateString()}</span>
                </div>
                <div>
                  <span className="text-fd-text-muted">Currency:</span>
                  <span className="text-fd-text ml-2">{selectedStatement.currency}</span>
                </div>
                <div>
                  <span className="text-fd-text-muted">Format:</span>
                  <span className="text-fd-text ml-2">{selectedStatement.format}</span>
                </div>
                <div>
                  <span className="text-fd-text-muted">File Size:</span>
                  <span className="text-fd-text ml-2">{formatFileSize(selectedStatement.fileSize)}</span>
                </div>
              </div>

              {selectedStatement.errorMessage && (
                <div className="bg-red-500/20 border border-red-500/50 rounded-lg p-3">
                  <div className="text-red-400 text-sm font-medium mb-1">Error Message:</div>
                  <div className="text-red-300 text-sm">{selectedStatement.errorMessage}</div>
                </div>
              )}

              <div className="flex justify-end space-x-4 pt-4 border-t border-fd-border">
                <button
                  onClick={() => setSelectedStatement(null)}
                  className="px-4 py-2 bg-fd-border text-fd-text rounded hover:bg-fd-border/80 transition-colors"
                >
                  Close
                </button>
                {selectedStatement.status === 'PROCESSED' && (
                  <button
                    onClick={() => {
                      setShowPositionsModal(true);
                    }}
                    className="px-4 py-2 bg-fd-green text-fd-dark rounded hover:bg-fd-green/80 transition-colors"
                  >
                    View Positions
                  </button>
                )}
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Positions Modal */}
      {showPositionsModal && selectedStatement && (
        <MarginPositionsModal
          statementId={selectedStatement.id}
          statementName={`${selectedStatement.ccpName} - ${selectedStatement.statementId}`}
          onClose={() => setShowPositionsModal(false)}
        />
      )}
    </div>
  );
};

export default StatementList;

import React, { useState, useRef } from 'react';

interface StatementUploadProps {
  onUploadSuccess?: (statementId: number) => void;
  onUploadError?: (error: string) => void;
}

interface UploadFormData {
  statementId: string;
  ccpName: string;
  memberFirm: string;
  accountNumber: string;
  statementDate: string;
  currency: string;
  format: string;
}

const StatementUpload: React.FC<StatementUploadProps> = ({ onUploadSuccess, onUploadError }) => {
  const [formData, setFormData] = useState<UploadFormData>({
    statementId: '',
    ccpName: 'LCH',
    memberFirm: '',
    accountNumber: '',
    statementDate: '',
    currency: 'USD',
    format: 'CSV'
  });

  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [uploading, setUploading] = useState(false);
  const [dragActive, setDragActive] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState<string | null>(null);

  const fileInputRef = useRef<HTMLInputElement>(null);

  const ccpOptions = ['LCH', 'CME', 'EUREX', 'ICE'];
  const currencyOptions = ['USD', 'EUR', 'GBP', 'JPY', 'CHF'];
  const formatOptions = ['CSV', 'XML', 'JSON', 'PROPRIETARY'];

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    setError(null);
  };

  const handleDrag = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setDragActive(true);
    } else if (e.type === 'dragleave') {
      setDragActive(false);
    }
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      handleFileSelect(e.dataTransfer.files[0]);
    }
  };

  const handleFileSelect = (file: File) => {
    setSelectedFile(file);
    setError(null);
    setSuccess(null);

    // Auto-detect format based on file extension
    const extension = file.name.toLowerCase().split('.').pop();
    if (extension === 'csv') {
      setFormData(prev => ({ ...prev, format: 'CSV' }));
    } else if (extension === 'xml') {
      setFormData(prev => ({ ...prev, format: 'XML' }));
    } else if (extension === 'json') {
      setFormData(prev => ({ ...prev, format: 'JSON' }));
    }

    // Generate statement ID if not provided
    if (!formData.statementId) {
      const timestamp = new Date().toISOString().split('T')[0].replace(/-/g, '');
      const randomSuffix = Math.random().toString(36).substring(2, 8).toUpperCase();
      setFormData(prev => ({ 
        ...prev, 
        statementId: `${formData.ccpName}_${timestamp}_${randomSuffix}` 
      }));
    }
  };

  const handleFileInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      handleFileSelect(e.target.files[0]);
    }
  };

  const validateForm = (): string | null => {
    if (!selectedFile) return 'Please select a file to upload';
    if (!formData.statementId.trim()) return 'Statement ID is required';
    if (!formData.memberFirm.trim()) return 'Member firm is required';
    if (!formData.accountNumber.trim()) return 'Account number is required';
    if (!formData.statementDate) return 'Statement date is required';

    // Validate file size (50MB limit)
    if (selectedFile.size > 50 * 1024 * 1024) {
      return 'File size must be less than 50MB';
    }

    // Validate date is not in future
    const statementDate = new Date(formData.statementDate);
    if (statementDate > new Date()) {
      return 'Statement date cannot be in the future';
    }

    return null;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    const validationError = validateForm();
    if (validationError) {
      setError(validationError);
      return;
    }

    setUploading(true);
    setError(null);
    setSuccess(null);
    setUploadProgress(0);

    try {
      const formDataToSend = new FormData();
      formDataToSend.append('file', selectedFile!);
      formDataToSend.append('statementId', formData.statementId);
      formDataToSend.append('ccpName', formData.ccpName);
      formDataToSend.append('memberFirm', formData.memberFirm);
      formDataToSend.append('accountNumber', formData.accountNumber);
      formDataToSend.append('statementDate', formData.statementDate);
      formDataToSend.append('currency', formData.currency);
      formDataToSend.append('format', formData.format);

      // Simulate upload progress
      const progressInterval = setInterval(() => {
        setUploadProgress(prev => Math.min(prev + 10, 90));
      }, 200);

      const response = await fetch('/api/margin-statements/upload', {
        method: 'POST',
        body: formDataToSend
      });

      clearInterval(progressInterval);
      setUploadProgress(100);

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.error || 'Upload failed');
      }

      setSuccess(`Statement uploaded successfully! ID: ${result.statementId}`);

      // Reset form
      setSelectedFile(null);
      setFormData({
        statementId: '',
        ccpName: 'LCH',
        memberFirm: '',
        accountNumber: '',
        statementDate: '',
        currency: 'USD',
        format: 'CSV'
      });

      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }

      if (onUploadSuccess) {
        onUploadSuccess(result.statementId);
      }

    } catch (err: any) {
      setError(err.message || 'Upload failed');
      if (onUploadError) {
        onUploadError(err.message);
      }
    } finally {
      setUploading(false);
      setTimeout(() => setUploadProgress(0), 2000);
    }
  };

  const formatFileSize = (bytes: number): string => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  return (
    <div className="bg-fd-darker rounded-lg border border-fd-border p-6">
      <div className="flex items-center space-x-3 mb-6">
        <div className="w-8 h-8 bg-blue-500/20 rounded-full flex items-center justify-center">
          <svg className="w-5 h-5 text-blue-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12"></path>
          </svg>
        </div>
        <h2 className="text-2xl font-bold text-fd-text">Upload Margin Statement</h2>
      </div>

      <form onSubmit={handleSubmit} className="space-y-6">
        {/* File Upload Area */}
        <div className="space-y-4">
          <label className="block text-sm font-medium text-fd-text">Statement File</label>
          <div
            className={`relative border-2 border-dashed rounded-lg p-8 text-center transition-colors ${
              dragActive
                ? 'border-blue-400 bg-blue-400/10'
                : 'border-fd-border hover:border-fd-green'
            }`}
            onDragEnter={handleDrag}
            onDragLeave={handleDrag}
            onDragOver={handleDrag}
            onDrop={handleDrop}
          >
            <input
              ref={fileInputRef}
              type="file"
              onChange={handleFileInputChange}
              accept=".csv,.xml,.json,.txt"
              className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
            />

            {selectedFile ? (
              <div className="space-y-2">
                <div className="w-12 h-12 bg-green-500/20 rounded-full flex items-center justify-center mx-auto">
                  <svg className="w-6 h-6 text-green-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z"></path>
                  </svg>
                </div>
                <div className="text-fd-text font-medium">{selectedFile.name}</div>
                <div className="text-fd-text-muted text-sm">{formatFileSize(selectedFile.size)}</div>
                {uploadProgress > 0 && uploadProgress < 100 && (
                  <div className="w-full bg-fd-border rounded-full h-2 mt-2">
                    <div 
                      className="bg-blue-500 h-2 rounded-full transition-all duration-300"
                      style={{ width: `${uploadProgress}%` }}
                    ></div>
                  </div>
                )}
              </div>
            ) : (
              <div className="space-y-2">
                <div className="w-12 h-12 bg-fd-border rounded-full flex items-center justify-center mx-auto">
                  <svg className="w-6 h-6 text-fd-text-muted" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12"></path>
                  </svg>
                </div>
                <div className="text-fd-text">
                  <span className="font-medium">Click to upload</span> or drag and drop
                </div>
                <div className="text-fd-text-muted text-sm">
                  CSV, XML, JSON files up to 50MB
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Form Fields */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <label className="block text-sm font-medium text-fd-text mb-2">Statement ID</label>
            <input
              type="text"
              name="statementId"
              value={formData.statementId}
              onChange={handleInputChange}
              className="w-full px-3 py-2 bg-fd-dark border border-fd-border rounded-md text-fd-text focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="AUTO_GENERATED_001"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-fd-text mb-2">CCP Name</label>
            <select
              name="ccpName"
              value={formData.ccpName}
              onChange={handleInputChange}
              className="w-full px-3 py-2 bg-fd-dark border border-fd-border rounded-md text-fd-text focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              {ccpOptions.map(ccp => (
                <option key={ccp} value={ccp}>{ccp}</option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-fd-text mb-2">Member Firm</label>
            <input
              type="text"
              name="memberFirm"
              value={formData.memberFirm}
              onChange={handleInputChange}
              className="w-full px-3 py-2 bg-fd-dark border border-fd-border rounded-md text-fd-text focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Goldman Sachs"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-fd-text mb-2">Account Number</label>
            <input
              type="text"
              name="accountNumber"
              value={formData.accountNumber}
              onChange={handleInputChange}
              className="w-full px-3 py-2 bg-fd-dark border border-fd-border rounded-md text-fd-text focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="HOUSE-001"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-fd-text mb-2">Statement Date</label>
            <input
              type="date"
              name="statementDate"
              value={formData.statementDate}
              onChange={handleInputChange}
              className="w-full px-3 py-2 bg-fd-dark border border-fd-border rounded-md text-fd-text focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-fd-text mb-2">Currency</label>
            <select
              name="currency"
              value={formData.currency}
              onChange={handleInputChange}
              className="w-full px-3 py-2 bg-fd-dark border border-fd-border rounded-md text-fd-text focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              {currencyOptions.map(currency => (
                <option key={currency} value={currency}>{currency}</option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-fd-text mb-2">Format</label>
            <select
              name="format"
              value={formData.format}
              onChange={handleInputChange}
              className="w-full px-3 py-2 bg-fd-dark border border-fd-border rounded-md text-fd-text focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              {formatOptions.map(format => (
                <option key={format} value={format}>{format}</option>
              ))}
            </select>
          </div>
        </div>

        {/* Error/Success Messages */}
        {error && (
          <div className="bg-red-500/20 border border-red-500/50 rounded-lg p-3">
            <div className="flex items-center space-x-2">
              <svg className="w-5 h-5 text-red-400 flex-shrink-0" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z"></path>
              </svg>
              <span className="text-red-400 text-sm">{error}</span>
            </div>
          </div>
        )}

        {success && (
          <div className="bg-green-500/20 border border-green-500/50 rounded-lg p-3">
            <div className="flex items-center space-x-2">
              <svg className="w-5 h-5 text-green-400 flex-shrink-0" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z"></path>
              </svg>
              <span className="text-green-400 text-sm">{success}</span>
            </div>
          </div>
        )}

        {/* Submit Button */}
        <div className="flex justify-end space-x-4 pt-4 border-t border-fd-border">
          <button
            type="button"
            onClick={() => {
              setSelectedFile(null);
              setFormData({
                statementId: '',
                ccpName: 'LCH',
                memberFirm: '',
                accountNumber: '',
                statementDate: '',
                currency: 'USD',
                format: 'CSV'
              });
              setError(null);
              setSuccess(null);
              if (fileInputRef.current) {
                fileInputRef.current.value = '';
              }
            }}
            className="px-6 py-2 bg-fd-border text-fd-text rounded hover:bg-fd-border/80 transition-colors"
          >
            Clear
          </button>
          <button
            type="submit"
            disabled={uploading || !selectedFile}
            className="px-6 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition-colors disabled:opacity-50 disabled:cursor-not-allowed flex items-center space-x-2"
          >
            {uploading && (
              <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
            )}
            <span>{uploading ? 'Uploading...' : 'Upload Statement'}</span>
          </button>
        </div>
      </form>
    </div>
  );
};

export default StatementUpload;

import React, { useState, useEffect, useCallback } from 'react';
import {
  BondPortfolioConstituent,
  BasketPortfolioConstituent,
  CdsPortfolioConstituent,
} from '../../services/portfolioService';

interface EnhancedOverviewProps {
  portfolioId: number;
  cdsConstituents: CdsPortfolioConstituent[];
  bondConstituents: BondPortfolioConstituent[];
  basketConstituents: BasketPortfolioConstituent[];
  pricingData: any;
}

interface IssuerExposure {
  issuer: string;
  sector: string;
  bondNotional: number;
  cdsProtectionBought: number;
  cdsProtectionSold: number;
  netCreditExposure: number;
  hedgeRatio: number;
  hedgeStatus: 'OVER_HEDGED' | 'UNDER_HEDGED' | 'BALANCED' | 'UNHEDGED';
  recommendation: string;
}

interface SectorExposure {
  sector: string;
  notional: number;
  percentage: number;
  instrumentCount: number;
}

const EnhancedOverview: React.FC<EnhancedOverviewProps> = ({
  portfolioId,
  cdsConstituents,
  bondConstituents,
  basketConstituents,
  pricingData,
}) => {
  const [issuerExposures, setIssuerExposures] = useState<IssuerExposure[]>([]);
  const [sectorExposures, setSectorExposures] = useState<SectorExposure[]>([]);

  useEffect(() => {
    calculateExposures();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [cdsConstituents, bondConstituents, basketConstituents]);

  const calculateExposures = useCallback(() => {
    // Map to aggregate by issuer
    const issuerMap = new Map<string, IssuerExposure>();

    // Process bonds
    bondConstituents.forEach((bc) => {
      const issuer = bc.bond.issuer;
      if (!issuerMap.has(issuer)) {
        issuerMap.set(issuer, {
          issuer,
          sector: getSectorFromIssuer(issuer),
          bondNotional: 0,
          cdsProtectionBought: 0,
          cdsProtectionSold: 0,
          netCreditExposure: 0,
          hedgeRatio: 0,
          hedgeStatus: 'UNHEDGED',
          recommendation: '',
        });
      }
      const exposure = issuerMap.get(issuer)!;
      exposure.bondNotional += bc.bond.notional;
    });

    // Process CDS
    cdsConstituents.forEach((cc) => {
      const issuer = cc.trade.referenceEntity;
      if (!issuerMap.has(issuer)) {
        issuerMap.set(issuer, {
          issuer,
          sector: getSectorFromIssuer(issuer),
          bondNotional: 0,
          cdsProtectionBought: 0,
          cdsProtectionSold: 0,
          netCreditExposure: 0,
          hedgeRatio: 0,
          hedgeStatus: 'UNHEDGED',
          recommendation: '',
        });
      }
      const exposure = issuerMap.get(issuer)!;

      // Assuming BUY = buying protection (short credit), SELL = selling protection (long credit)
      // This is a simplification - you may need to check actual buySell field
      exposure.cdsProtectionBought += cc.trade.notionalAmount;
    });

    // Calculate net exposures and recommendations
    const exposures: IssuerExposure[] = [];
    issuerMap.forEach((exp) => {
      exp.netCreditExposure = exp.bondNotional - exp.cdsProtectionBought + exp.cdsProtectionSold;

      if (exp.bondNotional > 0) {
        exp.hedgeRatio = (exp.cdsProtectionBought / exp.bondNotional) * 100;

        if (exp.hedgeRatio > 110) {
          exp.hedgeStatus = 'OVER_HEDGED';
          exp.recommendation = `Consider reducing CDS protection by $${((exp.cdsProtectionBought - exp.bondNotional) / 1000000).toFixed(1)}M`;
        } else if (exp.hedgeRatio >= 90 && exp.hedgeRatio <= 110) {
          exp.hedgeStatus = 'BALANCED';
          exp.recommendation = 'Well hedged';
        } else if (exp.hedgeRatio > 0) {
          exp.hedgeStatus = 'UNDER_HEDGED';
          exp.recommendation = `Consider buying $${((exp.bondNotional - exp.cdsProtectionBought) / 1000000).toFixed(1)}M CDS protection`;
        } else {
          exp.hedgeStatus = 'UNHEDGED';
          exp.recommendation = `⚠️ Buy $${(exp.bondNotional / 1000000).toFixed(1)}M CDS protection to hedge bond exposure`;
        }
      } else if (exp.cdsProtectionBought > 0) {
        exp.hedgeStatus = 'UNHEDGED';
        exp.recommendation = `Consider buying $${(exp.cdsProtectionBought / 1000000).toFixed(1)}M in bonds to capture basis`;
      }

      exposures.push(exp);
    });

    setIssuerExposures(
      exposures.sort((a, b) => Math.abs(b.netCreditExposure) - Math.abs(a.netCreditExposure))
    );

    // Calculate sector exposures
    const sectorMap = new Map<string, { notional: number; count: number }>();

    bondConstituents.forEach((bc) => {
      const sector = getSectorFromIssuer(bc.bond.issuer);
      if (!sectorMap.has(sector)) {
        sectorMap.set(sector, { notional: 0, count: 0 });
      }
      const sectorData = sectorMap.get(sector)!;
      sectorData.notional += bc.bond.notional;
      sectorData.count++;
    });

    cdsConstituents.forEach((cc) => {
      const sector = getSectorFromIssuer(cc.trade.referenceEntity);
      if (!sectorMap.has(sector)) {
        sectorMap.set(sector, { notional: 0, count: 0 });
      }
      const sectorData = sectorMap.get(sector)!;
      sectorData.notional += cc.trade.notionalAmount;
      sectorData.count++;
    });

    // Add baskets as "BASKET" sector for now
    // TODO: In future, unwind basket constituents to their actual sectors
    basketConstituents.forEach((bc) => {
      const sector = 'BASKET';
      if (!sectorMap.has(sector)) {
        sectorMap.set(sector, { notional: 0, count: 0 });
      }
      const sectorData = sectorMap.get(sector)!;
      // Use basket.notional (backend field name)
      const notional = bc.basket?.notional || bc.weightValue || 0;
      sectorData.notional += notional;
      sectorData.count++;
    });

    const totalNotional = Array.from(sectorMap.values()).reduce((sum, s) => sum + s.notional, 0);
    const sectors: SectorExposure[] = [];

    sectorMap.forEach((data, sector) => {
      sectors.push({
        sector,
        notional: data.notional,
        percentage: totalNotional > 0 ? (data.notional / totalNotional) * 100 : 0,
        instrumentCount: data.count,
      });
    });

    setSectorExposures(sectors.sort((a, b) => b.notional - a.notional));
  }, [basketConstituents, bondConstituents, cdsConstituents]); // useCallback dependency array

  const getSectorFromIssuer = (issuer: string): string => {
    const sectorMap: Record<string, string> = {
      AAPL: 'TECH',
      MSFT: 'TECH',
      AMZN: 'TECH',
      GOOGL: 'TECH',
      TSLA: 'TECH',
      NFLX: 'TECH',
      META: 'TECH',
      NVDA: 'TECH',
      AMD: 'TECH',
      JPM: 'FINANCIALS',
      BAC: 'FINANCIALS',
      WFC: 'FINANCIALS',
      GS: 'FINANCIALS',
      MS: 'FINANCIALS',
      C: 'FINANCIALS',
    };
    return sectorMap[issuer] || 'OTHER';
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const getHedgeStatusColor = (status: string) => {
    switch (status) {
      case 'BALANCED':
        return 'text-fd-green';
      case 'UNDER_HEDGED':
        return 'text-yellow-400';
      case 'OVER_HEDGED':
        return 'text-blue-400';
      case 'UNHEDGED':
        return 'text-red-400';
      default:
        return 'text-fd-text-muted';
    }
  };

  const getHedgeStatusBadge = (status: string) => {
    switch (status) {
      case 'BALANCED':
        return 'bg-green-900/30 text-green-400 border-green-500';
      case 'UNDER_HEDGED':
        return 'bg-yellow-900/30 text-yellow-400 border-yellow-500';
      case 'OVER_HEDGED':
        return 'bg-blue-900/30 text-blue-400 border-blue-500';
      case 'UNHEDGED':
        return 'bg-red-900/30 text-red-400 border-red-500';
      default:
        return 'bg-fd-dark text-fd-text-muted border-fd-border';
    }
  };

  const getSectorColor = (index: number) => {
    const colors = [
      'bg-fd-green',
      'bg-blue-500',
      'bg-yellow-500',
      'bg-purple-500',
      'bg-pink-500',
      'bg-indigo-500',
      'bg-teal-500',
      'bg-orange-500',
    ];
    return colors[index % colors.length];
  };

  const totalCdsNotional = cdsConstituents.reduce((sum, c) => sum + c.trade.notionalAmount, 0);
  const totalBondNotional = bondConstituents.reduce((sum, c) => sum + c.bond.notional, 0);
  const totalBasketNotional = basketConstituents.reduce((sum, c) => {
    // Use basket.notional (backend field name)
    const notional = c.basket?.notional || c.weightValue || 0;
    return sum + notional;
  }, 0);
  const totalNotional = totalCdsNotional + totalBondNotional + totalBasketNotional;

  return (
    <div className="space-y-6">
      {/* Instrument Distribution */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Instrument Type Breakdown */}
        <div className="bg-fd-darker border border-fd-border rounded-lg p-6">
          <h3 className="text-lg font-semibold text-fd-text mb-4">Instrument Distribution</h3>
          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <div className="w-4 h-4 bg-fd-green rounded"></div>
                <span className="text-fd-text">🛡️ CDS Trades</span>
              </div>
              <div className="text-right">
                <div className="text-fd-text font-medium">{formatCurrency(totalCdsNotional)}</div>
                <div className="text-sm text-fd-text-muted">
                  {totalNotional > 0 ? ((totalCdsNotional / totalNotional) * 100).toFixed(1) : 0}% •{' '}
                  {cdsConstituents.length} positions
                </div>
              </div>
            </div>
            <div className="w-full bg-fd-dark rounded-full h-2">
              <div
                className="bg-fd-green h-2 rounded-full transition-all"
                style={{
                  width: `${totalNotional > 0 ? (totalCdsNotional / totalNotional) * 100 : 0}%`,
                }}
              ></div>
            </div>

            <div className="flex items-center justify-between mt-4">
              <div className="flex items-center space-x-3">
                <div className="w-4 h-4 bg-blue-500 rounded"></div>
                <span className="text-fd-text">📜 Corporate Bonds</span>
              </div>
              <div className="text-right">
                <div className="text-fd-text font-medium">{formatCurrency(totalBondNotional)}</div>
                <div className="text-sm text-fd-text-muted">
                  {totalNotional > 0 ? ((totalBondNotional / totalNotional) * 100).toFixed(1) : 0}%
                  • {bondConstituents.length} positions
                </div>
              </div>
            </div>
            <div className="w-full bg-fd-dark rounded-full h-2">
              <div
                className="bg-blue-500 h-2 rounded-full transition-all"
                style={{
                  width: `${totalNotional > 0 ? (totalBondNotional / totalNotional) * 100 : 0}%`,
                }}
              ></div>
            </div>

            <div className="flex items-center justify-between mt-4">
              <div className="flex items-center space-x-3">
                <div className="w-4 h-4 bg-purple-500 rounded"></div>
                <span className="text-fd-text">🗂️ Credit Baskets</span>
              </div>
              <div className="text-right">
                <div className="text-fd-text font-medium">
                  {formatCurrency(totalBasketNotional)}
                </div>
                <div className="text-sm text-fd-text-muted">
                  {totalNotional > 0 ? ((totalBasketNotional / totalNotional) * 100).toFixed(1) : 0}
                  % • {basketConstituents.length} positions
                </div>
              </div>
            </div>
            <div className="w-full bg-fd-dark rounded-full h-2">
              <div
                className="bg-purple-500 h-2 rounded-full transition-all"
                style={{
                  width: `${totalNotional > 0 ? (totalBasketNotional / totalNotional) * 100 : 0}%`,
                }}
              ></div>
            </div>
          </div>
        </div>

        {/* Sector Exposure */}
        <div className="bg-fd-darker border border-fd-border rounded-lg p-6">
          <h3 className="text-lg font-semibold text-fd-text mb-4">Sector Allocation</h3>
          <div className="space-y-3">
            {sectorExposures.slice(0, 5).map((sector, idx) => (
              <div key={sector.sector} className="space-y-1">
                <div className="flex items-center justify-between text-sm">
                  <div className="flex items-center space-x-2">
                    <div className={`w-3 h-3 rounded ${getSectorColor(idx)}`}></div>
                    <span className="text-fd-text">{sector.sector}</span>
                  </div>
                  <div className="text-fd-text-muted">
                    {sector.percentage.toFixed(1)}% • {sector.instrumentCount} instruments
                  </div>
                </div>
                <div className="w-full bg-fd-dark rounded-full h-1.5">
                  <div
                    className={`${getSectorColor(idx)} h-1.5 rounded-full transition-all`}
                    style={{ width: `${sector.percentage}%` }}
                  ></div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Net Exposure by Issuer */}
      <div className="bg-fd-darker border border-fd-border rounded-lg p-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-fd-text">Net Credit Exposure by Issuer</h3>
          <div className="text-sm text-fd-text-muted">Showing bond positions vs CDS hedges</div>
        </div>

        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-fd-border">
            <thead>
              <tr className="text-left text-xs font-medium text-fd-text-muted uppercase tracking-wider">
                <th className="px-4 py-3">Issuer</th>
                <th className="px-4 py-3">Sector</th>
                <th className="px-4 py-3 text-right">Bond Exposure</th>
                <th className="px-4 py-3 text-right">CDS Protection</th>
                <th className="px-4 py-3 text-right">Net Exposure</th>
                <th className="px-4 py-3 text-center">Hedge Ratio</th>
                <th className="px-4 py-3 text-center">Status</th>
                <th className="px-4 py-3">Recommendation</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-fd-border">
              {issuerExposures.map((exp) => (
                <tr key={exp.issuer} className="hover:bg-fd-dark transition-colors">
                  <td className="px-4 py-3 text-sm font-medium text-fd-text">{exp.issuer}</td>
                  <td className="px-4 py-3 text-sm text-fd-text-muted">{exp.sector}</td>
                  <td className="px-4 py-3 text-sm text-right text-fd-text">
                    {exp.bondNotional > 0 ? formatCurrency(exp.bondNotional) : '-'}
                  </td>
                  <td className="px-4 py-3 text-sm text-right text-fd-text">
                    {exp.cdsProtectionBought > 0 ? formatCurrency(exp.cdsProtectionBought) : '-'}
