import React, { useState, useEffect } from 'react';
import { Bond, bondService } from '../../services/bondService';
import { REFERENCE_ENTITIES, SECTORS } from '../../data/referenceData';

interface BondCreationModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSuccess: (bond: Bond) => void;
}

interface FormErrors {
  [key: string]: string;
}

const SENIORITY_OPTIONS = [
  { value: 'SR_UNSEC', label: 'Senior Unsecured' },
  { value: 'SR_SEC', label: 'Senior Secured' },
  { value: 'SUBORD', label: 'Subordinated' },
];

const COUPON_FREQUENCY_OPTIONS = [
  { value: 'ANNUAL', label: 'Annual' },
  { value: 'SEMI_ANNUAL', label: 'Semi-Annual' },
  { value: 'QUARTERLY', label: 'Quarterly' },
];

const DAY_COUNT_OPTIONS = [
  { value: 'ACT_ACT', label: 'ACT/ACT' },
  { value: 'THIRTY_360', label: '30/360' },
];

const CURRENCY_OPTIONS = ['USD', 'EUR', 'GBP', 'JPY'];

const BondCreationModal: React.FC<BondCreationModalProps> = ({ isOpen, onClose, onSuccess }) => {
  const [formData, setFormData] = useState<Partial<Bond>>({
    currency: 'USD',
    couponFrequency: 'SEMI_ANNUAL',
    dayCount: 'ACT_ACT',
    seniority: 'SR_UNSEC',
    settlementDays: 2,
    faceValue: 100,
    priceConvention: 'CLEAN',
  });

  const [errors, setErrors] = useState<FormErrors>({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [userModifiedSector, setUserModifiedSector] = useState(false);

  // Auto-populate sector when issuer changes
  useEffect(() => {
    if (!userModifiedSector && formData.issuer) {
      const entity = REFERENCE_ENTITIES.find((e) => e.code === formData.issuer);
      if (entity?.sector) {
        setFormData((prev) => ({
          ...prev,
          sector: entity.sector,
        }));
      }
    }
  }, [formData.issuer, userModifiedSector]);

  const validateForm = (): boolean => {
    const newErrors: FormErrors = {};

    if (!formData.issuer?.trim()) {
      newErrors.issuer = 'Issuer is required';
    }

    if (!formData.notional || formData.notional <= 0) {
      newErrors.notional = 'Notional must be positive';
    }

    if (!formData.couponRate || formData.couponRate < 0) {
      newErrors.couponRate = 'Coupon rate must be >= 0';
    }

    if (formData.couponRate && formData.couponRate > 1) {
      newErrors.couponRate = 'Coupon rate exceeds 100% - please verify';
    }

    if (!formData.issueDate) {
      newErrors.issueDate = 'Issue date is required';
    }

    if (!formData.maturityDate) {
      newErrors.maturityDate = 'Maturity date is required';
    }

    if (
      formData.issueDate &&
      formData.maturityDate &&
      new Date(formData.issueDate) >= new Date(formData.maturityDate)
    ) {
      newErrors.maturityDate = 'Maturity date must be after issue date';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleInputChange = (field: keyof Bond, value: any) => {
    // Track if user manually edits the sector
    if (field === 'sector') {
      setUserModifiedSector(true);
    }

    setFormData((prev) => ({
      ...prev,
      [field]: value,
    }));

    if (errors[field]) {
      setErrors((prev) => ({
        ...prev,
        [field]: '',
      }));
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);

    try {
      const bond = await bondService.createBond(formData as Bond);
      onSuccess(bond);
      resetForm();
      onClose();
    } catch (error: any) {
      setErrors({ submit: error.message || 'Failed to create bond' });
    } finally {
      setIsSubmitting(false);
    }
  };

  const resetForm = () => {
    setFormData({
      currency: 'USD',
      couponFrequency: 'SEMI_ANNUAL',
      dayCount: 'ACT_ACT',
      seniority: 'SR_UNSEC',
      settlementDays: 2,
      faceValue: 100,
      priceConvention: 'CLEAN',
    });
    setErrors({});
    setUserModifiedSector(false);
  };

  if (!isOpen) return null;

  return (
    <div
      className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50"
      onClick={onClose}
    >
      <div
        className="bg-fd-darker rounded-lg shadow-xl border border-fd-border max-w-4xl w-full max-h-[90vh] overflow-y-auto m-4"
        onClick={(e) => e.stopPropagation()}
      >
        {/* Header */}
        <div className="sticky top-0 bg-fd-darker border-b border-fd-border px-6 py-4 flex justify-between items-center z-10">
          <h2 className="text-xl font-semibold text-fd-text">Create New Bond</h2>
          <button
            onClick={onClose}
            className="text-fd-text-muted hover:text-fd-text transition-colors"
            aria-label="Close modal"
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M6 18L18 6M6 6l12 12"
              />
            </svg>
          </button>
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit} className="p-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {/* Issuer & Credit Info Section */}
            <div className="md:col-span-2">
              <h3 className="text-sm font-semibold text-fd-text-muted uppercase tracking-wide mb-4">
                Issuer & Credit Information
              </h3>
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Issuer <span className="text-red-500">*</span>
              </label>
              <select
                value={formData.issuer || ''}
                onChange={(e) => handleInputChange('issuer', e.target.value)}
                className={`w-full px-3 py-2 bg-fd-dark border ${errors.issuer ? 'border-red-500' : 'border-fd-border'} text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent`}
              >
                <option value="">Select Issuer</option>
                {REFERENCE_ENTITIES.map((entity) => (
                  <option key={entity.code} value={entity.code}>
                    {entity.code} - {entity.name}
                  </option>
                ))}
              </select>
              {errors.issuer && <p className="mt-1 text-sm text-red-400">{errors.issuer}</p>}
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Seniority <span className="text-red-500">*</span>
              </label>
              <select
                value={formData.seniority}
                onChange={(e) => handleInputChange('seniority', e.target.value)}
                className="w-full px-3 py-2 bg-fd-dark border border-fd-border text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent"
              >
                {SENIORITY_OPTIONS.map((opt) => (
                  <option key={opt.value} value={opt.value}>
                    {opt.label}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">Sector</label>
              <select
                value={formData.sector || ''}
                onChange={(e) => handleInputChange('sector', e.target.value)}
                className="w-full px-3 py-2 bg-fd-dark border border-fd-border text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent"
              >
                <option value="">Auto-suggested from issuer</option>
                {SECTORS.map((sector) => (
                  <option key={sector.code} value={sector.code}>
                    {sector.name}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">ISIN</label>
              <input
                type="text"
                value={formData.isin || ''}
                onChange={(e) => handleInputChange('isin', e.target.value)}
                className="w-full px-3 py-2 bg-fd-dark border border-fd-border text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent placeholder-fd-text-muted"
                placeholder="Optional"
                maxLength={12}
              />
            </div>

            {/* Economics Section */}
            <div className="md:col-span-2 mt-4">
              <h3 className="text-sm font-semibold text-fd-text-muted uppercase tracking-wide mb-4">
                Bond Economics
              </h3>
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Currency <span className="text-red-500">*</span>
              </label>
              <select
                value={formData.currency}
                onChange={(e) => handleInputChange('currency', e.target.value)}
                className="w-full px-3 py-2 bg-fd-dark border border-fd-border text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent"
              >
                {CURRENCY_OPTIONS.map((curr) => (
                  <option key={curr} value={curr}>
                    {curr}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Notional <span className="text-red-500">*</span>
              </label>
              <input
                type="number"
                step="0.01"
                value={formData.notional || ''}
                onChange={(e) => handleInputChange('notional', parseFloat(e.target.value))}
                className={`w-full px-3 py-2 bg-fd-dark border ${errors.notional ? 'border-red-500' : 'border-fd-border'} text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent placeholder-fd-text-muted`}
              />
              {errors.notional && <p className="mt-1 text-sm text-red-400">{errors.notional}</p>}
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Coupon Rate (decimal) <span className="text-red-500">*</span>
              </label>
              <input
                type="number"
                step="0.000001"
                value={formData.couponRate || ''}
                onChange={(e) => handleInputChange('couponRate', parseFloat(e.target.value))}
                className={`w-full px-3 py-2 bg-fd-dark border ${errors.couponRate ? 'border-red-500' : 'border-fd-border'} text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent placeholder-fd-text-muted`}
                placeholder="e.g., 0.045 for 4.5%"
              />
              {errors.couponRate && (
                <p className="mt-1 text-sm text-red-400">{errors.couponRate}</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Coupon Frequency
              </label>
              <select
                value={formData.couponFrequency}
                onChange={(e) => handleInputChange('couponFrequency', e.target.value)}
                className="w-full px-3 py-2 bg-fd-dark border border-fd-border text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent"
              >
                {COUPON_FREQUENCY_OPTIONS.map((opt) => (
                  <option key={opt.value} value={opt.value}>
                    {opt.label}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Day Count Convention
              </label>
              <select
                value={formData.dayCount}
                onChange={(e) => handleInputChange('dayCount', e.target.value)}
                className="w-full px-3 py-2 bg-fd-dark border border-fd-border text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent"
              >
                {DAY_COUNT_OPTIONS.map((opt) => (
                  <option key={opt.value} value={opt.value}>
                    {opt.label}
                  </option>
                ))}
              </select>
            </div>

            {/* Dates Section */}
            <div className="md:col-span-2 mt-4">
              <h3 className="text-sm font-semibold text-fd-text-muted uppercase tracking-wide mb-4">
                Dates
              </h3>
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Issue Date <span className="text-red-500">*</span>
              </label>
              <input
                type="date"
                value={formData.issueDate || ''}
                onChange={(e) => handleInputChange('issueDate', e.target.value)}
                className={`w-full px-3 py-2 bg-fd-dark border ${errors.issueDate ? 'border-red-500' : 'border-fd-border'} text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent`}
              />
              {errors.issueDate && <p className="mt-1 text-sm text-red-400">{errors.issueDate}</p>}
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Maturity Date <span className="text-red-500">*</span>
              </label>
              <input
                type="date"
                value={formData.maturityDate || ''}
                onChange={(e) => handleInputChange('maturityDate', e.target.value)}
                className={`w-full px-3 py-2 bg-fd-dark border ${errors.maturityDate ? 'border-red-500' : 'border-fd-border'} text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent`}
              />
              {errors.maturityDate && (
                <p className="mt-1 text-sm text-red-400">{errors.maturityDate}</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">Settlement Days</label>
              <input
                type="number"
                value={formData.settlementDays || ''}
                onChange={(e) => handleInputChange('settlementDays', parseInt(e.target.value))}
                className="w-full px-3 py-2 bg-fd-dark border border-fd-border text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent placeholder-fd-text-muted"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">Face Value</label>
              <input
                type="number"
                step="0.01"
                value={formData.faceValue || ''}
                onChange={(e) => handleInputChange('faceValue', parseFloat(e.target.value))}
                className="w-full px-3 py-2 bg-fd-dark border border-fd-border text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent placeholder-fd-text-muted"
              />
            </div>
          </div>

          {/* Error Message */}
          {errors.submit && (
            <div className="mt-4 p-3 bg-red-900/20 border border-red-500/30 rounded-md">
              <p className="text-sm text-red-400">{errors.submit}</p>
            </div>
          )}

          {/* Action Buttons */}
          <div className="mt-6 flex justify-end space-x-3">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 text-sm font-medium text-fd-text bg-fd-dark border border-fd-border rounded-md hover:bg-fd-darker focus:outline-none focus:ring-2 focus:ring-fd-green"
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={isSubmitting}
              className="px-4 py-2 text-sm font-medium text-fd-dark bg-fd-green border border-transparent rounded-md hover:bg-fd-green-hover focus:outline-none focus:ring-2 focus:ring-fd-green disabled:bg-fd-green/50 disabled:cursor-not-allowed"
            >
              {isSubmitting ? 'Creating...' : 'Create Bond'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default BondCreationModal;

import React, { useState, useEffect } from 'react';
import {
  REFERENCE_ENTITIES,
  COUNTERPARTIES,
  CURRENCIES,
  PREMIUM_FREQUENCIES,
  DAY_COUNT_CONVENTIONS,
  RESTRUCTURING_CLAUSES,
  PAYMENT_CALENDARS,
  TRADE_STATUSES,
  SETTLEMENT_METHODS,
  CDSTrade
} from '../../data/referenceData';
import { bondService, Bond } from '../../services/bondService';

interface FormErrors {
  [key: string]: string;
}

interface CDSTradeFormProps {
  onSubmit: (trade: CDSTrade) => void;
}

const CDSTradeForm: React.FC<CDSTradeFormProps> = ({ onSubmit }) => {
  const [formData, setFormData] = useState<Partial<CDSTrade>>({
    tradeDate: new Date().toISOString().split('T')[0],
    currency: 'USD',
    premiumFrequency: 'QUARTERLY',
    dayCountConvention: 'ACT_360',
    buySellProtection: 'BUY',
    paymentCalendar: 'NYC',
    tradeStatus: 'PENDING',
    recoveryRate: 40,
    settlementType: 'CASH'
  });

  const [errors, setErrors] = useState<FormErrors>({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [availableBonds, setAvailableBonds] = useState<Bond[]>([]);
  const [loadingBonds, setLoadingBonds] = useState(false);

  // Fetch bonds when reference entity changes
  useEffect(() => {
    const fetchBonds = async () => {
      if (!formData.referenceEntity) {
        setAvailableBonds([]);
        return;
      }

      setLoadingBonds(true);
      try {
        const bonds = await bondService.getBondsByIssuer(formData.referenceEntity);
        setAvailableBonds(bonds);
      } catch (error) {
        console.error('Error fetching bonds:', error);
        setAvailableBonds([]);
      } finally {
        setLoadingBonds(false);
      }
    };

    fetchBonds();
  }, [formData.referenceEntity]);

  const validateForm = (): boolean => {
    const newErrors: FormErrors = {};

    // Required field validations
    if (!formData.referenceEntity) {
      newErrors.referenceEntity = 'Reference Entity is required';
    }

    if (formData.referenceEntity && !formData.obligation?.id) {
      newErrors.obligation = 'Obligation is required';
    }

    if (!formData.notionalAmount || formData.notionalAmount <= 0) {
      newErrors.notionalAmount = 'Notional Amount must be greater than 0';
    }

    if (!formData.spread || formData.spread < 0) {
      newErrors.spread = 'Spread must be 0 or greater';
    }

    if (!formData.recoveryRate || formData.recoveryRate < 0 || formData.recoveryRate > 100) {
      newErrors.recoveryRate = 'Recovery Rate must be between 0 and 100';
    }

    if (!formData.maturityDate) {
      newErrors.maturityDate = 'Maturity Date is required';
    }

    if (!formData.effectiveDate) {
      newErrors.effectiveDate = 'Effective Date is required';
    }

    if (!formData.counterparty) {
      newErrors.counterparty = 'Counterparty is required';
    }

    if (!formData.tradeDate) {
      newErrors.tradeDate = 'Trade Date is required';
    }

    if (!formData.accrualStartDate) {
      newErrors.accrualStartDate = 'Accrual Start Date is required';
    }

    // Date validations
    if (formData.effectiveDate && formData.maturityDate && 
        new Date(formData.effectiveDate) >= new Date(formData.maturityDate)) {
      newErrors.maturityDate = 'Maturity Date must be after Effective Date';
    }

    if (formData.tradeDate && formData.effectiveDate && 
        new Date(formData.tradeDate) > new Date(formData.effectiveDate)) {
      newErrors.effectiveDate = 'Effective Date must be on or after Trade Date';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleInputChange = (field: keyof CDSTrade, value: any) => {
    setFormData(prev => {
      const updates: Partial<CDSTrade> = {};

      // Handle obligation specially - convert to nested object
      if (field === 'obligation') {
        updates.obligation = value ? { id: value } : undefined;
      } else {
        updates[field] = value as any;
      }

      // Clear obligation when reference entity changes
      if (field === 'referenceEntity' && prev.referenceEntity !== value) {
        updates.obligation = undefined;
      }

      return {
        ...prev,
        ...updates
      };
    });

    // Clear error for this field when user starts typing
    if (errors[field]) {
      setErrors(prev => ({
        ...prev,
        [field]: ''
      }));
    }
  };

  // Format number with commas for display
  const formatNumberWithCommas = (num: number | undefined): string => {
    if (!num && num !== 0) return '';
    return num.toLocaleString('en-US');
  };

  // Handle notional amount input with formatting
  const handleNotionalChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const rawValue = e.target.value.replace(/,/g, ''); // Remove commas
    const numValue = parseFloat(rawValue);

    if (!isNaN(numValue) || rawValue === '') {
      handleInputChange('notionalAmount', rawValue === '' ? undefined : numValue);
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);

    // Defensive normalization
    if (formData.tradeStatus === 'SETTLED' || formData.tradeStatus === 'CONFIRMED') {
      formData.tradeStatus = 'ACTIVE';
    }

    // Simulate API call delay
    setTimeout(() => {
      onSubmit(formData as CDSTrade);
      setIsSubmitting(false);

      // Reset form
      setFormData({
        tradeDate: new Date().toISOString().split('T')[0],
        currency: 'USD',
        premiumFrequency: 'QUARTERLY',
        dayCountConvention: 'ACT_360',
        buySellProtection: 'BUY',
        paymentCalendar: 'NYC',
        tradeStatus: 'PENDING',
        recoveryRate: 40,
        settlementType: 'CASH'
      });
    }, 1000);
  };

  const inputClassName = (fieldName: string) => {
    const baseClass = "w-full px-3 py-2 bg-fd-input border rounded-md text-fd-text placeholder-fd-text-muted focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-fd-green";
    const errorClass = "border-red-500";
    const normalClass = "border-fd-border";

    return `${baseClass} ${errors[fieldName] ? errorClass : normalClass}`;
  };

  const selectClassName = (fieldName: string) => {
    const baseClass = "w-full px-3 py-2 bg-fd-input border rounded-md text-fd-text focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-fd-green";
    const errorClass = "border-red-500";
    const normalClass = "border-fd-border";

    return `${baseClass} ${errors[fieldName] ? errorClass : normalClass}`;
  };

  const generateRandomData = () => {
    // Helper function to get random item from array
    const getRandomItem = (array: any[]) => array[Math.floor(Math.random() * array.length)];

    // Helper function to get random date within a range (days offset from today)
    const getRandomDate = (startDays: number, endDays: number) => {
      const start = new Date();
      start.setDate(start.getDate() + startDays);
      const end = new Date();
      end.setDate(end.getDate() + endDays);

      const randomTime = start.getTime() + Math.random() * (end.getTime() - start.getTime());
      return new Date(randomTime).toISOString().split('T')[0];
    };

    // Generate trades that started in the PAST for demo purposes
    // This allows demonstrating coupon payments immediately

    // Trade date: 24-48 months ago (2-4 years in the past) for more coupons
    const tradeDate = getRandomDate(-365 * 4, -365 * 2);

    // Effective date: 1-7 days after trade date (still in the past)
    const tradeDateObj = new Date(tradeDate);
    const effectiveDaysOffset = Math.floor(Math.random() * 7) + 1;
    const effectiveDateObj = new Date(tradeDateObj);
    effectiveDateObj.setDate(effectiveDateObj.getDate() + effectiveDaysOffset);
    const effectiveDate = effectiveDateObj.toISOString().split('T')[0];

    // Maturity date: 2-5 years from TODAY (in the future)
    const maturityDate = getRandomDate(365 * 2, 365 * 5);

    // Accrual start date: same as effective date
    const accrualStartDate = effectiveDate;

    // Generate round notional amounts (5M, 10M, 20M, 50M, 100M, 200M, 500M)
    const roundNotionals = [5000000, 10000000, 20000000, 50000000, 100000000, 200000000, 500000000];
    const notionalAmount = getRandomItem(roundNotionals);

    const randomData: Partial<CDSTrade> = {
      referenceEntity: getRandomItem(REFERENCE_ENTITIES).code,
      counterparty: getRandomItem(COUNTERPARTIES).code,
      currency: getRandomItem(CURRENCIES).code,
      notionalAmount,
      spread: Math.floor(Math.random() * 500) + 50, // 50 to 550 bps
      buySellProtection: Math.random() > 0.5 ? 'BUY' : 'SELL',
      tradeDate,
      effectiveDate,
      maturityDate,
      accrualStartDate,
      premiumFrequency: getRandomItem(PREMIUM_FREQUENCIES).value,
      dayCountConvention: getRandomItem(DAY_COUNT_CONVENTIONS).value,
      restructuringClause: Math.random() > 0.3 ? getRandomItem(RESTRUCTURING_CLAUSES).value : '', // 70% chance of having a clause
      paymentCalendar: getRandomItem(PAYMENT_CALENDARS).value,
      tradeStatus: 'ACTIVE', // Always generate ACTIVE trades for demo purposes
      recoveryRate: 40,  // Default recovery rate
      settlementType: getRandomItem(SETTLEMENT_METHODS).value
    };
    setFormData(randomData);
    setErrors({}); // Clear any existing errors
  };

  return (
    <div className="max-w-4xl mx-auto bg-fd-darker rounded-lg shadow-fd border border-fd-border p-6">
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-fd-text">New CDS Trade Entry</h2>
      </div>

      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Row 1: Reference Entity, Counterparty, Currency */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block text-fd-text font-medium mb-2">
              Reference Entity <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.referenceEntity || ''}
              onChange={(e) => handleInputChange('referenceEntity', e.target.value)}
              className={selectClassName('referenceEntity')}
            >
              <option value="">Select Reference Entity</option>
              {REFERENCE_ENTITIES.map((entity) => (
                <option key={entity.code} value={entity.code}>
                  {entity.code} - {entity.name}
                </option>
              ))}
            </select>
            {errors.referenceEntity && (
              <p className="text-red-500 text-sm mt-1">{errors.referenceEntity}</p>
            )}
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Counterparty <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.counterparty || ''}
              onChange={(e) => handleInputChange('counterparty', e.target.value)}
              className={selectClassName('counterparty')}
            >
              <option value="">Select Counterparty</option>
              {COUNTERPARTIES.map((cp) => (
                <option key={cp.code} value={cp.code}>
                  {cp.name}
                </option>
              ))}
            </select>
            {errors.counterparty && (
              <p className="text-red-500 text-sm mt-1">{errors.counterparty}</p>
            )}
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Currency <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.currency || 'USD'}
              onChange={(e) => handleInputChange('currency', e.target.value)}
              className={selectClassName('currency')}
            >
              {CURRENCIES.map((curr) => (
                <option key={curr.code} value={curr.code}>
                  {curr.code} - {curr.name}
                </option>
              ))}
            </select>
          </div>
        </div>

        {/* Row 1.5: Obligation (conditional on reference entity) */}
        {formData.referenceEntity && (
          <div className="grid grid-cols-1 gap-4">
            <div>
              <label className="block text-fd-text font-medium mb-2">
                Obligation (Bond) <span className="text-red-500">*</span>
              </label>
              <select
                value={formData.obligation?.id || ''}
                onChange={(e) => handleInputChange('obligation', e.target.value ? Number(e.target.value) : undefined)}
                className={selectClassName('obligation')}
                disabled={loadingBonds}
              >
                <option value="">Select Obligation</option>
                {loadingBonds && <option value="">Loading bonds...</option>}
                {!loadingBonds && availableBonds.length === 0 && (
                  <option value="">No bonds available for {formData.referenceEntity}</option>
                )}
                {!loadingBonds && availableBonds.map((bond) => (
                  <option key={bond.id} value={bond.id}>
                    {bond.isin ? `${bond.isin} - ` : ''}
                    {bond.issuer} {bond.seniority} - 
                    Coupon: {bond.couponRate}% - 
                    Maturity: {new Date(bond.maturityDate).toLocaleDateString()}
                  </option>
                ))}
              </select>
              <p className="text-xs text-fd-text-muted mt-1">
                Select a specific bond from {formData.referenceEntity}
              </p>
            </div>
          </div>
        )}

        {/* Row 2: Notional Amount, Spread, Buy/Sell Protection */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block text-fd-text font-medium mb-2">
              Notional Amount <span className="text-red-500">*</span>
            </label>
            <input
              type="text"
              value={formatNumberWithCommas(formData.notionalAmount)}
              onChange={handleNotionalChange}
              className={inputClassName('notionalAmount')}
              placeholder="e.g., 10,000,000"
            />
            {errors.notionalAmount && (
              <p className="text-red-500 text-sm mt-1">{errors.notionalAmount}</p>
            )}
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Spread (bps) <span className="text-red-500">*</span>
            </label>
            <input
              type="number"
              step="0.01"
              min="0"
              value={formData.spread || ''}
              onChange={(e) => handleInputChange('spread', parseFloat(e.target.value))}
              className={inputClassName('spread')}
              placeholder="e.g., 100"
            />
            {errors.spread && (
              <p className="text-red-500 text-sm mt-1">{errors.spread}</p>
            )}
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Recovery Rate (%) <span className="text-red-500">*</span>
            </label>
            <input
              type="number"
              step="1"
              min="0"
              max="100"
              value={formData.recoveryRate || 40}
              onChange={(e) => handleInputChange('recoveryRate', parseFloat(e.target.value))}
              className={inputClassName('recoveryRate')}
              placeholder="e.g., 40"
            />
            {errors.recoveryRate && (
              <p className="text-red-500 text-sm mt-1">{errors.recoveryRate}</p>
            )}
          </div>
        </div>

        {/* Row 3: Buy/Sell Protection, Settlement Type */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block text-fd-text font-medium mb-2">
              Buy/Sell Protection <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.buySellProtection || 'BUY'}
              onChange={(e) => handleInputChange('buySellProtection', e.target.value)}
              className={selectClassName('buySellProtection')}
            >
              <option value="BUY">Buy Protection</option>
              <option value="SELL">Sell Protection</option>
            </select>
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Settlement Type <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.settlementType || 'CASH'}
              onChange={(e) => handleInputChange('settlementType', e.target.value)}
              className={selectClassName('settlementType')}
            >
              {SETTLEMENT_METHODS.map((method) => (
                <option key={method.value} value={method.value}>
                  {method.label}
                </option>
              ))}
            </select>
          </div>
        </div>

        {/* Row 4: Trade Date, Effective Date, Maturity Date */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block text-fd-text font-medium mb-2">
              Trade Date <span className="text-red-500">*</span>
            </label>
            <input
              type="date"
              value={formData.tradeDate || ''}
              onChange={(e) => handleInputChange('tradeDate', e.target.value)}
              className={inputClassName('tradeDate')}
            />
            {errors.tradeDate && (
              <p className="text-red-500 text-sm mt-1">{errors.tradeDate}</p>
            )}
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Effective Date <span className="text-red-500">*</span>
            </label>
            <input
              type="date"
              value={formData.effectiveDate || ''}
              onChange={(e) => handleInputChange('effectiveDate', e.target.value)}
              className={inputClassName('effectiveDate')}
            />
            {errors.effectiveDate && (
              <p className="text-red-500 text-sm mt-1">{errors.effectiveDate}</p>
            )}
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Maturity Date <span className="text-red-500">*</span>
            </label>
            <input
              type="date"
              value={formData.maturityDate || ''}
              onChange={(e) => handleInputChange('maturityDate', e.target.value)}
              className={inputClassName('maturityDate')}
            />
            {errors.maturityDate && (
              <p className="text-red-500 text-sm mt-1">{errors.maturityDate}</p>
            )}
          </div>
        </div>

        {/* Row 4: Premium Frequency, Day Count Convention, Accrual Start Date */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block text-fd-text font-medium mb-2">
              Premium Frequency <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.premiumFrequency || 'QUARTERLY'}
              onChange={(e) => handleInputChange('premiumFrequency', e.target.value)}
              className={selectClassName('premiumFrequency')}
            >
              {PREMIUM_FREQUENCIES.map((freq) => (
                <option key={freq.value} value={freq.value}>
                  {freq.label}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Day Count Convention <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.dayCountConvention || 'ACT_360'}
              onChange={(e) => handleInputChange('dayCountConvention', e.target.value)}
              className={selectClassName('dayCountConvention')}
            >
              {DAY_COUNT_CONVENTIONS.map((dcc) => (
                <option key={dcc.value} value={dcc.value}>
                  {dcc.label}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Accrual Start Date <span className="text-red-500">*</span>
            </label>
            <input
              type="date"
              value={formData.accrualStartDate || ''}
              onChange={(e) => handleInputChange('accrualStartDate', e.target.value)}
              className={inputClassName('accrualStartDate')}
            />
            {errors.accrualStartDate && (
              <p className="text-red-500 text-sm mt-1">{errors.accrualStartDate}</p>
            )}
          </div>
        </div>

        {/* Row 5: Payment Calendar, Restructuring Clause, Trade Status */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block text-fd-text font-medium mb-2">
              Payment Calendar <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.paymentCalendar || 'NYC'}
              onChange={(e) => handleInputChange('paymentCalendar', e.target.value)}
              className={selectClassName('paymentCalendar')}
            >
              {PAYMENT_CALENDARS.map((cal) => (
                <option key={cal.value} value={cal.value}>
                  {cal.label}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Restructuring Clause
            </label>
            <select
              value={formData.restructuringClause || ''}
              onChange={(e) => handleInputChange('restructuringClause', e.target.value)}
              className={selectClassName('restructuringClause')}
            >
              <option value="">None</option>
              {RESTRUCTURING_CLAUSES.map((clause) => (
                <option key={clause.value} value={clause.value}>
                  {clause.label}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Trade Status <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.tradeStatus || 'PENDING'}
              onChange={(e) => handleInputChange('tradeStatus', e.target.value)}
              className={selectClassName('tradeStatus')}
            >
              {TRADE_STATUSES.map((status) => (
                <option key={status.value} value={status.value}>
                  {status.label}
                </option>
              ))}
            </select>
          </div>
        </div>

        {/* Submit Button */}
        <div className="flex justify-end space-x-4 pt-6 border-t border-fd-border">
          <button
            type="button"
            onClick={generateRandomData}
            className="px-6 py-2 bg-transparent border border-fd-border text-fd-text rounded hover:bg-fd-dark transition-colors"
          >
            Fill Random Data
          </button>
          <button
            type="button"
            onClick={() => {
              setFormData({
                tradeDate: new Date().toISOString().split('T')[0],
                currency: 'USD',
                premiumFrequency: 'QUARTERLY',
                dayCountConvention: 'ACT_360',
                buySellProtection: 'BUY',
                paymentCalendar: 'NYC',
                tradeStatus: 'PENDING',
                recoveryRate: 40
              });
              setErrors({});
            }}
            className="px-6 py-2 bg-transparent border border-fd-border text-fd-text rounded hover:bg-fd-dark transition-colors"
          >
            Clear Form
          </button>
          <button
            type="submit"
            disabled={isSubmitting}
            className="px-6 py-2 bg-fd-green text-fd-dark font-medium rounded hover:bg-fd-green-hover disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
          >
            {isSubmitting ? 'Booking Trade...' : 'Book Trade'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default CDSTradeForm;

import React, { useState } from 'react';
import { REFERENCE_ENTITIES } from '../../data/referenceData';

export type CreditEventType =
  | 'BANKRUPTCY'
  | 'FAILURE_TO_PAY'
  | 'RESTRUCTURING'
  | 'OBLIGATION_DEFAULT'
  | 'REPUDIATION_MORATORIUM'
  | 'PAYOUT';
export type SettlementMethod = 'CASH' | 'PHYSICAL';

export interface CreateCreditEventRequest {
  eventType: CreditEventType;
  eventDate: string;
  noticeDate: string;
  settlementMethod: SettlementMethod;
  comments?: string;
}

interface CreditEventModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSubmit: (request: CreateCreditEventRequest) => Promise<void>;
  tradeId: number;
  referenceEntity?: string;
  isLoading?: boolean;
}

const CreditEventModal: React.FC<CreditEventModalProps> = ({
  isOpen,
  onClose,
  onSubmit,
  tradeId,
  referenceEntity,
  isLoading = false
}) => {
  // Get today's date in YYYY-MM-DD format
  const getTodayDate = () => {
    const today = new Date();
    return today.toISOString().split('T')[0];
  };

  // Helper function to get full name of reference entity
  const getReferenceEntityFullName = (code: string): string => {
    const entity = REFERENCE_ENTITIES.find(e => e.code === code);
    return entity ? entity.name : code;
  };

  const [formData, setFormData] = useState<CreateCreditEventRequest>({
    eventType: 'RESTRUCTURING',
    eventDate: getTodayDate(),
    noticeDate: getTodayDate(),
    settlementMethod: 'PHYSICAL',
    comments: '',
  });

  const [errors, setErrors] = useState<Record<string, string>>({});

  const eventTypeOptions: { value: CreditEventType; label: string }[] = [
    { value: 'RESTRUCTURING', label: 'Restructuring' },
    { value: 'BANKRUPTCY', label: 'Bankruptcy' },
  ];

  const settlementMethodOptions: { value: SettlementMethod; label: string }[] = [
    { value: 'PHYSICAL', label: 'Physical Settlement' },
    { value: 'CASH', label: 'Cash Settlement' },
  ];

  const validateForm = (): boolean => {
    const newErrors: Record<string, string> = {};

    if (!formData.eventType) {
      newErrors.eventType = 'Event type is required';
    }

    if (!formData.eventDate) {
      newErrors.eventDate = 'Event date is required';
    } else if (new Date(formData.eventDate) > new Date()) {
      newErrors.eventDate = 'Event date cannot be in the future';
    }

    if (!formData.noticeDate) {
      newErrors.noticeDate = 'Notice date is required';
    } else if (formData.eventDate && new Date(formData.noticeDate) < new Date(formData.eventDate)) {
      newErrors.noticeDate = 'Notice date must be on or after event date';
    }

    if (!formData.settlementMethod) {
      newErrors.settlementMethod = 'Settlement method is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    try {
      await onSubmit(formData);
      // Reset form on successful submission
      setFormData({
        eventType: 'RESTRUCTURING',
        eventDate: getTodayDate(),
        noticeDate: getTodayDate(),
        settlementMethod: 'PHYSICAL',
        comments: '',
      });
      setErrors({});
      onClose();
    } catch (error) {
      console.error('Error submitting credit event:', error);
    }
  };

  const handleInputChange = (field: keyof CreateCreditEventRequest, value: string) => {
    setFormData((prev) => ({
      ...prev,
      [field]: value,
    }));

    // Clear error when user starts typing
    if (errors[field]) {
      setErrors((prev) => ({
        ...prev,
        [field]: '',
      }));
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-fd-darker rounded-lg shadow-fd border border-fd-border p-6 w-full max-w-md max-h-[90vh] overflow-y-auto">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold text-fd-text">Record Credit Event</h2>
          <button
            onClick={onClose}
            className="text-fd-text-muted hover:text-fd-text transition-colors"
            disabled={isLoading}
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M6 18L18 6M6 6l12 12"
              />
            </svg>
          </button>
        </div>

        {/* Reference Entity Information */}
        {referenceEntity && (
          <div className="mb-4 p-3 bg-fd-dark rounded-lg border border-fd-border">
            <div className="flex items-center gap-2">
              <svg className="w-5 h-5 text-fd-green" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4" />
              </svg>
              <div className="flex-1">
                <p className="text-xs text-fd-text-muted uppercase tracking-wide">Reference Entity</p>
                <p className="text-fd-green font-semibold">{getReferenceEntityFullName(referenceEntity)}</p>
                <p className="text-fd-text-muted text-xs mt-0.5">{referenceEntity}</p>
              </div>
            </div>
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label htmlFor="eventType" className="block text-sm font-medium text-fd-text mb-1">
              Event Type *
            </label>
            <select
              id="eventType"
              value={formData.eventType}
              onChange={(e) => handleInputChange('eventType', e.target.value)}
              className={`w-full px-3 py-2 bg-fd-dark border rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green text-fd-text ${
                errors.eventType ? 'border-red-500' : 'border-fd-border'
              }`}
              disabled={isLoading}
            >
              {eventTypeOptions.map((option) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
            {errors.eventType && <p className="mt-1 text-sm text-red-400">{errors.eventType}</p>}
          </div>

          <div>
            <label htmlFor="eventDate" className="block text-sm font-medium text-fd-text mb-1">
              Event Date *
            </label>
            <input
              type="date"
              id="eventDate"
              value={formData.eventDate}
              onChange={(e) => handleInputChange('eventDate', e.target.value)}
              className={`w-full px-3 py-2 bg-fd-dark border rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green text-fd-text ${
                errors.eventDate ? 'border-red-500' : 'border-fd-border'
              }`}
              disabled={isLoading}
            />
            {errors.eventDate && <p className="mt-1 text-sm text-red-400">{errors.eventDate}</p>}
          </div>

          <div>
            <label htmlFor="noticeDate" className="block text-sm font-medium text-fd-text mb-1">
              Notice Date *
            </label>
            <input
              type="date"
              id="noticeDate"
              value={formData.noticeDate}
              onChange={(e) => handleInputChange('noticeDate', e.target.value)}
              className={`w-full px-3 py-2 bg-fd-dark border rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green text-fd-text ${
                errors.noticeDate ? 'border-red-500' : 'border-fd-border'
              }`}
              disabled={isLoading}
            />
            {errors.noticeDate && <p className="mt-1 text-sm text-red-400">{errors.noticeDate}</p>}
          </div>

          <div>
            <label
              htmlFor="settlementMethod"
              className="block text-sm font-medium text-fd-text mb-1"
            >
              Settlement Method *
            </label>
            <select
              id="settlementMethod"
              value={formData.settlementMethod}
              onChange={(e) => handleInputChange('settlementMethod', e.target.value)}
              className={`w-full px-3 py-2 bg-fd-dark border rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green text-fd-text ${
                errors.settlementMethod ? 'border-red-500' : 'border-fd-border'
              }`}
              disabled={isLoading}
            >
              {settlementMethodOptions.map((option) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
            {errors.settlementMethod && (
              <p className="mt-1 text-sm text-red-400">{errors.settlementMethod}</p>
            )}
          </div>

          <div>
            <label htmlFor="comments" className="block text-sm font-medium text-fd-text mb-1">
              Comments
            </label>
            <textarea
              id="comments"
              value={formData.comments}
              onChange={(e) => handleInputChange('comments', e.target.value)}
              rows={3}
              className="w-full px-3 py-2 bg-fd-dark border border-fd-border rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green text-fd-text placeholder-fd-text-muted"
              placeholder="Optional comments about the credit event..."
              disabled={isLoading}
            />
          </div>

          <div className="flex justify-end space-x-3 pt-4">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 text-sm font-medium text-fd-text bg-fd-dark border border-fd-border rounded-md hover:bg-fd-border focus:outline-none focus:ring-2 focus:ring-fd-green transition-colors"
              disabled={isLoading}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="px-4 py-2 text-sm font-medium text-fd-dark bg-fd-green border border-transparent rounded-md hover:bg-fd-green-hover focus:outline-none focus:ring-2 focus:ring-fd-green disabled:opacity-50 transition-colors"
              disabled={isLoading}
            >
              {isLoading ? 'Recording...' : 'Record Credit Event'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreditEventModal;

import React, { useState, useEffect } from 'react';
import { formatCurrency, formatDateTime } from '../../utils/formatters';

interface MarginStatement {
  id: number;
  statementId: string;
  ccpName: string;
  memberFirm: string;
  accountNumber: string;
  statementDate: string;
  currency: string;
  format: string;
  fileName: string;
  fileSize: number;
  status: string;
  createdAt: string;
  updatedAt: string;
  processedAt: string | null;
  errorMessage: string;
  retryCount: number;
  variationMargin?: number;
  initialMargin?: number;
  totalPositions?: number;
}

interface MarginPosition {
  id: number;
  positionId: string;
  ccpAccount: string;
  product: string;
  variationMargin: number;
  initialMargin: number;
  currency: string;
}

interface MarginDashboardProps {
  asOfDate: string;
  onRefresh: () => void;
}

const MarginDashboard: React.FC<MarginDashboardProps> = ({
  asOfDate,
  onRefresh
}) => {
  const [statements, setStatements] = useState<MarginStatement[]>([]);
  const [selectedStatement, setSelectedStatement] = useState<MarginStatement | null>(null);
  const [positions, setPositions] = useState<MarginPosition[]>([]);
  const [loading, setLoading] = useState(true);
  const [loadingPositions, setLoadingPositions] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [filter, setFilter] = useState<string>('ALL');

  const statusFilters = ['ALL', 'PROCESSED', 'PENDING', 'FAILED'];

  useEffect(() => {
    loadStatements();
  }, [asOfDate]); // eslint-disable-line react-hooks/exhaustive-deps

  const loadStatements = async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await fetch(`/api/margin-statements?asOfDate=${asOfDate}`);
      if (!response.ok) {
        throw new Error('Failed to load statements');
      }

      const data = await response.json();
      setStatements(data);
    } catch (err: any) {
      setError(err.message || 'Failed to load statements');
    } finally {
      setLoading(false);
    }
  };

  const loadPositions = async (statementId: number) => {
    try {
      setLoadingPositions(true);
      const response = await fetch(`/api/margin-statements/${statementId}/positions`);
      if (!response.ok) {
        throw new Error('Failed to load positions');
      }
      const data = await response.json();
      setPositions(data);
    } catch (err: any) {
      console.error('Failed to load positions:', err);
      setPositions([]);
    } finally {
      setLoadingPositions(false);
    }
  };

  const formatFileSize = (bytes: number): string => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'PENDING':
        return 'text-yellow-400';
      case 'PROCESSING':
        return 'text-blue-400';
      case 'PROCESSED':
        return 'text-green-400';
      case 'FAILED':
        return 'text-red-400';
      case 'DISPUTED':
        return 'text-orange-400';
      default:
        return 'text-fd-text-muted';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'PENDING':
        return (
          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z"></path>
          </svg>
        );
      case 'PROCESSING':
        return (
          <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-current"></div>
        );
      case 'PROCESSED':
        return (
          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z"></path>
          </svg>
        );
      case 'FAILED':
        return (
          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path>
          </svg>
        );
      default:
        return null;
    }
  };

  const filteredStatements = statements.filter(statement => 
    filter === 'ALL' || statement.status === filter
  );

  const handleStatementClick = (statement: MarginStatement) => {
    setSelectedStatement(statement);
    if (statement.status === 'PROCESSED') {
      loadPositions(statement.id);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-400"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-500/20 border border-red-500/50 rounded-lg p-4">
        <div className="flex items-center">
          <svg className="w-5 h-5 text-red-400 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"></path>
          </svg>
          <div>
            <h3 className="text-sm font-medium text-red-400">Error Loading Statements</h3>
            <p className="text-sm text-red-300 mt-1">{error}</p>
          </div>
        </div>
        <button
          onClick={loadStatements}
          className="mt-3 px-3 py-1 bg-red-500/30 text-red-400 rounded text-sm hover:bg-red-500/40 transition-colors"
        >
          Retry
        </button>
      </div>
    );
  }

  return (
    <div className="bg-fd-darker rounded-lg border border-fd-border">
      {/* Header */}
      <div className="px-6 py-4 border-b border-fd-border">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <svg className="w-8 h-8 text-blue-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 17v-2m3 2v-4m3 4v-6m2 10H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"></path>
            </svg>
            <div>
              <h3 className="text-xl font-semibold text-fd-text">Margin Statements</h3>
              <p className="text-sm text-fd-text-muted">Daily VM/IM positions from CCPs</p>
            </div>
          </div>

          <div className="flex items-center space-x-3">
            <span className="text-sm text-fd-text-muted">As of {asOfDate}</span>
            <button
              onClick={() => { loadStatements(); onRefresh(); }}
              className="p-2 text-fd-text-muted hover:text-fd-text hover:bg-fd-border/50 rounded-md transition-colors"
              title="Refresh statements"
            >
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15"></path>
              </svg>
            </button>
          </div>
        </div>

        {/* Filter Tabs */}
        <div className="flex space-x-1 bg-fd-dark rounded-lg p-1 mt-4">
          {statusFilters.map(status => (
            <button
              key={status}
              onClick={() => setFilter(status)}
              className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                filter === status
                  ? 'bg-blue-500 text-white'
                  : 'text-fd-text-muted hover:text-fd-text hover:bg-fd-border/50'
              }`}
            >
              {status}
              <span className="ml-1 text-xs">
                ({status === 'ALL' ? statements.length : statements.filter(s => s.status === status).length})
              </span>
            </button>
          ))}
        </div>
      </div>

      {/* Statements Grid */}
      <div className="p-6">
        {filteredStatements.length === 0 ? (
          <div className="text-center py-12">
            <svg className="mx-auto h-12 w-12 text-fd-text-muted" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 17v-2m3 2v-4m3 4v-6m2 10H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"></path>
            </svg>
            <h3 className="mt-2 text-sm font-medium text-fd-text">No margin statements found</h3>
            <p className="mt-1 text-sm text-fd-text-muted">
              {filter === 'ALL' ? `No statements available for ${asOfDate}` : `No statements with status: ${filter}`}
            </p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {filteredStatements.map((statement) => (
              <div
                key={statement.id}
                className="bg-fd-dark rounded-lg p-4 border border-fd-border hover:border-blue-500/50 cursor-pointer transition-all duration-200 hover:shadow-lg"
                onClick={() => handleStatementClick(statement)}
              >
                {/* Statement Header */}
                <div className="flex items-center justify-between mb-3">
                  <div className="flex items-center space-x-2">
                    <div className={`flex items-center space-x-1 ${getStatusColor(statement.status)}`}>
                      {getStatusIcon(statement.status)}
                      <span className="text-xs font-medium uppercase">{statement.status}</span>
                    </div>
                  </div>
                  <span className="text-xs text-fd-text-muted">
                    {new Date(statement.statementDate).toLocaleDateString()}
                  </span>
                </div>

                {/* Statement Details */}
                <div className="space-y-2">
                  <div>
                    <h4 className="font-medium text-fd-text truncate">{statement.statementId}</h4>
                    <p className="text-sm text-fd-text-muted">{statement.ccpName}</p>
                  </div>

                  <div className="flex justify-between text-sm">
                    <span className="text-fd-text-muted">Account:</span>
                    <span className="text-fd-text font-mono">{statement.accountNumber}</span>
                  </div>

                  {statement.status === 'PROCESSED' && (
                    <>
                      {statement.variationMargin !== undefined && (
                        <div className="flex justify-between text-sm">
                          <span className="text-fd-text-muted">VM:</span>
                          <span className={`font-medium ${statement.variationMargin >= 0 ? 'text-green-400' : 'text-red-400'}`}>
                            {formatCurrency(statement.variationMargin)}
                          </span>
                        </div>
                      )}
                      {statement.initialMargin !== undefined && (
                        <div className="flex justify-between text-sm">
                          <span className="text-fd-text-muted">IM:</span>
                          <span className="text-fd-text font-medium">
                            {formatCurrency(statement.initialMargin)}
                          </span>
                        </div>
                      )}
                      {statement.totalPositions !== undefined && (
                        <div className="flex justify-between text-sm">
                          <span className="text-fd-text-muted">Positions:</span>
                          <span className="text-fd-text">{statement.totalPositions}</span>
                        </div>
                      )}
                    </>
                  )}

                  <div className="flex justify-between text-xs text-fd-text-muted">
                    <span>{formatFileSize(statement.fileSize)} {statement.format}</span>
                    <span>{formatDateTime(statement.createdAt)}</span>
                  </div>
                </div>

                {statement.errorMessage && (
                  <div className="mt-2 p-2 bg-red-500/20 border border-red-500/50 rounded text-xs text-red-400">
                    {statement.errorMessage.substring(0, 100)}...
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Statement Detail Modal */}
      {selectedStatement && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-fd-darker rounded-lg shadow-lg border border-fd-border p-6 max-w-4xl w-full mx-4 max-h-[90vh] overflow-y-auto">
            <div className="flex items-center justify-between mb-6">
              <h3 className="text-xl font-bold text-fd-text">{selectedStatement.statementId}</h3>
              <button 
                onClick={() => setSelectedStatement(null)}
                className="text-fd-text-muted hover:text-fd-text transition-colors"
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path>
                </svg>
              </button>
            </div>

            {/* Statement Info */}
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6 text-sm">
              <div>
                <span className="text-fd-text-muted">CCP:</span>
                <div className="text-fd-text font-medium">{selectedStatement.ccpName}</div>
              </div>
              <div>
                <span className="text-fd-text-muted">Account:</span>
                <div className="text-fd-text font-mono">{selectedStatement.accountNumber}</div>
              </div>
              <div>
                <span className="text-fd-text-muted">Date:</span>
                <div className="text-fd-text">{new Date(selectedStatement.statementDate).toLocaleDateString()}</div>
              </div>
              <div>
                <span className="text-fd-text-muted">Status:</span>
                <div className={`flex items-center space-x-1 ${getStatusColor(selectedStatement.status)}`}>
                  {getStatusIcon(selectedStatement.status)}
                  <span className="font-medium">{selectedStatement.status}</span>
                </div>
              </div>
            </div>

            {/* Positions Table */}
            {selectedStatement.status === 'PROCESSED' && (
              <div className="border border-fd-border rounded-lg">
                <div className="px-4 py-3 border-b border-fd-border bg-fd-dark">
                  <h4 className="font-medium text-fd-text">Margin Positions</h4>
                </div>

                {loadingPositions ? (
                  <div className="flex items-center justify-center py-8">
                    <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-400"></div>
                  </div>
                ) : positions.length > 0 ? (
                  <div className="overflow-x-auto">
                    <table className="w-full">
                      <thead className="bg-fd-dark text-xs">
                        <tr>
                          <th className="px-4 py-2 text-left text-fd-text-muted">Position</th>
                          <th className="px-4 py-2 text-left text-fd-text-muted">Account</th>
                          <th className="px-4 py-2 text-left text-fd-text-muted">Product</th>
                          <th className="px-4 py-2 text-right text-fd-text-muted">VM</th>
                          <th className="px-4 py-2 text-right text-fd-text-muted">IM</th>
                          <th className="px-4 py-2 text-right text-fd-text-muted">Currency</th>
                        </tr>
                      </thead>
                      <tbody className="text-sm">
                        {positions.map((position) => (
                          <tr key={position.id} className="border-t border-fd-border hover:bg-fd-dark/50">
                            <td className="px-4 py-2 text-fd-text font-mono text-xs">{position.positionId}</td>
                            <td className="px-4 py-2 text-fd-text">{position.ccpAccount}</td>
                            <td className="px-4 py-2 text-fd-text">{position.product}</td>
                            <td className={`px-4 py-2 text-right font-medium ${position.variationMargin >= 0 ? 'text-green-400' : 'text-red-400'}`}>
                              {formatCurrency(position.variationMargin)}
                            </td>
                            <td className="px-4 py-2 text-right text-fd-text font-medium">
                              {formatCurrency(position.initialMargin)}
                            </td>
                            <td className="px-4 py-2 text-right text-fd-text-muted">{position.currency}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                ) : (
                  <div className="text-center py-8 text-fd-text-muted">
                    No positions found
                  </div>
                )}
              </div>
            )}

            {selectedStatement.errorMessage && (
              <div className="mt-4 bg-red-500/20 border border-red-500/50 rounded-lg p-3">
                <div className="text-red-400 text-sm font-medium mb-1">Error Message:</div>
                <div className="text-red-300 text-sm">{selectedStatement.errorMessage}</div>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default MarginDashboard;

import React, { useState } from 'react';
import { formatCurrency, formatNumber } from '../../utils/formatters';

interface SimmBucketData {
  bucketNumber: string;
  assetClass: string;
  initialMargin: number;
  sensitivities: number;
  delta: number;
  vega: number;
  curvature: number;
}

interface SimmCalculation {
  id: string;
  portfolioId: string;
  calculationDate: string;
  totalInitialMargin: number;
  currency: string;
  buckets: SimmBucketData[];
  parametersVersion: string;
  calculationStatus: 'COMPLETED' | 'FAILED' | 'PENDING';
}

interface SimmDashboardProps {
  simmCalculations: SimmCalculation[];
  asOfDate: string;
  onRefresh: () => void;
}

const SimmDashboard: React.FC<SimmDashboardProps> = ({
  simmCalculations,
  asOfDate,
  onRefresh
}) => {
  const [selectedCalculation, setSelectedCalculation] = useState<SimmCalculation | null>(
    simmCalculations.length > 0 ? simmCalculations[0] : null
  );
  const [expandedBuckets, setExpandedBuckets] = useState<Set<string>>(new Set());
  const [groupBy, setGroupBy] = useState<'assetClass' | 'bucket'>('assetClass');

  const toggleBucketExpansion = (bucketId: string) => {
    const newExpanded = new Set(expandedBuckets);
    if (newExpanded.has(bucketId)) {
      newExpanded.delete(bucketId);
    } else {
      newExpanded.add(bucketId);
    }
    setExpandedBuckets(newExpanded);
  };

  const getGroupedBuckets = () => {
    if (!selectedCalculation) return {};

    return selectedCalculation.buckets.reduce((groups, bucket) => {
      const key = groupBy === 'assetClass' ? bucket.assetClass : bucket.bucketNumber;
      if (!groups[key]) {
        groups[key] = [];
      }
      groups[key].push(bucket);
      return groups;
    }, {} as Record<string, SimmBucketData[]>);
  };

  const getAssetClassTotals = () => {
    if (!selectedCalculation) return {};

    return selectedCalculation.buckets.reduce((totals, bucket) => {
      const assetClass = bucket.assetClass;
      if (!totals[assetClass]) {
        totals[assetClass] = {
          initialMargin: 0,
          sensitivities: 0,
          bucketCount: 0
        };
      }
      totals[assetClass].initialMargin += bucket.initialMargin;
      totals[assetClass].sensitivities += bucket.sensitivities;
      totals[assetClass].bucketCount += 1;
      return totals;
    }, {} as Record<string, { initialMargin: number; sensitivities: number; bucketCount: number }>);
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'COMPLETED': return 'text-green-400';
      case 'FAILED': return 'text-red-400';
      case 'PENDING': return 'text-yellow-400';
      default: return 'text-fd-text-muted';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'COMPLETED':
        return (
          <svg className="w-4 h-4 text-green-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7"></path>
          </svg>
        );
      case 'FAILED':
        return (
          <svg className="w-4 h-4 text-red-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path>
          </svg>
        );
      case 'PENDING':
        return (
          <svg className="w-4 h-4 text-yellow-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z"></path>
          </svg>
        );
      default:
        return null;
    }
  };

  return (
    <div className="bg-fd-darker rounded-lg border border-fd-border p-6">
      {/* Header */}
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center space-x-3">
          <svg className="w-8 h-8 text-purple-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 7h6m0 10v-3m-3 3h.01M9 17h.01M9 14h.01M12 14h.01M15 11h.01M12 11h.01M9 11h.01M7 21h10a2 2 0 002-2V5a2 2 0 00-2-2H7a2 2 0 00-2 2v14a2 2 0 002 2z"></path>
          </svg>
          <div>
            <h3 className="text-xl font-semibold text-fd-text">SIMM Calculations</h3>
            <p className="text-sm text-fd-text-muted">Initial Margin Model</p>
          </div>
        </div>
        <div className="flex items-center space-x-3">
          <span className="text-sm text-fd-text-muted">As of {asOfDate}</span>
          <button
            onClick={onRefresh}
            className="p-2 text-fd-text-muted hover:text-fd-text hover:bg-fd-border/50 rounded-md transition-colors"
            title="Refresh SIMM data"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15"></path>
            </svg>
          </button>
        </div>
      </div>

      {simmCalculations.length === 0 ? (
        <div className="text-center py-8">
          <svg className="w-12 h-12 text-fd-text-muted mx-auto mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"></path>
          </svg>
          <p className="text-fd-text-muted">No SIMM calculations available</p>
          <p className="text-sm text-fd-text-muted mt-1">Upload CRIF files to generate calculations</p>
        </div>
      ) : (
        <>
          {/* Calculation Selector */}
          <div className="flex items-center space-x-4 mb-6">
            <label className="text-sm font-medium text-fd-text">Calculation:</label>
            <select
              value={selectedCalculation?.id || ''}
              onChange={(e) => {
                const calc = simmCalculations.find(c => c.id === e.target.value);
                setSelectedCalculation(calc || null);
              }}
              className="bg-fd-dark border border-fd-border rounded-md px-3 py-2 text-fd-text focus:outline-none focus:ring-2 focus:ring-purple-500"
            >
              {simmCalculations.map((calc) => (
                <option key={calc.id} value={calc.id}>
                  {calc.portfolioId} - {calc.calculationDate} ({calc.parametersVersion})
                </option>
              ))}
            </select>
          </div>

          {selectedCalculation && (
            <>
              {/* Summary Card */}
              <div className="bg-fd-dark rounded-lg p-6 border border-fd-border mb-6">
                <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
                  <div>
                    <p className="text-sm font-medium text-fd-text-muted">Total Initial Margin</p>
                    <p className="text-2xl font-bold text-fd-text mt-1">
                      {formatCurrency(selectedCalculation.totalInitialMargin)}
                    </p>
                  </div>
                  <div>
                    <p className="text-sm font-medium text-fd-text-muted">Portfolio</p>
