import React, { useState } from 'react';
import { Basket, BasketType, BasketConstituent } from '../../types/basket';
import { basketService } from '../../services/basketService';
import { REFERENCE_ENTITIES } from '../../data/referenceData';

interface BasketCreationModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSuccess: (basket: Basket) => void;
}

interface FormErrors {
  [key: string]: string;
}

const BASKET_TYPE_OPTIONS = [
  { value: 'FIRST_TO_DEFAULT', label: 'First-to-Default (FTD)' },
  { value: 'NTH_TO_DEFAULT', label: 'N-th-to-Default' },
  { value: 'TRANCHETTE', label: 'Tranchette (Loss Slice)' },
];

const PREMIUM_FREQUENCY_OPTIONS = [
  { value: 'QUARTERLY', label: 'Quarterly' },
  { value: 'SEMI_ANNUAL', label: 'Semi-Annual' },
  { value: 'ANNUAL', label: 'Annual' },
];

const DAY_COUNT_OPTIONS = [
  { value: 'ACT_360', label: 'ACT/360' },
  { value: 'ACT_365', label: 'ACT/365' },
  { value: 'THIRTY_360', label: '30/360' },
];

const CURRENCY_OPTIONS = ['USD', 'EUR', 'GBP', 'JPY'];

const SENIORITY_OPTIONS = [
  { value: 'SR_UNSEC', label: 'Senior Unsecured' },
  { value: 'SR_SEC', label: 'Senior Secured' },
  { value: 'SUBORD', label: 'Subordinated' },
];

const BasketCreationModal: React.FC<BasketCreationModalProps> = ({
  isOpen,
  onClose,
  onSuccess,
}) => {
  const [formData, setFormData] = useState<Partial<Basket>>({
    type: 'FIRST_TO_DEFAULT',
    currency: 'USD',
    premiumFrequency: 'QUARTERLY',
    dayCount: 'ACT_360',
    constituents: [],
  });

  const [constituents, setConstituents] = useState<Partial<BasketConstituent>[]>([]);
  const [errors, setErrors] = useState<FormErrors>({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Calculate normalized weights dynamically
  const calculateNormalizedWeights = () => {
    const totalWeight = constituents.reduce((sum, c) => sum + (c.weight || 0), 0);

    if (totalWeight === 0) {
      // Equal weights if none provided
      return constituents.map(() => 1 / constituents.length);
    }

    // Normalize to sum to 1
    return constituents.map((c) => (c.weight || 0) / totalWeight);
  };

  const validateForm = (): boolean => {
    const newErrors: FormErrors = {};

    if (!formData.name?.trim()) {
      newErrors.name = 'Basket name is required';
    }

    if (!formData.type) {
      newErrors.type = 'Basket type is required';
    }

    if (formData.type === 'NTH_TO_DEFAULT' && (!formData.nth || formData.nth < 1)) {
      newErrors.nth = 'N-th value must be >= 1';
    }

    if (formData.type === 'NTH_TO_DEFAULT' && formData.nth && formData.nth > constituents.length) {
      newErrors.nth = `N-th value cannot exceed number of constituents (${constituents.length})`;
    }

    if (formData.type === 'TRANCHETTE') {
      if (
        formData.attachmentPoint === undefined ||
        formData.attachmentPoint < 0 ||
        formData.attachmentPoint > 1
      ) {
        newErrors.attachmentPoint = 'Attachment point must be between 0 and 1';
      }
      if (
        formData.detachmentPoint === undefined ||
        formData.detachmentPoint < 0 ||
        formData.detachmentPoint > 1
      ) {
        newErrors.detachmentPoint = 'Detachment point must be between 0 and 1';
      }
      if (
        formData.attachmentPoint !== undefined &&
        formData.detachmentPoint !== undefined &&
        formData.attachmentPoint >= formData.detachmentPoint
      ) {
        newErrors.detachmentPoint = 'Detachment point must be greater than attachment point';
      }
    }

    if (!formData.notional || formData.notional <= 0) {
      newErrors.notional = 'Notional must be positive';
    }

    if (!formData.maturityDate) {
      newErrors.maturityDate = 'Maturity date is required';
    }

    if (constituents.length === 0) {
      newErrors.constituents = 'At least one constituent is required';
    }

    // Check for duplicate issuers
    const issuerSet = new Set<string>();
    constituents.forEach((c, idx) => {
      if (c.issuer) {
        if (issuerSet.has(c.issuer)) {
          newErrors[`constituent_${idx}_issuer`] = 'Duplicate issuer';
        }
        issuerSet.add(c.issuer);
      } else {
        newErrors[`constituent_${idx}_issuer`] = 'Issuer is required';
      }
    });

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleInputChange = (field: keyof Basket, value: any) => {
    setFormData((prev) => ({
      ...prev,
      [field]: value,
    }));

    if (errors[field]) {
      setErrors((prev) => ({
        ...prev,
        [field]: '',
      }));
    }
  };

  const handleConstituentChange = (index: number, field: keyof BasketConstituent, value: string | number) => {
    const updated = [...constituents];
    updated[index] = { ...updated[index], [field]: value };
    setConstituents(updated);

    const errorKey = `constituent_${index}_${field}`;
    if (errors[errorKey]) {
      setErrors((prev) => ({
        ...prev,
        [errorKey]: '',
      }));
    }
  };

  const addConstituent = () => {
    setConstituents([...constituents, { seniority: 'SR_UNSEC' }]);
  };

  const removeConstituent = (index: number) => {
    setConstituents(constituents.filter((_, i) => i !== index));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);

    try {
      const normalizedWeights = calculateNormalizedWeights();

      const basketRequest = {
        ...formData,
        constituents: constituents.map((c, idx) => ({
          issuer: c.issuer!,
          weight: normalizedWeights[idx],
          recoveryOverride: c.recoveryOverride,
          seniority: c.seniority || 'SR_UNSEC',
          sector: c.sector,
        })),
      };

      const basket = await basketService.createBasket(basketRequest as any);
      onSuccess(basket);
      resetForm();
      onClose();
    } catch (error: any) {
      setErrors({ submit: error.message || 'Failed to create basket' });
    } finally {
      setIsSubmitting(false);
    }
  };

  const resetForm = () => {
    setFormData({
      type: 'FIRST_TO_DEFAULT',
      currency: 'USD',
      premiumFrequency: 'QUARTERLY',
      dayCount: 'ACT_360',
      constituents: [],
    });
    setConstituents([]);
    setErrors({});
  };

  if (!isOpen) return null;

  const normalizedWeights = calculateNormalizedWeights();

  return (
    <div
      className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50"
      onClick={onClose}
    >
      <div
        className="bg-fd-darker rounded-lg shadow-xl border border-fd-border max-w-6xl w-full max-h-[90vh] overflow-y-auto m-4"
        onClick={(e) => e.stopPropagation()}
      >
        {/* Header */}
        <div className="sticky top-0 bg-fd-darker border-b border-fd-border px-6 py-4 flex justify-between items-center z-10">
          <h2 className="text-xl font-semibold text-fd-text">Create New Basket Derivative</h2>
          <button
            onClick={onClose}
            className="text-fd-text-muted hover:text-fd-text transition-colors"
            aria-label="Close modal"
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M6 18L18 6M6 6l12 12"
              />
            </svg>
          </button>
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit} className="p-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {/* Basic Information Section */}
            <div className="md:col-span-2">
              <h3 className="text-sm font-semibold text-fd-text-muted uppercase tracking-wide mb-4">
                Basic Information
              </h3>
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Basket Name <span className="text-red-500">*</span>
              </label>
              <input
                type="text"
                value={formData.name || ''}
                onChange={(e) => handleInputChange('name', e.target.value)}
                className={`w-full px-3 py-2 bg-fd-dark border ${errors.name ? 'border-red-500' : 'border-fd-border'} text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent`}
                placeholder="e.g., US_IG_TECH_FTD_5Y"
              />
              {errors.name && <p className="mt-1 text-sm text-red-400">{errors.name}</p>}
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Basket Type <span className="text-red-500">*</span>
              </label>
              <select
                value={formData.type}
                onChange={(e) => handleInputChange('type', e.target.value as BasketType)}
                className="w-full px-3 py-2 bg-fd-dark border border-fd-border text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent"
              >
                {BASKET_TYPE_OPTIONS.map((opt) => (
                  <option key={opt.value} value={opt.value}>
                    {opt.label}
                  </option>
                ))}
              </select>
              {errors.type && <p className="mt-1 text-sm text-red-400">{errors.type}</p>}
            </div>

            {/* Conditional Fields for N-th-to-Default */}
            {formData.type === 'NTH_TO_DEFAULT' && (
              <div>
                <label className="block text-sm font-medium text-fd-text mb-1">
                  N-th Value <span className="text-red-500">*</span>
                </label>
                <input
                  type="number"
                  min="1"
                  value={formData.nth || ''}
                  onChange={(e) => handleInputChange('nth', parseInt(e.target.value))}
                  className={`w-full px-3 py-2 bg-fd-dark border ${errors.nth ? 'border-red-500' : 'border-fd-border'} text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent`}
                  placeholder="e.g., 2 for 2nd-to-default"
                />
                {errors.nth && <p className="mt-1 text-sm text-red-400">{errors.nth}</p>}
              </div>
            )}

            {/* Conditional Fields for Tranchette */}
            {formData.type === 'TRANCHETTE' && (
              <>
                <div>
                  <label className="block text-sm font-medium text-fd-text mb-1">
                    Attachment Point (0-1) <span className="text-red-500">*</span>
                  </label>
                  <input
                    type="number"
                    step="0.01"
                    min="0"
                    max="1"
                    value={formData.attachmentPoint || ''}
                    onChange={(e) =>
                      handleInputChange('attachmentPoint', parseFloat(e.target.value))
                    }
                    className={`w-full px-3 py-2 bg-fd-dark border ${errors.attachmentPoint ? 'border-red-500' : 'border-fd-border'} text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent`}
                    placeholder="e.g., 0.03 for 3%"
                  />
                  {errors.attachmentPoint && (
                    <p className="mt-1 text-sm text-red-400">{errors.attachmentPoint}</p>
                  )}
                </div>

                <div>
                  <label className="block text-sm font-medium text-fd-text mb-1">
                    Detachment Point (0-1) <span className="text-red-500">*</span>
                  </label>
                  <input
                    type="number"
                    step="0.01"
                    min="0"
                    max="1"
                    value={formData.detachmentPoint || ''}
                    onChange={(e) =>
                      handleInputChange('detachmentPoint', parseFloat(e.target.value))
                    }
                    className={`w-full px-3 py-2 bg-fd-dark border ${errors.detachmentPoint ? 'border-red-500' : 'border-fd-border'} text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent`}
                    placeholder="e.g., 0.07 for 7%"
                  />
                  {errors.detachmentPoint && (
                    <p className="mt-1 text-sm text-red-400">{errors.detachmentPoint}</p>
                  )}
                </div>
              </>
            )}

            {/* Economics Section */}
            <div className="md:col-span-2 mt-4">
              <h3 className="text-sm font-semibold text-fd-text-muted uppercase tracking-wide mb-4">
                Economics
              </h3>
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Currency <span className="text-red-500">*</span>
              </label>
              <select
                value={formData.currency}
                onChange={(e) => handleInputChange('currency', e.target.value)}
                className="w-full px-3 py-2 bg-fd-dark border border-fd-border text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent"
              >
                {CURRENCY_OPTIONS.map((curr) => (
                  <option key={curr} value={curr}>
                    {curr}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Notional <span className="text-red-500">*</span>
              </label>
              <input
                type="number"
                step="0.01"
                value={formData.notional || ''}
                onChange={(e) => handleInputChange('notional', parseFloat(e.target.value))}
                className={`w-full px-3 py-2 bg-fd-dark border ${errors.notional ? 'border-red-500' : 'border-fd-border'} text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent`}
                placeholder="e.g., 10000000"
              />
              {errors.notional && <p className="mt-1 text-sm text-red-400">{errors.notional}</p>}
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Premium Frequency
              </label>
              <select
                value={formData.premiumFrequency}
                onChange={(e) => handleInputChange('premiumFrequency', e.target.value)}
                className="w-full px-3 py-2 bg-fd-dark border border-fd-border text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent"
              >
                {PREMIUM_FREQUENCY_OPTIONS.map((opt) => (
                  <option key={opt.value} value={opt.value}>
                    {opt.label}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Day Count Convention
              </label>
              <select
                value={formData.dayCount}
                onChange={(e) => handleInputChange('dayCount', e.target.value)}
                className="w-full px-3 py-2 bg-fd-dark border border-fd-border text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent"
              >
                {DAY_COUNT_OPTIONS.map((opt) => (
                  <option key={opt.value} value={opt.value}>
                    {opt.label}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Maturity Date <span className="text-red-500">*</span>
              </label>
              <input
                type="date"
                value={formData.maturityDate || ''}
                onChange={(e) => handleInputChange('maturityDate', e.target.value)}
                className={`w-full px-3 py-2 bg-fd-dark border ${errors.maturityDate ? 'border-red-500' : 'border-fd-border'} text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent`}
              />
              {errors.maturityDate && (
                <p className="mt-1 text-sm text-red-400">{errors.maturityDate}</p>
              )}
            </div>

            {/* Constituents Section */}
            <div className="md:col-span-2 mt-4">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-sm font-semibold text-fd-text-muted uppercase tracking-wide">
                  Basket Constituents <span className="text-red-500">*</span>
                </h3>
                <button
                  type="button"
                  onClick={addConstituent}
                  className="px-3 py-1 text-sm bg-fd-green text-fd-dark rounded-md hover:bg-fd-green-hover focus:outline-none focus:ring-2 focus:ring-fd-green"
                >
                  + Add Constituent
                </button>
              </div>

              {errors.constituents && (
                <p className="mb-2 text-sm text-red-400">{errors.constituents}</p>
              )}

              {constituents.map((constituent, index) => {
                const normalizedWeight = normalizedWeights[index];
                return (
                  <div
                    key={index}
                    className="mb-4 p-4 bg-fd-dark border border-fd-border rounded-md"
                  >
                    <div className="flex justify-between items-center mb-3">
                      <h4 className="text-sm font-medium text-fd-text">Constituent #{index + 1}</h4>
                      <button
                        type="button"
                        onClick={() => removeConstituent(index)}
                        className="text-red-400 hover:text-red-300 text-sm"
                      >
                        Remove
                      </button>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-3 gap-3">
                      <div>
                        <label className="block text-xs font-medium text-fd-text-muted mb-1">
                          Issuer <span className="text-red-500">*</span>
                        </label>
                        <select
                          value={constituent.issuer || ''}
                          onChange={(e) => handleConstituentChange(index, 'issuer', e.target.value)}
                          className={`w-full px-2 py-1 text-sm bg-fd-darker border ${errors[`constituent_${index}_issuer`] ? 'border-red-500' : 'border-fd-border'} text-fd-text rounded-md focus:outline-none focus:ring-1 focus:ring-fd-green`}
                        >
                          <option value="">Select Issuer</option>
                          {REFERENCE_ENTITIES.map((entity) => (
                            <option key={entity.code} value={entity.code}>
                              {entity.code} - {entity.name}
                            </option>
                          ))}
                        </select>
                        {errors[`constituent_${index}_issuer`] && (
                          <p className="mt-1 text-xs text-red-400">
                            {errors[`constituent_${index}_issuer`]}
                          </p>
                        )}
                      </div>

                      <div>
                        <label className="block text-xs font-medium text-fd-text-muted mb-1">
                          Weight (optional, normalized)
                        </label>
                        <input
                          type="number"
                          step="0.01"
                          value={constituent.weight || ''}
                          onChange={(e) =>
                            handleConstituentChange(
                              index,
                              'weight',
                              parseFloat(e.target.value) || 0
                            )
                          }
                          className="w-full px-2 py-1 text-sm bg-fd-darker border border-fd-border text-fd-text rounded-md focus:outline-none focus:ring-1 focus:ring-fd-green"
                          placeholder={`Auto: ${normalizedWeight.toFixed(4)}`}
                        />
                        {normalizedWeight !== undefined && (
                          <p className="mt-1 text-xs text-fd-text-muted">
                            Normalized: {(normalizedWeight * 100).toFixed(2)}%
                          </p>
                        )}
                      </div>

                      <div>
                        <label className="block text-xs font-medium text-fd-text-muted mb-1">
                          Seniority
                        </label>
                        <select
                          value={constituent.seniority || 'SR_UNSEC'}
                          onChange={(e) =>
                            handleConstituentChange(index, 'seniority', e.target.value)
                          }
                          className="w-full px-2 py-1 text-sm bg-fd-darker border border-fd-border text-fd-text rounded-md focus:outline-none focus:ring-1 focus:ring-fd-green"
                        >
                          {SENIORITY_OPTIONS.map((opt) => (
                            <option key={opt.value} value={opt.value}>
                              {opt.label}
                            </option>
                          ))}
                        </select>
                      </div>

                      <div>
                        <label className="block text-xs font-medium text-fd-text-muted mb-1">
                          Recovery Override (0-1)
                        </label>
                        <input
                          type="number"
                          step="0.01"
                          min="0"
                          max="1"
                          value={constituent.recoveryOverride || ''}
                          onChange={(e) =>
                            handleConstituentChange(
                              index,
                              'recoveryOverride',
                              parseFloat(e.target.value)
                            )
                          }
                          className="w-full px-2 py-1 text-sm bg-fd-darker border border-fd-border text-fd-text rounded-md focus:outline-none focus:ring-1 focus:ring-fd-green"
                          placeholder="Optional"
                        />
                      </div>

                      <div className="md:col-span-2">
                        <label className="block text-xs font-medium text-fd-text-muted mb-1">
                          Sector (optional)
                        </label>
                        <input
                          type="text"
                          value={constituent.sector || ''}
                          onChange={(e) => handleConstituentChange(index, 'sector', e.target.value)}
                          className="w-full px-2 py-1 text-sm bg-fd-darker border border-fd-border text-fd-text rounded-md focus:outline-none focus:ring-1 focus:ring-fd-green"
                          placeholder="e.g., TECH, ENERGY"
                        />
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>

          {/* Error Message */}
          {errors.submit && (
            <div className="mt-4 p-3 bg-red-900/20 border border-red-500/30 rounded-md">
              <p className="text-sm text-red-400">{errors.submit}</p>
            </div>
          )}

          {/* Action Buttons */}
          <div className="mt-6 flex justify-end space-x-3">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 text-sm font-medium text-fd-text bg-fd-dark border border-fd-border rounded-md hover:bg-fd-darker focus:outline-none focus:ring-2 focus:ring-fd-green"
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={isSubmitting}
              className="px-4 py-2 text-sm font-medium text-fd-dark bg-fd-green border border-transparent rounded-md hover:bg-fd-green-hover focus:outline-none focus:ring-2 focus:ring-fd-green disabled:bg-fd-green/50 disabled:cursor-not-allowed"
            >
              {isSubmitting ? 'Creating...' : 'Create Basket'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default BasketCreationModal;

import React, { useState, useEffect } from 'react';
import { Bond, bondService } from '../../services/bondService';
import { REFERENCE_ENTITIES, SECTORS } from '../../data/referenceData';

interface BondCreationModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSuccess: (bond: Bond) => void;
}

interface FormErrors {
  [key: string]: string;
}

const SENIORITY_OPTIONS = [
  { value: 'SR_UNSEC', label: 'Senior Unsecured' },
  { value: 'SR_SEC', label: 'Senior Secured' },
  { value: 'SUBORD', label: 'Subordinated' },
];

const COUPON_FREQUENCY_OPTIONS = [
  { value: 'ANNUAL', label: 'Annual' },
  { value: 'SEMI_ANNUAL', label: 'Semi-Annual' },
  { value: 'QUARTERLY', label: 'Quarterly' },
];

const DAY_COUNT_OPTIONS = [
  { value: 'ACT_ACT', label: 'ACT/ACT' },
  { value: 'THIRTY_360', label: '30/360' },
];

const CURRENCY_OPTIONS = ['USD', 'EUR', 'GBP', 'JPY'];

const BondCreationModal: React.FC<BondCreationModalProps> = ({ isOpen, onClose, onSuccess }) => {
  const [formData, setFormData] = useState<Partial<Bond>>({
    currency: 'USD',
    couponFrequency: 'SEMI_ANNUAL',
    dayCount: 'ACT_ACT',
    seniority: 'SR_UNSEC',
    settlementDays: 2,
    faceValue: 100,
    priceConvention: 'CLEAN',
  });

  const [errors, setErrors] = useState<FormErrors>({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [userModifiedSector, setUserModifiedSector] = useState(false);

  // Auto-populate sector when issuer changes
  useEffect(() => {
    if (!userModifiedSector && formData.issuer) {
      const entity = REFERENCE_ENTITIES.find((e) => e.code === formData.issuer);
      if (entity?.sector) {
        setFormData((prev) => ({
          ...prev,
          sector: entity.sector,
        }));
      }
    }
  }, [formData.issuer, userModifiedSector]);

  const validateForm = (): boolean => {
    const newErrors: FormErrors = {};

    if (!formData.issuer?.trim()) {
      newErrors.issuer = 'Issuer is required';
    }

    if (!formData.notional || formData.notional <= 0) {
      newErrors.notional = 'Notional must be positive';
    }

    if (!formData.couponRate || formData.couponRate < 0) {
      newErrors.couponRate = 'Coupon rate must be >= 0';
    }

    if (formData.couponRate && formData.couponRate > 1) {
      newErrors.couponRate = 'Coupon rate exceeds 100% - please verify';
    }

    if (!formData.issueDate) {
      newErrors.issueDate = 'Issue date is required';
    }

    if (!formData.maturityDate) {
      newErrors.maturityDate = 'Maturity date is required';
    }

    if (
      formData.issueDate &&
      formData.maturityDate &&
      new Date(formData.issueDate) >= new Date(formData.maturityDate)
    ) {
      newErrors.maturityDate = 'Maturity date must be after issue date';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleInputChange = (field: keyof Bond, value: any) => {
    // Track if user manually edits the sector
    if (field === 'sector') {
      setUserModifiedSector(true);
    }

    setFormData((prev) => ({
      ...prev,
      [field]: value,
    }));

    if (errors[field]) {
      setErrors((prev) => ({
        ...prev,
        [field]: '',
      }));
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);

    try {
      const bond = await bondService.createBond(formData as Bond);
      onSuccess(bond);
      resetForm();
      onClose();
    } catch (error: any) {
      setErrors({ submit: error.message || 'Failed to create bond' });
    } finally {
      setIsSubmitting(false);
    }
  };

  const resetForm = () => {
    setFormData({
      currency: 'USD',
      couponFrequency: 'SEMI_ANNUAL',
      dayCount: 'ACT_ACT',
      seniority: 'SR_UNSEC',
      settlementDays: 2,
      faceValue: 100,
      priceConvention: 'CLEAN',
    });
    setErrors({});
    setUserModifiedSector(false);
  };

  if (!isOpen) return null;

  return (
    <div
      className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50"
      onClick={onClose}
    >
      <div
        className="bg-fd-darker rounded-lg shadow-xl border border-fd-border max-w-4xl w-full max-h-[90vh] overflow-y-auto m-4"
        onClick={(e) => e.stopPropagation()}
      >
        {/* Header */}
        <div className="sticky top-0 bg-fd-darker border-b border-fd-border px-6 py-4 flex justify-between items-center z-10">
          <h2 className="text-xl font-semibold text-fd-text">Create New Bond</h2>
          <button
            onClick={onClose}
            className="text-fd-text-muted hover:text-fd-text transition-colors"
            aria-label="Close modal"
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M6 18L18 6M6 6l12 12"
              />
            </svg>
          </button>
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit} className="p-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {/* Issuer & Credit Info Section */}
            <div className="md:col-span-2">
              <h3 className="text-sm font-semibold text-fd-text-muted uppercase tracking-wide mb-4">
                Issuer & Credit Information
              </h3>
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Issuer <span className="text-red-500">*</span>
              </label>
              <select
                value={formData.issuer || ''}
                onChange={(e) => handleInputChange('issuer', e.target.value)}
                className={`w-full px-3 py-2 bg-fd-dark border ${errors.issuer ? 'border-red-500' : 'border-fd-border'} text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent`}
              >
                <option value="">Select Issuer</option>
                {REFERENCE_ENTITIES.map((entity) => (
                  <option key={entity.code} value={entity.code}>
                    {entity.code} - {entity.name}
                  </option>
                ))}
              </select>
              {errors.issuer && <p className="mt-1 text-sm text-red-400">{errors.issuer}</p>}
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Seniority <span className="text-red-500">*</span>
              </label>
              <select
                value={formData.seniority}
                onChange={(e) => handleInputChange('seniority', e.target.value)}
                className="w-full px-3 py-2 bg-fd-dark border border-fd-border text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent"
              >
                {SENIORITY_OPTIONS.map((opt) => (
                  <option key={opt.value} value={opt.value}>
                    {opt.label}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">Sector</label>
              <select
                value={formData.sector || ''}
                onChange={(e) => handleInputChange('sector', e.target.value)}
                className="w-full px-3 py-2 bg-fd-dark border border-fd-border text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent"
              >
                <option value="">Auto-suggested from issuer</option>
                {SECTORS.map((sector) => (
                  <option key={sector.code} value={sector.code}>
                    {sector.name}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">ISIN</label>
              <input
                type="text"
                value={formData.isin || ''}
                onChange={(e) => handleInputChange('isin', e.target.value)}
                className="w-full px-3 py-2 bg-fd-dark border border-fd-border text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent placeholder-fd-text-muted"
                placeholder="Optional"
                maxLength={12}
              />
            </div>

            {/* Economics Section */}
            <div className="md:col-span-2 mt-4">
              <h3 className="text-sm font-semibold text-fd-text-muted uppercase tracking-wide mb-4">
                Bond Economics
              </h3>
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Currency <span className="text-red-500">*</span>
              </label>
              <select
                value={formData.currency}
                onChange={(e) => handleInputChange('currency', e.target.value)}
                className="w-full px-3 py-2 bg-fd-dark border border-fd-border text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent"
              >
                {CURRENCY_OPTIONS.map((curr) => (
                  <option key={curr} value={curr}>
                    {curr}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Notional <span className="text-red-500">*</span>
              </label>
              <input
                type="number"
                step="0.01"
                value={formData.notional || ''}
                onChange={(e) => handleInputChange('notional', parseFloat(e.target.value))}
                className={`w-full px-3 py-2 bg-fd-dark border ${errors.notional ? 'border-red-500' : 'border-fd-border'} text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent placeholder-fd-text-muted`}
              />
              {errors.notional && <p className="mt-1 text-sm text-red-400">{errors.notional}</p>}
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Coupon Rate (decimal) <span className="text-red-500">*</span>
              </label>
              <input
                type="number"
                step="0.000001"
                value={formData.couponRate || ''}
                onChange={(e) => handleInputChange('couponRate', parseFloat(e.target.value))}
                className={`w-full px-3 py-2 bg-fd-dark border ${errors.couponRate ? 'border-red-500' : 'border-fd-border'} text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent placeholder-fd-text-muted`}
                placeholder="e.g., 0.045 for 4.5%"
              />
              {errors.couponRate && (
                <p className="mt-1 text-sm text-red-400">{errors.couponRate}</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Coupon Frequency
              </label>
              <select
                value={formData.couponFrequency}
                onChange={(e) => handleInputChange('couponFrequency', e.target.value)}
                className="w-full px-3 py-2 bg-fd-dark border border-fd-border text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent"
              >
                {COUPON_FREQUENCY_OPTIONS.map((opt) => (
                  <option key={opt.value} value={opt.value}>
                    {opt.label}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Day Count Convention
              </label>
              <select
                value={formData.dayCount}
                onChange={(e) => handleInputChange('dayCount', e.target.value)}
                className="w-full px-3 py-2 bg-fd-dark border border-fd-border text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent"
              >
                {DAY_COUNT_OPTIONS.map((opt) => (
                  <option key={opt.value} value={opt.value}>
                    {opt.label}
                  </option>
                ))}
              </select>
            </div>

            {/* Dates Section */}
            <div className="md:col-span-2 mt-4">
              <h3 className="text-sm font-semibold text-fd-text-muted uppercase tracking-wide mb-4">
                Dates
              </h3>
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Issue Date <span className="text-red-500">*</span>
              </label>
              <input
                type="date"
                value={formData.issueDate || ''}
                onChange={(e) => handleInputChange('issueDate', e.target.value)}
                className={`w-full px-3 py-2 bg-fd-dark border ${errors.issueDate ? 'border-red-500' : 'border-fd-border'} text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent`}
              />
              {errors.issueDate && <p className="mt-1 text-sm text-red-400">{errors.issueDate}</p>}
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">
                Maturity Date <span className="text-red-500">*</span>
              </label>
              <input
                type="date"
                value={formData.maturityDate || ''}
                onChange={(e) => handleInputChange('maturityDate', e.target.value)}
                className={`w-full px-3 py-2 bg-fd-dark border ${errors.maturityDate ? 'border-red-500' : 'border-fd-border'} text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent`}
              />
              {errors.maturityDate && (
                <p className="mt-1 text-sm text-red-400">{errors.maturityDate}</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">Settlement Days</label>
              <input
                type="number"
                value={formData.settlementDays || ''}
                onChange={(e) => handleInputChange('settlementDays', parseInt(e.target.value))}
                className="w-full px-3 py-2 bg-fd-dark border border-fd-border text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent placeholder-fd-text-muted"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-fd-text mb-1">Face Value</label>
              <input
                type="number"
                step="0.01"
                value={formData.faceValue || ''}
                onChange={(e) => handleInputChange('faceValue', parseFloat(e.target.value))}
                className="w-full px-3 py-2 bg-fd-dark border border-fd-border text-fd-text rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-transparent placeholder-fd-text-muted"
              />
            </div>
          </div>

          {/* Error Message */}
          {errors.submit && (
            <div className="mt-4 p-3 bg-red-900/20 border border-red-500/30 rounded-md">
              <p className="text-sm text-red-400">{errors.submit}</p>
            </div>
          )}

          {/* Action Buttons */}
          <div className="mt-6 flex justify-end space-x-3">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 text-sm font-medium text-fd-text bg-fd-dark border border-fd-border rounded-md hover:bg-fd-darker focus:outline-none focus:ring-2 focus:ring-fd-green"
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={isSubmitting}
              className="px-4 py-2 text-sm font-medium text-fd-dark bg-fd-green border border-transparent rounded-md hover:bg-fd-green-hover focus:outline-none focus:ring-2 focus:ring-fd-green disabled:bg-fd-green/50 disabled:cursor-not-allowed"
            >
              {isSubmitting ? 'Creating...' : 'Create Bond'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default BondCreationModal;

import React, { useState, useEffect } from 'react';
import {
  REFERENCE_ENTITIES,
  COUNTERPARTIES,
  CURRENCIES,
  PREMIUM_FREQUENCIES,
  DAY_COUNT_CONVENTIONS,
  RESTRUCTURING_CLAUSES,
  PAYMENT_CALENDARS,
  TRADE_STATUSES,
  SETTLEMENT_METHODS,
  CDSTrade
} from '../../data/referenceData';
import { bondService, Bond } from '../../services/bondService';

interface FormErrors {
  [key: string]: string;
}

interface CDSTradeFormProps {
  onSubmit: (trade: CDSTrade) => void;
}

const CDSTradeForm: React.FC<CDSTradeFormProps> = ({ onSubmit }) => {
  const [formData, setFormData] = useState<Partial<CDSTrade>>({
    tradeDate: new Date().toISOString().split('T')[0],
    currency: 'USD',
    premiumFrequency: 'QUARTERLY',
    dayCountConvention: 'ACT_360',
    buySellProtection: 'BUY',
    paymentCalendar: 'NYC',
    tradeStatus: 'PENDING',
    recoveryRate: 40,
    settlementType: 'CASH'
  });

  const [errors, setErrors] = useState<FormErrors>({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [availableBonds, setAvailableBonds] = useState<Bond[]>([]);
  const [loadingBonds, setLoadingBonds] = useState(false);

  // Fetch bonds when reference entity changes
  useEffect(() => {
    const fetchBonds = async () => {
      if (!formData.referenceEntity) {
        setAvailableBonds([]);
        return;
      }

      setLoadingBonds(true);
      try {
        const bonds = await bondService.getBondsByIssuer(formData.referenceEntity);
        setAvailableBonds(bonds);
      } catch (error) {
        console.error('Error fetching bonds:', error);
        setAvailableBonds([]);
      } finally {
        setLoadingBonds(false);
      }
    };

    fetchBonds();
  }, [formData.referenceEntity]);

  const validateForm = (): boolean => {
    const newErrors: FormErrors = {};

    // Required field validations
    if (!formData.referenceEntity) {
      newErrors.referenceEntity = 'Reference Entity is required';
    }
    
    if (formData.referenceEntity && !formData.obligation?.id) {
      newErrors.obligation = 'Obligation is required';
    }

    if (!formData.notionalAmount || formData.notionalAmount <= 0) {
      newErrors.notionalAmount = 'Notional Amount must be greater than 0';
    }

    if (!formData.spread || formData.spread < 0) {
      newErrors.spread = 'Spread must be 0 or greater';
    }

    if (!formData.recoveryRate || formData.recoveryRate < 0 || formData.recoveryRate > 100) {
      newErrors.recoveryRate = 'Recovery Rate must be between 0 and 100';
    }

    if (!formData.maturityDate) {
      newErrors.maturityDate = 'Maturity Date is required';
    }

    if (!formData.effectiveDate) {
      newErrors.effectiveDate = 'Effective Date is required';
    }

    if (!formData.counterparty) {
      newErrors.counterparty = 'Counterparty is required';
    }

    if (!formData.tradeDate) {
      newErrors.tradeDate = 'Trade Date is required';
    }

    if (!formData.accrualStartDate) {
      newErrors.accrualStartDate = 'Accrual Start Date is required';
    }

    // Date validations
    if (formData.effectiveDate && formData.maturityDate && 
        new Date(formData.effectiveDate) >= new Date(formData.maturityDate)) {
      newErrors.maturityDate = 'Maturity Date must be after Effective Date';
    }

    if (formData.tradeDate && formData.effectiveDate && 
        new Date(formData.tradeDate) > new Date(formData.effectiveDate)) {
      newErrors.effectiveDate = 'Effective Date must be on or after Trade Date';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleInputChange = (field: keyof CDSTrade, value: any) => {
    setFormData(prev => {
      const updates: Partial<CDSTrade> = {};
      
      // Handle obligation specially - convert to nested object
      if (field === 'obligation') {
        updates.obligation = value ? { id: value } : undefined;
      } else {
        updates[field] = value as any;
      }
      
      // Clear obligation when reference entity changes
      if (field === 'referenceEntity' && prev.referenceEntity !== value) {
        updates.obligation = undefined;
      }
      
      return {
        ...prev,
        ...updates
      };
    });

    // Clear error for this field when user starts typing
    if (errors[field]) {
      setErrors(prev => ({
        ...prev,
        [field]: ''
      }));
    }
  };

  // Format number with commas for display
  const formatNumberWithCommas = (num: number | undefined): string => {
    if (!num && num !== 0) return '';
    return num.toLocaleString('en-US');
  };

  // Handle notional amount input with formatting
  const handleNotionalChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const rawValue = e.target.value.replace(/,/g, ''); // Remove commas
    const numValue = parseFloat(rawValue);
    
    if (!isNaN(numValue) || rawValue === '') {
      handleInputChange('notionalAmount', rawValue === '' ? undefined : numValue);
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);

    // Defensive normalization
    if (formData.tradeStatus === 'SETTLED' || formData.tradeStatus === 'CONFIRMED') {
      formData.tradeStatus = 'ACTIVE';
    }

    // Simulate API call delay
    setTimeout(() => {
      onSubmit(formData as CDSTrade);
      setIsSubmitting(false);
      
      // Reset form
      setFormData({
        tradeDate: new Date().toISOString().split('T')[0],
        currency: 'USD',
        premiumFrequency: 'QUARTERLY',
        dayCountConvention: 'ACT_360',
        buySellProtection: 'BUY',
        paymentCalendar: 'NYC',
        tradeStatus: 'PENDING',
        recoveryRate: 40,
        settlementType: 'CASH'
      });
    }, 1000);
  };

  const inputClassName = (fieldName: string) => {
    const baseClass = "w-full px-3 py-2 bg-fd-input border rounded-md text-fd-text placeholder-fd-text-muted focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-fd-green";
    const errorClass = "border-red-500";
    const normalClass = "border-fd-border";
    
    return `${baseClass} ${errors[fieldName] ? errorClass : normalClass}`;
  };

  const selectClassName = (fieldName: string) => {
    const baseClass = "w-full px-3 py-2 bg-fd-input border rounded-md text-fd-text focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-fd-green";
    const errorClass = "border-red-500";
    const normalClass = "border-fd-border";
    
    return `${baseClass} ${errors[fieldName] ? errorClass : normalClass}`;
  };

  const generateRandomData = () => {
    // Helper function to get random item from array
    const getRandomItem = (array: any[]) => array[Math.floor(Math.random() * array.length)];
    
    // Helper function to get random date within a range (days offset from today)
    const getRandomDate = (startDays: number, endDays: number) => {
      const start = new Date();
      start.setDate(start.getDate() + startDays);
      const end = new Date();
      end.setDate(end.getDate() + endDays);
      
      const randomTime = start.getTime() + Math.random() * (end.getTime() - start.getTime());
      return new Date(randomTime).toISOString().split('T')[0];
    };
    
    // Generate trades that started in the PAST for demo purposes
    // This allows demonstrating coupon payments immediately
    
    // Trade date: 24-48 months ago (2-4 years in the past) for more coupons
    const tradeDate = getRandomDate(-365 * 4, -365 * 2);
    
    // Effective date: 1-7 days after trade date (still in the past)
    const tradeDateObj = new Date(tradeDate);
    const effectiveDaysOffset = Math.floor(Math.random() * 7) + 1;
    const effectiveDateObj = new Date(tradeDateObj);
    effectiveDateObj.setDate(effectiveDateObj.getDate() + effectiveDaysOffset);
    const effectiveDate = effectiveDateObj.toISOString().split('T')[0];
    
    // Maturity date: 2-5 years from TODAY (in the future)
    const maturityDate = getRandomDate(365 * 2, 365 * 5);
    
    // Accrual start date: same as effective date
    const accrualStartDate = effectiveDate;
    
    // Generate round notional amounts (5M, 10M, 20M, 50M, 100M, 200M, 500M)
    const roundNotionals = [5000000, 10000000, 20000000, 50000000, 100000000, 200000000, 500000000];
    const notionalAmount = getRandomItem(roundNotionals);
    
    const randomData: Partial<CDSTrade> = {
      referenceEntity: getRandomItem(REFERENCE_ENTITIES).code,
      counterparty: getRandomItem(COUNTERPARTIES).code,
      currency: getRandomItem(CURRENCIES).code,
      notionalAmount,
      spread: Math.floor(Math.random() * 500) + 50, // 50 to 550 bps
      buySellProtection: Math.random() > 0.5 ? 'BUY' : 'SELL',
      tradeDate,
      effectiveDate,
      maturityDate,
      accrualStartDate,
      premiumFrequency: getRandomItem(PREMIUM_FREQUENCIES).value,
      dayCountConvention: getRandomItem(DAY_COUNT_CONVENTIONS).value,
      restructuringClause: Math.random() > 0.3 ? getRandomItem(RESTRUCTURING_CLAUSES).value : '', // 70% chance of having a clause
      paymentCalendar: getRandomItem(PAYMENT_CALENDARS).value,
      tradeStatus: 'ACTIVE', // Always generate ACTIVE trades for demo purposes
      recoveryRate: 40,  // Default recovery rate
      settlementType: getRandomItem(SETTLEMENT_METHODS).value
    };
    setFormData(randomData);
    setErrors({}); // Clear any existing errors
  };

  return (
    <div className="max-w-4xl mx-auto bg-fd-darker rounded-lg shadow-fd border border-fd-border p-6">
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-fd-text">New CDS Trade Entry</h2>
      </div>
      
      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Row 1: Reference Entity, Counterparty, Currency */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block text-fd-text font-medium mb-2">
              Reference Entity <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.referenceEntity || ''}
              onChange={(e) => handleInputChange('referenceEntity', e.target.value)}
              className={selectClassName('referenceEntity')}
            >
              <option value="">Select Reference Entity</option>
              {REFERENCE_ENTITIES.map((entity) => (
                <option key={entity.code} value={entity.code}>
                  {entity.code} - {entity.name}
                </option>
              ))}
            </select>
            {errors.referenceEntity && (
              <p className="text-red-500 text-sm mt-1">{errors.referenceEntity}</p>
            )}
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Counterparty <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.counterparty || ''}
              onChange={(e) => handleInputChange('counterparty', e.target.value)}
              className={selectClassName('counterparty')}
            >
              <option value="">Select Counterparty</option>
              {COUNTERPARTIES.map((cp) => (
                <option key={cp.code} value={cp.code}>
                  {cp.name}
                </option>
              ))}
            </select>
            {errors.counterparty && (
              <p className="text-red-500 text-sm mt-1">{errors.counterparty}</p>
            )}
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Currency <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.currency || 'USD'}
              onChange={(e) => handleInputChange('currency', e.target.value)}
              className={selectClassName('currency')}
            >
              {CURRENCIES.map((curr) => (
                <option key={curr.code} value={curr.code}>
                  {curr.code} - {curr.name}
                </option>
              ))}
            </select>
          </div>
        </div>

        {/* Row 1.5: Obligation (conditional on reference entity) */}
        {formData.referenceEntity && (
          <div className="grid grid-cols-1 gap-4">
            <div>
              <label className="block text-fd-text font-medium mb-2">
                Obligation (Bond) <span className="text-red-500">*</span>
              </label>
              <select
                value={formData.obligation?.id || ''}
                onChange={(e) => handleInputChange('obligation', e.target.value ? Number(e.target.value) : undefined)}
                className={selectClassName('obligation')}
                disabled={loadingBonds}
              >
                <option value="">Select Obligation</option>
                {loadingBonds && <option value="">Loading bonds...</option>}
                {!loadingBonds && availableBonds.length === 0 && (
                  <option value="">No bonds available for {formData.referenceEntity}</option>
                )}
                {!loadingBonds && availableBonds.map((bond) => (
                  <option key={bond.id} value={bond.id}>
                    {bond.isin ? `${bond.isin} - ` : ''}
                    {bond.issuer} {bond.seniority} - 
                    Coupon: {bond.couponRate}% - 
                    Maturity: {new Date(bond.maturityDate).toLocaleDateString()}
                  </option>
                ))}
              </select>
              <p className="text-xs text-fd-text-muted mt-1">
                Select a specific bond from {formData.referenceEntity}
              </p>
            </div>
          </div>
        )}

        {/* Row 2: Notional Amount, Spread, Buy/Sell Protection */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block text-fd-text font-medium mb-2">
              Notional Amount <span className="text-red-500">*</span>
            </label>
            <input
              type="text"
              value={formatNumberWithCommas(formData.notionalAmount)}
              onChange={handleNotionalChange}
              className={inputClassName('notionalAmount')}
              placeholder="e.g., 10,000,000"
            />
            {errors.notionalAmount && (
              <p className="text-red-500 text-sm mt-1">{errors.notionalAmount}</p>
            )}
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Spread (bps) <span className="text-red-500">*</span>
            </label>
            <input
              type="number"
              step="0.01"
              min="0"
              value={formData.spread || ''}
              onChange={(e) => handleInputChange('spread', parseFloat(e.target.value))}
              className={inputClassName('spread')}
              placeholder="e.g., 100"
            />
            {errors.spread && (
              <p className="text-red-500 text-sm mt-1">{errors.spread}</p>
            )}
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Recovery Rate (%) <span className="text-red-500">*</span>
            </label>
            <input
              type="number"
              step="1"
              min="0"
              max="100"
              value={formData.recoveryRate || 40}
              onChange={(e) => handleInputChange('recoveryRate', parseFloat(e.target.value))}
              className={inputClassName('recoveryRate')}
              placeholder="e.g., 40"
            />
            {errors.recoveryRate && (
              <p className="text-red-500 text-sm mt-1">{errors.recoveryRate}</p>
            )}
          </div>
        </div>

        {/* Row 3: Buy/Sell Protection, Settlement Type */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block text-fd-text font-medium mb-2">
              Buy/Sell Protection <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.buySellProtection || 'BUY'}
              onChange={(e) => handleInputChange('buySellProtection', e.target.value)}
              className={selectClassName('buySellProtection')}
            >
              <option value="BUY">Buy Protection</option>
              <option value="SELL">Sell Protection</option>
            </select>
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Settlement Type <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.settlementType || 'CASH'}
              onChange={(e) => handleInputChange('settlementType', e.target.value)}
              className={selectClassName('settlementType')}
            >
              {SETTLEMENT_METHODS.map((method) => (
                <option key={method.value} value={method.value}>
                  {method.label}
                </option>
              ))}
            </select>
          </div>
        </div>

        {/* Row 4: Trade Date, Effective Date, Maturity Date */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block text-fd-text font-medium mb-2">
              Trade Date <span className="text-red-500">*</span>
            </label>
            <input
              type="date"
              value={formData.tradeDate || ''}
              onChange={(e) => handleInputChange('tradeDate', e.target.value)}
              className={inputClassName('tradeDate')}
            />
            {errors.tradeDate && (
              <p className="text-red-500 text-sm mt-1">{errors.tradeDate}</p>
            )}
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Effective Date <span className="text-red-500">*</span>
            </label>
            <input
              type="date"
              value={formData.effectiveDate || ''}
              onChange={(e) => handleInputChange('effectiveDate', e.target.value)}
              className={inputClassName('effectiveDate')}
            />
            {errors.effectiveDate && (
              <p className="text-red-500 text-sm mt-1">{errors.effectiveDate}</p>
            )}
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Maturity Date <span className="text-red-500">*</span>
            </label>
            <input
              type="date"
              value={formData.maturityDate || ''}
              onChange={(e) => handleInputChange('maturityDate', e.target.value)}
              className={inputClassName('maturityDate')}
            />
            {errors.maturityDate && (
              <p className="text-red-500 text-sm mt-1">{errors.maturityDate}</p>
            )}
          </div>
        </div>

        {/* Row 4: Premium Frequency, Day Count Convention, Accrual Start Date */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block text-fd-text font-medium mb-2">
              Premium Frequency <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.premiumFrequency || 'QUARTERLY'}
              onChange={(e) => handleInputChange('premiumFrequency', e.target.value)}
              className={selectClassName('premiumFrequency')}
            >
              {PREMIUM_FREQUENCIES.map((freq) => (
                <option key={freq.value} value={freq.value}>
                  {freq.label}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Day Count Convention <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.dayCountConvention || 'ACT_360'}
              onChange={(e) => handleInputChange('dayCountConvention', e.target.value)}
              className={selectClassName('dayCountConvention')}
            >
              {DAY_COUNT_CONVENTIONS.map((dcc) => (
                <option key={dcc.value} value={dcc.value}>
                  {dcc.label}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Accrual Start Date <span className="text-red-500">*</span>
            </label>
            <input
              type="date"
              value={formData.accrualStartDate || ''}
              onChange={(e) => handleInputChange('accrualStartDate', e.target.value)}
              className={inputClassName('accrualStartDate')}
            />
            {errors.accrualStartDate && (
              <p className="text-red-500 text-sm mt-1">{errors.accrualStartDate}</p>
            )}
          </div>
        </div>

        {/* Row 5: Payment Calendar, Restructuring Clause, Trade Status */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block text-fd-text font-medium mb-2">
              Payment Calendar <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.paymentCalendar || 'NYC'}
              onChange={(e) => handleInputChange('paymentCalendar', e.target.value)}
              className={selectClassName('paymentCalendar')}
            >
              {PAYMENT_CALENDARS.map((cal) => (
                <option key={cal.value} value={cal.value}>
                  {cal.label}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Restructuring Clause
            </label>
            <select
              value={formData.restructuringClause || ''}
              onChange={(e) => handleInputChange('restructuringClause', e.target.value)}
              className={selectClassName('restructuringClause')}
            >
              <option value="">None</option>
              {RESTRUCTURING_CLAUSES.map((clause) => (
                <option key={clause.value} value={clause.value}>
                  {clause.label}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Trade Status <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.tradeStatus || 'PENDING'}
              onChange={(e) => handleInputChange('tradeStatus', e.target.value)}
              className={selectClassName('tradeStatus')}
            >
              {TRADE_STATUSES.map((status) => (
                <option key={status.value} value={status.value}>
                  {status.label}
                </option>
              ))}
            </select>
          </div>
        </div>

        {/* Submit Button */}
        <div className="flex justify-end space-x-4 pt-6 border-t border-fd-border">
          <button
            type="button"
            onClick={generateRandomData}
            className="px-6 py-2 bg-transparent border border-fd-border text-fd-text rounded hover:bg-fd-dark transition-colors"
          >
            Fill Random Data
          </button>
          <button
            type="button"
            onClick={() => {
              setFormData({
                tradeDate: new Date().toISOString().split('T')[0],
                currency: 'USD',
                premiumFrequency: 'QUARTERLY',
                dayCountConvention: 'ACT_360',
                buySellProtection: 'BUY',
                paymentCalendar: 'NYC',
                tradeStatus: 'PENDING',
                recoveryRate: 40
              });
              setErrors({});
            }}
            className="px-6 py-2 bg-transparent border border-fd-border text-fd-text rounded hover:bg-fd-dark transition-colors"
          >
            Clear Form
          </button>
          <button
            type="submit"
            disabled={isSubmitting}
            className="px-6 py-2 bg-fd-green text-fd-dark font-medium rounded hover:bg-fd-green-hover disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
          >
            {isSubmitting ? 'Booking Trade...' : 'Book Trade'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default CDSTradeForm;

import React, { useState } from 'react';
import { REFERENCE_ENTITIES } from '../../data/referenceData';

export type CreditEventType =
  | 'BANKRUPTCY'
  | 'FAILURE_TO_PAY'
  | 'RESTRUCTURING'
  | 'OBLIGATION_DEFAULT'
  | 'REPUDIATION_MORATORIUM'
  | 'PAYOUT';
export type SettlementMethod = 'CASH' | 'PHYSICAL';

export interface CreateCreditEventRequest {
  eventType: CreditEventType;
  eventDate: string;
  noticeDate: string;
  settlementMethod: SettlementMethod;
  comments?: string;
}

interface CreditEventModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSubmit: (request: CreateCreditEventRequest) => Promise<void>;
  tradeId: number;
  referenceEntity?: string;
  isLoading?: boolean;
}

const CreditEventModal: React.FC<CreditEventModalProps> = ({
  isOpen,
  onClose,
  onSubmit,
  tradeId,
  referenceEntity,
  isLoading = false
}) => {
  // Get today's date in YYYY-MM-DD format
  const getTodayDate = () => {
    const today = new Date();
    return today.toISOString().split('T')[0];
  };

  // Helper function to get full name of reference entity
  const getReferenceEntityFullName = (code: string): string => {
    const entity = REFERENCE_ENTITIES.find(e => e.code === code);
    return entity ? entity.name : code;
  };

  const [formData, setFormData] = useState<CreateCreditEventRequest>({
    eventType: 'RESTRUCTURING',
    eventDate: getTodayDate(),
    noticeDate: getTodayDate(),
    settlementMethod: 'PHYSICAL',
    comments: '',
  });

  const [errors, setErrors] = useState<Record<string, string>>({});

  const eventTypeOptions: { value: CreditEventType; label: string }[] = [
    { value: 'RESTRUCTURING', label: 'Restructuring' },
    { value: 'BANKRUPTCY', label: 'Bankruptcy' },
  ];

  const settlementMethodOptions: { value: SettlementMethod; label: string }[] = [
    { value: 'PHYSICAL', label: 'Physical Settlement' },
    { value: 'CASH', label: 'Cash Settlement' },
  ];

  const validateForm = (): boolean => {
    const newErrors: Record<string, string> = {};

    if (!formData.eventType) {
      newErrors.eventType = 'Event type is required';
    }

    if (!formData.eventDate) {
      newErrors.eventDate = 'Event date is required';
    } else if (new Date(formData.eventDate) > new Date()) {
      newErrors.eventDate = 'Event date cannot be in the future';
    }

    if (!formData.noticeDate) {
      newErrors.noticeDate = 'Notice date is required';
    } else if (formData.eventDate && new Date(formData.noticeDate) < new Date(formData.eventDate)) {
      newErrors.noticeDate = 'Notice date must be on or after event date';
    }

    if (!formData.settlementMethod) {
      newErrors.settlementMethod = 'Settlement method is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    try {
      await onSubmit(formData);
      // Reset form on successful submission
      setFormData({
        eventType: 'RESTRUCTURING',
        eventDate: getTodayDate(),
        noticeDate: getTodayDate(),
        settlementMethod: 'PHYSICAL',
        comments: '',
      });
      setErrors({});
      onClose();
    } catch (error) {
      console.error('Error submitting credit event:', error);
    }
  };

  const handleInputChange = (field: keyof CreateCreditEventRequest, value: string) => {
    setFormData((prev) => ({
      ...prev,
      [field]: value,
    }));

    // Clear error when user starts typing
    if (errors[field]) {
      setErrors((prev) => ({
        ...prev,
        [field]: '',
      }));
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-fd-darker rounded-lg shadow-fd border border-fd-border p-6 w-full max-w-md max-h-[90vh] overflow-y-auto">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold text-fd-text">Record Credit Event</h2>
          <button
            onClick={onClose}
            className="text-fd-text-muted hover:text-fd-text transition-colors"
            disabled={isLoading}
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M6 18L18 6M6 6l12 12"
              />
            </svg>
          </button>
        </div>

        {/* Reference Entity Information */}
        {referenceEntity && (
          <div className="mb-4 p-3 bg-fd-dark rounded-lg border border-fd-border">
            <div className="flex items-center gap-2">
              <svg className="w-5 h-5 text-fd-green" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4" />
              </svg>
              <div className="flex-1">
                <p className="text-xs text-fd-text-muted uppercase tracking-wide">Reference Entity</p>
                <p className="text-fd-green font-semibold">{getReferenceEntityFullName(referenceEntity)}</p>
                <p className="text-fd-text-muted text-xs mt-0.5">{referenceEntity}</p>
              </div>
            </div>
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label htmlFor="eventType" className="block text-sm font-medium text-fd-text mb-1">
              Event Type *
            </label>
            <select
              id="eventType"
              value={formData.eventType}
              onChange={(e) => handleInputChange('eventType', e.target.value)}
              className={`w-full px-3 py-2 bg-fd-dark border rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green text-fd-text ${
                errors.eventType ? 'border-red-500' : 'border-fd-border'
              }`}
              disabled={isLoading}
            >
              {eventTypeOptions.map((option) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
            {errors.eventType && <p className="mt-1 text-sm text-red-400">{errors.eventType}</p>}
          </div>

          <div>
            <label htmlFor="eventDate" className="block text-sm font-medium text-fd-text mb-1">
              Event Date *
            </label>
            <input
              type="date"
              id="eventDate"
              value={formData.eventDate}
              onChange={(e) => handleInputChange('eventDate', e.target.value)}
              className={`w-full px-3 py-2 bg-fd-dark border rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green text-fd-text ${
                errors.eventDate ? 'border-red-500' : 'border-fd-border'
              }`}
              disabled={isLoading}
            />
            {errors.eventDate && <p className="mt-1 text-sm text-red-400">{errors.eventDate}</p>}
          </div>

          <div>
            <label htmlFor="noticeDate" className="block text-sm font-medium text-fd-text mb-1">
              Notice Date *
            </label>
            <input
              type="date"
              id="noticeDate"
              value={formData.noticeDate}
              onChange={(e) => handleInputChange('noticeDate', e.target.value)}
              className={`w-full px-3 py-2 bg-fd-dark border rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green text-fd-text ${
                errors.noticeDate ? 'border-red-500' : 'border-fd-border'
              }`}
              disabled={isLoading}
            />
            {errors.noticeDate && <p className="mt-1 text-sm text-red-400">{errors.noticeDate}</p>}
          </div>

          <div>
            <label
              htmlFor="settlementMethod"
              className="block text-sm font-medium text-fd-text mb-1"
            >
              Settlement Method *
            </label>
            <select
              id="settlementMethod"
              value={formData.settlementMethod}
              onChange={(e) => handleInputChange('settlementMethod', e.target.value)}
              className={`w-full px-3 py-2 bg-fd-dark border rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green text-fd-text ${
                errors.settlementMethod ? 'border-red-500' : 'border-fd-border'
              }`}
              disabled={isLoading}
            >
              {settlementMethodOptions.map((option) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
            {errors.settlementMethod && (
              <p className="mt-1 text-sm text-red-400">{errors.settlementMethod}</p>
            )}
          </div>

          <div>
            <label htmlFor="comments" className="block text-sm font-medium text-fd-text mb-1">
              Comments
            </label>
            <textarea
              id="comments"
              value={formData.comments}
              onChange={(e) => handleInputChange('comments', e.target.value)}
              rows={3}
              className="w-full px-3 py-2 bg-fd-dark border border-fd-border rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green text-fd-text placeholder-fd-text-muted"
              placeholder="Optional comments about the credit event..."
              disabled={isLoading}
            />
          </div>

          <div className="flex justify-end space-x-3 pt-4">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 text-sm font-medium text-fd-text bg-fd-dark border border-fd-border rounded-md hover:bg-fd-border focus:outline-none focus:ring-2 focus:ring-fd-green transition-colors"
              disabled={isLoading}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="px-4 py-2 text-sm font-medium text-fd-dark bg-fd-green border border-transparent rounded-md hover:bg-fd-green-hover focus:outline-none focus:ring-2 focus:ring-fd-green disabled:opacity-50 transition-colors"
              disabled={isLoading}
            >
              {isLoading ? 'Recording...' : 'Record Credit Event'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreditEventModal;

import React, { useEffect, useState } from 'react';
import { lifecycleService } from '../../services/lifecycleService';
import { AccrualEvent } from '../../types/lifecycle';

interface Props {
  tradeId: number;
}

export const AccrualHistoryPanel: React.FC<Props> = ({ tradeId }) => {
  const [accruals, setAccruals] = useState<AccrualEvent[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [range, setRange] = useState({ start: '', end: '' });
  const [cumulative, setCumulative] = useState<number | null>(null);

  const loadAccruals = async () => {
    if (!range.start || !range.end) return;
    setLoading(true);
    setError(null);
    try {
      const data = await lifecycleService.getAccrualEvents(tradeId, range.start, range.end);
      setAccruals(data);
    } catch (e: any) {
      setError(e.message || 'Failed to load accrual events');
    } finally {
      setLoading(false);
    }
  };

  const loadCumulative = async () => {
    try {
      const cum = await lifecycleService.getCumulativeAccrual(tradeId);
      setCumulative(cum);
    } catch {}
  };

  useEffect(() => {
    loadCumulative();
  }, [tradeId, loadCumulative]);

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <h4 className="text-fd-text font-semibold">Accrual History</h4>
        <div className="flex items-center space-x-2">
          <input
            type="date"
            value={range.start}
            onChange={(e) => setRange((r) => ({ ...r, start: e.target.value }))}
            className="bg-fd-dark border border-fd-border rounded px-2 py-1 text-xs text-fd-text"
          />
          <span className="text-fd-text-muted text-xs">to</span>
          <input
            type="date"
            value={range.end}
            onChange={(e) => setRange((r) => ({ ...r, end: e.target.value }))}
            className="bg-fd-dark border border-fd-border rounded px-2 py-1 text-xs text-fd-text"
          />
          <button
            onClick={loadAccruals}
            disabled={!range.start || !range.end}
            className="px-3 py-1 text-xs bg-fd-green text-fd-dark rounded font-medium disabled:opacity-40"
          >
            Load
          </button>
        </div>
      </div>
      {cumulative !== null && (
        <div className="bg-fd-dark border border-fd-border rounded p-3 flex items-center justify-between">
          <span className="text-fd-text-muted text-xs uppercase tracking-wide">
            Cumulative Accrual
          </span>
          <span className="text-fd-green font-semibold">
            {cumulative.toLocaleString(undefined, {
              minimumFractionDigits: 2,
              maximumFractionDigits: 2,
            })}
          </span>
        </div>
      )}
      {loading && <div className="text-fd-text-muted text-sm">Loading accruals...</div>}
      {error && <div className="text-red-400 text-sm">{error}</div>}
      {!loading && accruals.length === 0 && !error && range.start && range.end && (
        <div className="text-fd-text-muted text-sm">No accruals in range.</div>
      )}
      {accruals.length > 0 && (
        <div className="overflow-x-auto border border-fd-border rounded max-h-72 overflow-y-auto">
          <table className="min-w-full text-xs">
            <thead className="bg-fd-dark sticky top-0">
              <tr className="text-fd-text-muted">
                <th className="px-3 py-2 text-left font-medium">Date</th>
                <th className="px-3 py-2 text-left font-medium">Accrual</th>
                <th className="px-3 py-2 text-left font-medium">Cumulative</th>
                <th className="px-3 py-2 text-left font-medium">Notional</th>
                <th className="px-3 py-2 text-left font-medium">Version</th>
              </tr>
            </thead>
            <tbody>
              {accruals.map((a) => (
                <tr key={a.id} className="border-t border-fd-border hover:bg-fd-darker/40">
                  <td className="px-3 py-1 text-fd-text font-mono">{a.accrualDate}</td>
                  <td className="px-3 py-1 text-fd-green font-medium">
                    {a.accrualAmount.toLocaleString(undefined, { minimumFractionDigits: 2 })}
                  </td>
                  <td className="px-3 py-1 text-fd-text-muted">
                    {a.cumulativeAccrual.toLocaleString(undefined, { minimumFractionDigits: 2 })}
                  </td>
                  <td className="px-3 py-1 text-fd-text-muted">
                    {a.notionalAmount.toLocaleString()}
                  </td>
                  <td className="px-3 py-1 text-fd-text-muted">{a.tradeVersion}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

import React, { useState, useEffect, useCallback } from 'react';
import {
  BondPortfolioConstituent,
  BasketPortfolioConstituent,
  CdsPortfolioConstituent,
} from '../../services/portfolioService';

interface EnhancedOverviewProps {
  portfolioId: number;
  cdsConstituents: CdsPortfolioConstituent[];
  bondConstituents: BondPortfolioConstituent[];
  basketConstituents: BasketPortfolioConstituent[];
  pricingData: any;
}

interface IssuerExposure {
  issuer: string;
  sector: string;
  bondNotional: number;
  cdsProtectionBought: number;
  cdsProtectionSold: number;
  netCreditExposure: number;
  hedgeRatio: number;
  hedgeStatus: 'OVER_HEDGED' | 'UNDER_HEDGED' | 'BALANCED' | 'UNHEDGED';
  recommendation: string;
}

interface SectorExposure {
  sector: string;
  notional: number;
  percentage: number;
  instrumentCount: number;
}

const EnhancedOverview: React.FC<EnhancedOverviewProps> = ({
  portfolioId,
  cdsConstituents,
  bondConstituents,
  basketConstituents,
  pricingData,
}) => {
  const [issuerExposures, setIssuerExposures] = useState<IssuerExposure[]>([]);
  const [sectorExposures, setSectorExposures] = useState<SectorExposure[]>([]);

  useEffect(() => {
    calculateExposures();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [cdsConstituents, bondConstituents, basketConstituents]);

  const calculateExposures = useCallback(() => {
    // Map to aggregate by issuer
    const issuerMap = new Map<string, IssuerExposure>();

    // Process bonds
    bondConstituents.forEach((bc) => {
      const issuer = bc.bond.issuer;
      if (!issuerMap.has(issuer)) {
        issuerMap.set(issuer, {
          issuer,
          sector: getSectorFromIssuer(issuer),
          bondNotional: 0,
          cdsProtectionBought: 0,
          cdsProtectionSold: 0,
          netCreditExposure: 0,
          hedgeRatio: 0,
          hedgeStatus: 'UNHEDGED',
          recommendation: '',
        });
      }
      const exposure = issuerMap.get(issuer)!;
      exposure.bondNotional += bc.bond.notional;
    });

    // Process CDS
    cdsConstituents.forEach((cc) => {
      const issuer = cc.trade.referenceEntity;
      if (!issuerMap.has(issuer)) {
        issuerMap.set(issuer, {
          issuer,
          sector: getSectorFromIssuer(issuer),
          bondNotional: 0,
          cdsProtectionBought: 0,
          cdsProtectionSold: 0,
          netCreditExposure: 0,
          hedgeRatio: 0,
          hedgeStatus: 'UNHEDGED',
          recommendation: '',
        });
      }
      const exposure = issuerMap.get(issuer)!;

      // Assuming BUY = buying protection (short credit), SELL = selling protection (long credit)
      // This is a simplification - you may need to check actual buySell field
      exposure.cdsProtectionBought += cc.trade.notionalAmount;
    });

    // Calculate net exposures and recommendations
    const exposures: IssuerExposure[] = [];
    issuerMap.forEach((exp) => {
      exp.netCreditExposure = exp.bondNotional - exp.cdsProtectionBought + exp.cdsProtectionSold;

      if (exp.bondNotional > 0) {
        exp.hedgeRatio = (exp.cdsProtectionBought / exp.bondNotional) * 100;

        if (exp.hedgeRatio > 110) {
          exp.hedgeStatus = 'OVER_HEDGED';
          exp.recommendation = `Consider reducing CDS protection by $${((exp.cdsProtectionBought - exp.bondNotional) / 1000000).toFixed(1)}M`;
        } else if (exp.hedgeRatio >= 90 && exp.hedgeRatio <= 110) {
          exp.hedgeStatus = 'BALANCED';
          exp.recommendation = 'Well hedged';
        } else if (exp.hedgeRatio > 0) {
          exp.hedgeStatus = 'UNDER_HEDGED';
          exp.recommendation = `Consider buying $${((exp.bondNotional - exp.cdsProtectionBought) / 1000000).toFixed(1)}M CDS protection`;
        } else {
          exp.hedgeStatus = 'UNHEDGED';
          exp.recommendation = ` Buy $${(exp.bondNotional / 1000000).toFixed(1)}M CDS protection to hedge bond exposure`;
        }
      } else if (exp.cdsProtectionBought > 0) {
        exp.hedgeStatus = 'UNHEDGED';
        exp.recommendation = `Consider buying $${(exp.cdsProtectionBought / 1000000).toFixed(1)}M in bonds to capture basis`;
      }

      exposures.push(exp);
    });

    setIssuerExposures(
      exposures.sort((a, b) => Math.abs(b.netCreditExposure) - Math.abs(a.netCreditExposure))
    );

    // Calculate sector exposures
    const sectorMap = new Map<string, { notional: number; count: number }>();

    bondConstituents.forEach((bc) => {
      const sector = getSectorFromIssuer(bc.bond.issuer);
      if (!sectorMap.has(sector)) {
        sectorMap.set(sector, { notional: 0, count: 0 });
      }
      const sectorData = sectorMap.get(sector)!;
      sectorData.notional += bc.bond.notional;
      sectorData.count++;
    });

    cdsConstituents.forEach((cc) => {
      const sector = getSectorFromIssuer(cc.trade.referenceEntity);
      if (!sectorMap.has(sector)) {
        sectorMap.set(sector, { notional: 0, count: 0 });
      }
      const sectorData = sectorMap.get(sector)!;
      sectorData.notional += cc.trade.notionalAmount;
      sectorData.count++;
    });

    // Add baskets as "BASKET" sector for now
    // TODO: In future, unwind basket constituents to their actual sectors
    basketConstituents.forEach((bc) => {
      const sector = 'BASKET';
      if (!sectorMap.has(sector)) {
        sectorMap.set(sector, { notional: 0, count: 0 });
      }
      const sectorData = sectorMap.get(sector)!;
      // Use basket.notional (backend field name)
      const notional = bc.basket?.notional || bc.weightValue || 0;
      sectorData.notional += notional;
      sectorData.count++;
    });

    const totalNotional = Array.from(sectorMap.values()).reduce((sum, s) => sum + s.notional, 0);
    const sectors: SectorExposure[] = [];

    sectorMap.forEach((data, sector) => {
      sectors.push({
        sector,
        notional: data.notional,
        percentage: totalNotional > 0 ? (data.notional / totalNotional) * 100 : 0,
        instrumentCount: data.count,
      });
    });

    setSectorExposures(sectors.sort((a, b) => b.notional - a.notional));
  }, [basketConstituents, bondConstituents, cdsConstituents]); // useCallback dependency array

  const getSectorFromIssuer = (issuer: string): string => {
    const sectorMap: Record<string, string> = {
      AAPL: 'TECH',
      MSFT: 'TECH',
      AMZN: 'TECH',
      GOOGL: 'TECH',
      TSLA: 'TECH',
      NFLX: 'TECH',
      META: 'TECH',
      NVDA: 'TECH',
      AMD: 'TECH',
      JPM: 'FINANCIALS',
      BAC: 'FINANCIALS',
      WFC: 'FINANCIALS',
      GS: 'FINANCIALS',
      MS: 'FINANCIALS',
      C: 'FINANCIALS',
    };
    return sectorMap[issuer] || 'OTHER';
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const getHedgeStatusColor = (status: string) => {
    switch (status) {
      case 'BALANCED':
        return 'text-fd-green';
      case 'UNDER_HEDGED':
        return 'text-yellow-400';
      case 'OVER_HEDGED':
        return 'text-blue-400';
      case 'UNHEDGED':
        return 'text-red-400';
      default:
        return 'text-fd-text-muted';
    }
  };

  const getHedgeStatusBadge = (status: string) => {
    switch (status) {
      case 'BALANCED':
        return 'bg-green-900/30 text-green-400 border-green-500';
      case 'UNDER_HEDGED':
        return 'bg-yellow-900/30 text-yellow-400 border-yellow-500';
      case 'OVER_HEDGED':
        return 'bg-blue-900/30 text-blue-400 border-blue-500';
      case 'UNHEDGED':
        return 'bg-red-900/30 text-red-400 border-red-500';
      default:
        return 'bg-fd-dark text-fd-text-muted border-fd-border';
    }
  };

  const getSectorColor = (index: number) => {
    const colors = [
      'bg-fd-green',
      'bg-blue-500',
      'bg-yellow-500',
      'bg-purple-500',
      'bg-pink-500',
      'bg-indigo-500',
      'bg-teal-500',
      'bg-orange-500',
    ];
    return colors[index % colors.length];
  };

  const totalCdsNotional = cdsConstituents.reduce((sum, c) => sum + c.trade.notionalAmount, 0);
  const totalBondNotional = bondConstituents.reduce((sum, c) => sum + c.bond.notional, 0);
  const totalBasketNotional = basketConstituents.reduce((sum, c) => {
    // Use basket.notional (backend field name)
    const notional = c.basket?.notional || c.weightValue || 0;
    return sum + notional;
  }, 0);
  const totalNotional = totalCdsNotional + totalBondNotional + totalBasketNotional;

  return (
    <div className="space-y-6">
      {/* Instrument Distribution */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Instrument Type Breakdown */}
        <div className="bg-fd-darker border border-fd-border rounded-lg p-6">
          <h3 className="text-lg font-semibold text-fd-text mb-4">Instrument Distribution</h3>
          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <div className="w-4 h-4 bg-fd-green rounded"></div>
                <span className="text-fd-text"> CDS Trades</span>
              </div>
              <div className="text-right">
                <div className="text-fd-text font-medium">{formatCurrency(totalCdsNotional)}</div>
                <div className="text-sm text-fd-text-muted">
                  {totalNotional > 0 ? ((totalCdsNotional / totalNotional) * 100).toFixed(1) : 0}% {' '}
                  {cdsConstituents.length} positions
                </div>
              </div>
            </div>
            <div className="w-full bg-fd-dark rounded-full h-2">
              <div
                className="bg-fd-green h-2 rounded-full transition-all"
                style={{
                  width: `${totalNotional > 0 ? (totalCdsNotional / totalNotional) * 100 : 0}%`,
                }}
              ></div>
            </div>

            <div className="flex items-center justify-between mt-4">
              <div className="flex items-center space-x-3">
                <div className="w-4 h-4 bg-blue-500 rounded"></div>
                <span className="text-fd-text"> Corporate Bonds</span>
              </div>
              <div className="text-right">
                <div className="text-fd-text font-medium">{formatCurrency(totalBondNotional)}</div>
                <div className="text-sm text-fd-text-muted">
                  {totalNotional > 0 ? ((totalBondNotional / totalNotional) * 100).toFixed(1) : 0}%
                   {bondConstituents.length} positions
                </div>
              </div>
            </div>
            <div className="w-full bg-fd-dark rounded-full h-2">
              <div
                className="bg-blue-500 h-2 rounded-full transition-all"
                style={{
                  width: `${totalNotional > 0 ? (totalBondNotional / totalNotional) * 100 : 0}%`,
                }}
              ></div>
            </div>

            <div className="flex items-center justify-between mt-4">
              <div className="flex items-center space-x-3">
                <div className="w-4 h-4 bg-purple-500 rounded"></div>
                <span className="text-fd-text"> Credit Baskets</span>
              </div>
              <div className="text-right">
                <div className="text-fd-text font-medium">
                  {formatCurrency(totalBasketNotional)}
                </div>
                <div className="text-sm text-fd-text-muted">
                  {totalNotional > 0 ? ((totalBasketNotional / totalNotional) * 100).toFixed(1) : 0}
                  %  {basketConstituents.length} positions
                </div>
              </div>
            </div>
            <div className="w-full bg-fd-dark rounded-full h-2">
              <div
                className="bg-purple-500 h-2 rounded-full transition-all"
                style={{
                  width: `${totalNotional > 0 ? (totalBasketNotional / totalNotional) * 100 : 0}%`,
                }}
              ></div>
            </div>
          </div>
        </div>

        {/* Sector Exposure */}
        <div className="bg-fd-darker border border-fd-border rounded-lg p-6">
          <h3 className="text-lg font-semibold text-fd-text mb-4">Sector Allocation</h3>
          <div className="space-y-3">
            {sectorExposures.slice(0, 5).map((sector, idx) => (
              <div key={sector.sector} className="space-y-1">
                <div className="flex items-center justify-between text-sm">
                  <div className="flex items-center space-x-2">
                    <div className={`w-3 h-3 rounded ${getSectorColor(idx)}`}></div>
                    <span className="text-fd-text">{sector.sector}</span>
                  </div>
                  <div className="text-fd-text-muted">
                    {sector.percentage.toFixed(1)}%  {sector.instrumentCount} instruments
                  </div>
                </div>
                <div className="w-full bg-fd-dark rounded-full h-1.5">
                  <div
                    className={`${getSectorColor(idx)} h-1.5 rounded-full transition-all`}
                    style={{ width: `${sector.percentage}%` }}
                  ></div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Net Exposure by Issuer */}
      <div className="bg-fd-darker border border-fd-border rounded-lg p-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-fd-text">Net Credit Exposure by Issuer</h3>
          <div className="text-sm text-fd-text-muted">Showing bond positions vs CDS hedges</div>
        </div>

        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-fd-border">
            <thead>
              <tr className="text-left text-xs font-medium text-fd-text-muted uppercase tracking-wider">
                <th className="px-4 py-3">Issuer</th>
                <th className="px-4 py-3">Sector</th>
                <th className="px-4 py-3 text-right">Bond Exposure</th>
                <th className="px-4 py-3 text-right">CDS Protection</th>
                <th className="px-4 py-3 text-right">Net Exposure</th>
                <th className="px-4 py-3 text-center">Hedge Ratio</th>
                <th className="px-4 py-3 text-center">Status</th>
                <th className="px-4 py-3">Recommendation</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-fd-border">
              {issuerExposures.map((exp) => (
                <tr key={exp.issuer} className="hover:bg-fd-dark transition-colors">
                  <td className="px-4 py-3 text-sm font-medium text-fd-text">{exp.issuer}</td>
                  <td className="px-4 py-3 text-sm text-fd-text-muted">{exp.sector}</td>
                  <td className="px-4 py-3 text-sm text-right text-fd-text">
                    {exp.bondNotional > 0 ? formatCurrency(exp.bondNotional) : '-'}
                  </td>
                  <td className="px-4 py-3 text-sm text-right text-fd-text">
                    {exp.cdsProtectionBought > 0 ? formatCurrency(exp.cdsProtectionBought) : '-'}
                  </td>
                  <td
                    className={`px-4 py-3 text-sm text-right font-medium ${
                      exp.netCreditExposure > 0
                        ? 'text-red-400'
                        : exp.netCreditExposure < 0
                          ? 'text-fd-green'
                          : 'text-fd-text'
                    }`}
                  >
                    {formatCurrency(Math.abs(exp.netCreditExposure))}
                    {exp.netCreditExposure > 0
                      ? ' Long'
                      : exp.netCreditExposure < 0
                        ? ' Short'
                        : ''}
                  </td>
                  <td className="px-4 py-3 text-sm text-center text-fd-text">
                    {exp.bondNotional > 0 ? `${exp.hedgeRatio.toFixed(0)}%` : 'N/A'}
                  </td>
                  <td className="px-4 py-3 text-center">
                    <span
                      className={`inline-flex items-center px-2 py-1 rounded text-xs font-medium border ${getHedgeStatusBadge(exp.hedgeStatus)}`}
                    >
                      {exp.hedgeStatus.replace('_', ' ')}
                    </span>
                  </td>
                  <td className={`px-4 py-3 text-sm ${getHedgeStatusColor(exp.hedgeStatus)}`}>
                    {exp.recommendation}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {issuerExposures.length === 0 && (
          <div className="text-center py-8 text-fd-text-muted">
            No exposures to analyze. Add some CDS trades and bonds to see hedge recommendations.
          </div>
        )}
      </div>

      {/* Key Metrics Summary */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <div className="bg-fd-darker border border-fd-border rounded-lg p-4">
          <div className="text-sm text-fd-text-muted mb-1">Total Instruments</div>
          <div className="text-2xl font-semibold text-fd-text">
            {cdsConstituents.length + bondConstituents.length + basketConstituents.length}
          </div>
          <div className="text-xs text-fd-text-muted mt-1">
            {cdsConstituents.length} CDS  {bondConstituents.length} Bonds {' '}
            {basketConstituents.length} Baskets
          </div>
        </div>

        <div className="bg-fd-darker border border-fd-border rounded-lg p-4">
          <div className="text-sm text-fd-text-muted mb-1">Unique Issuers</div>
          <div className="text-2xl font-semibold text-fd-text">{issuerExposures.length}</div>
          <div className="text-xs text-fd-text-muted mt-1">{sectorExposures.length} sectors</div>
        </div>

        <div className="bg-fd-darker border border-fd-border rounded-lg p-4">
          <div className="text-sm text-fd-text-muted mb-1">Hedge Coverage</div>
          <div className="text-2xl font-semibold text-fd-text">
            {totalBondNotional > 0 ? ((totalCdsNotional / totalBondNotional) * 100).toFixed(0) : 0}%
          </div>
          <div className="text-xs text-fd-text-muted mt-1">CDS / Bond ratio</div>
        </div>

        <div className="bg-fd-darker border border-fd-border rounded-lg p-4">
          <div className="text-sm text-fd-text-muted mb-1">Unhedged Exposure</div>
          <div className="text-2xl font-semibold text-red-400">
            {
              issuerExposures.filter(
                (e) => e.hedgeStatus === 'UNHEDGED' || e.hedgeStatus === 'UNDER_HEDGED'
              ).length
            }
          </div>
          <div className="text-xs text-fd-text-muted mt-1">issuers need attention</div>
        </div>
      </div>
    </div>
  );
};

export default EnhancedOverview;

import React, { useState } from 'react';
import { Contributor } from '../../../services/simulationService';

interface ContributorsTableProps {
  contributors: Contributor[];
}

type SortField = 'entity' | 'marginalElPct' | 'beta';
type SortDirection = 'asc' | 'desc';

const ContributorsTable: React.FC<ContributorsTableProps> = ({ contributors }) => {
  const [sortField, setSortField] = useState<SortField>('marginalElPct');
  const [sortDirection, setSortDirection] = useState<SortDirection>('desc');

  const handleSort = (field: SortField) => {
    if (sortField === field) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortField(field);
      setSortDirection(field === 'entity' ? 'asc' : 'desc');
    }
  };

  const sortedContributors = [...contributors].sort((a, b) => {
    let aVal = a[sortField];
    let bVal = b[sortField];

    if (typeof aVal === 'string') {
      aVal = aVal.toLowerCase();
      bVal = (bVal as string).toLowerCase();
    }

    if (aVal < bVal) return sortDirection === 'asc' ? -1 : 1;
    if (aVal > bVal) return sortDirection === 'asc' ? 1 : -1;
    return 0;
  });

  const SortIcon = ({ field }: { field: SortField }) => {
    if (sortField !== field) {
      return (
        <svg
          className="w-4 h-4 text-fd-text-muted"
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M7 16V4m0 0L3 8m4-4l4 4m6 0v12m0 0l4-4m-4 4l-4-4"
          />
        </svg>
      );
    }

    return sortDirection === 'asc' ? (
      <svg className="w-4 h-4 text-fd-green" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 15l7-7 7 7" />
      </svg>
    ) : (
      <svg className="w-4 h-4 text-fd-green" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
      </svg>
    );
  };

  return (
    <div className="bg-fd-darker rounded-lg border border-fd-border overflow-hidden">
      <div className="overflow-x-auto">
        <table className="w-full">
          <thead className="bg-fd-dark border-b border-fd-border">
            <tr>
              <th
                className="px-4 py-3 text-left text-sm font-medium text-fd-text cursor-pointer hover:bg-fd-border transition-colors"
                onClick={() => handleSort('entity')}
              >
                <div className="flex items-center gap-2">
                  Entity Name
                  <SortIcon field="entity" />
                </div>
              </th>
              <th
                className="px-4 py-3 text-right text-sm font-medium text-fd-text cursor-pointer hover:bg-fd-border transition-colors"
                onClick={() => handleSort('marginalElPct')}
              >
                <div className="flex items-center justify-end gap-2">
                  Marginal EL %
                  <SortIcon field="marginalElPct" />
                </div>
              </th>
              <th
                className="px-4 py-3 text-right text-sm font-medium text-fd-text cursor-pointer hover:bg-fd-border transition-colors"
                onClick={() => handleSort('beta')}
              >
                <div className="flex items-center justify-end gap-2">
                  Beta ()
                  <SortIcon field="beta" />
                </div>
              </th>
            </tr>
          </thead>
          <tbody className="divide-y divide-fd-border">
            {sortedContributors.map((contributor, index) => (
              <tr key={index} className="hover:bg-fd-dark/50 transition-colors">
                <td className="px-4 py-3 text-sm text-fd-text">{contributor.entity}</td>
                <td className="px-4 py-3 text-sm text-fd-text text-right font-medium">
                  {contributor.marginalElPct.toFixed(2)}%
                </td>
                <td className="px-4 py-3 text-sm text-fd-text-muted text-right">
                  {contributor.beta.toFixed(3)}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {sortedContributors.length === 0 && (
        <div className="p-8 text-center text-fd-text-muted">No contributors data available</div>
      )}
    </div>
  );
};

export default ContributorsTable;

import React from 'react';

interface Props {
  status: 'PASS' | 'FAIL' | 'UNKNOWN';
  lastRun?: string;
}

const colorMap: Record<string, string> = {
  PASS: 'bg-green-600 text-white',
  FAIL: 'bg-red-600 text-white',
  UNKNOWN: 'bg-gray-600 text-white',
};

const RegressionStatusBadge: React.FC<Props> = ({ status, lastRun }) => {
  return (
    <span
      className={`inline-flex items-center px-2 py-0.5 rounded text-xs font-semibold ${colorMap[status]}`}
      title={
        lastRun ? `Last regression: ${new Date(lastRun).toLocaleString()}` : 'No regression run yet'
      }
    >
      Regression: {status}
    </span>
  );
};

export default RegressionStatusBadge;

import React, { useEffect, useState } from 'react';
import { fetchRiskMeasures } from '../../services/risk/riskService';
import { RiskMeasures } from '../../services/risk/riskTypes';
import { lifecycleService } from '../../services/lifecycleService';
import { CouponPeriod } from '../../types/lifecycle';
import { CDSTradeResponse } from '../../services/cdsTradeService';
import { creditEventService } from '../../services/creditEventService';

interface Props {
  tradeId: number;
  trade?: CDSTradeResponse; // Optional trade object for richer display
}

const RiskMeasuresPanel: React.FC<Props> = ({ tradeId, trade }) => {
  const [data, setData] = useState<RiskMeasures | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [couponPeriods, setCouponPeriods] = useState<CouponPeriod[]>([]);
  const [payingPeriodId, setPayingPeriodId] = useState<number | null>(null);
  const [recalculating, setRecalculating] = useState(false);
  const [generatingSchedule, setGeneratingSchedule] = useState(false);
  const [valuationDate, setValuationDate] = useState<string | undefined>(undefined); // undefined means "today"
  const [hasPayoutEvent, setHasPayoutEvent] = useState(false);

  // Check if trade is in a final state (settled or terminated) where no modifications are allowed
  const isTradeFinalized = trade && (
    trade.tradeStatus === 'SETTLED_CASH' || 
    trade.tradeStatus === 'SETTLED_PHYSICAL' ||
    trade.tradeStatus === 'TERMINATED'
  );

  const checkForPayoutEvent = async () => {
    try {
      const events = await creditEventService.getCreditEventsForTrade(tradeId);
      const payoutExists = events.some((event) => event.eventType === 'PAYOUT');
      setHasPayoutEvent(payoutExists);
    } catch (error) {
      console.error('Failed to check for payout events:', error);
      setHasPayoutEvent(false);
    }
  };

  const loadRiskMeasures = async (customValuationDate?: string, forceRefresh: boolean = false) => {
    if (!tradeId) return;
    setLoading(true);
    try {
      // When forceRefresh is true, use customValuationDate directly (even if undefined)
      // Otherwise fall back to state's valuationDate
      const dateToUse = forceRefresh ? customValuationDate : customValuationDate || valuationDate;
      console.log(` DEBUG loadRiskMeasures called with:`, {
        customValuationDate,
        valuationDate,
        forceRefresh,
        dateToUse,
        tradeId,
        timestamp: new Date().toISOString(),
      });
      console.log(
        `Loading risk measures for trade ${tradeId} with valuation date: ${dateToUse || 'today'}...`
      );
      const measures = await fetchRiskMeasures(tradeId, dateToUse);
      console.log('Risk measures loaded:', {
        npv: measures.npv,
        currency: measures.currency,
        timestamp: measures.valuationTimestamp,
        loadedAt: new Date().toISOString(),
      });
      setData(measures);
      setError(null);
    } catch (e: any) {
      console.error('Failed to load risk measures:', e);
      setError(e.message);
    } finally {
      setLoading(false);
    }
  };

  const loadCouponPeriods = async () => {
    if (!tradeId) return;
    try {
      console.log('Loading coupon periods for trade:', tradeId);
      const periods = await lifecycleService.getCouponSchedule(tradeId);
      console.log('Loaded coupon periods:', periods.length, periods);
      setCouponPeriods(periods);
    } catch (e: any) {
      console.error('Failed to load coupon periods:', e);
      setCouponPeriods([]); // Ensure it's set to empty array on error
    }
  };

  const handlePayCoupon = async (periodId: number, payOnTime: boolean = false) => {
    setPayingPeriodId(periodId);
    try {
      await lifecycleService.payCoupon(tradeId, periodId, payOnTime);
      console.log('Coupon paid successfully, reloading data...');

      // Reload coupon periods to reflect payment
      await loadCouponPeriods();

      // Trigger risk recalculation - CRITICAL: Clear old data first to force re-render
      setRecalculating(true);
      setData(null); // Force clear to ensure React detects the change

      // Wait a bit to ensure backend has processed the payment
      await new Promise((resolve) => setTimeout(resolve, 500));

      await loadRiskMeasures();
      console.log('Risk measures reloaded after coupon payment');
      setRecalculating(false);
    } catch (e: any) {
      alert('Failed to pay coupon: ' + e.message);
      setRecalculating(false);
    } finally {
      setPayingPeriodId(null);
    }
  };

  const handleUnpayCoupon = async (periodId: number) => {
    setPayingPeriodId(periodId);
    try {
      await lifecycleService.unpayCoupon(tradeId, periodId);
      console.log('Coupon payment cancelled successfully, reloading data...');

      // Reload coupon periods to reflect cancellation
      await loadCouponPeriods();

      // Trigger risk recalculation
      setRecalculating(true);
      setData(null);

      await new Promise((resolve) => setTimeout(resolve, 500));

      await loadRiskMeasures();
      console.log('Risk measures reloaded after coupon cancellation');
      setRecalculating(false);
    } catch (e: any) {
      alert('Failed to cancel coupon payment: ' + e.message);
      setRecalculating(false);
    } finally {
      setPayingPeriodId(null);
    }
  };

  const handleGenerateSchedule = async () => {
    setGeneratingSchedule(true);
    try {
      await lifecycleService.generateCouponSchedule(tradeId);
      // Reload the schedule
      await loadCouponPeriods();
    } catch (e: any) {
      alert('Failed to generate coupon schedule: ' + e.message);
    } finally {
      setGeneratingSchedule(false);
    }
  };

  // Helper function to calculate business days offset (simplified - assumes no holidays)
  const getBusinessDaysFromToday = (days: number): string => {
    const today = new Date();
    const target = new Date(today);

    let addedDays = 0;
    let daysToAdd = days;

    while (daysToAdd > 0) {
      target.setDate(target.getDate() + 1);
      const dayOfWeek = target.getDay();
      // Skip weekends (0 = Sunday, 6 = Saturday)
      if (dayOfWeek !== 0 && dayOfWeek !== 6) {
        addedDays++;
        daysToAdd--;
      }
    }

    return target.toISOString().split('T')[0];
  };

  const handleQuickValuationDate = async (option: 'today' | 't+1' | 't+7' | 't+45') => {
    let newDate: string | undefined = undefined;

    switch (option) {
      case 'today':
        newDate = undefined; // undefined means use backend's "today"
        break;
      case 't+1':
        newDate = getBusinessDaysFromToday(1);
        break;
      case 't+7':
        newDate = getBusinessDaysFromToday(7);
        break;
      case 't+45':
        newDate = getBusinessDaysFromToday(45);
        break;
    }

    console.log(` DEBUG handleQuickValuationDate:`, {
      option,
      newDate,
      currentValuationDate: valuationDate,
    });

    // Update state first
    setValuationDate(newDate);

    // Force a fresh fetch by clearing data first
    setData(null);
    setLoading(true);
    setError(null);

    // Small delay to ensure state updates propagate
    await new Promise((resolve) => setTimeout(resolve, 10));

    // Now fetch with the new date, passing forceRefresh=true to use newDate directly
    await loadRiskMeasures(newDate, true);
  };

  useEffect(() => {
    loadRiskMeasures();
    loadCouponPeriods();
    checkForPayoutEvent();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [tradeId]);

  if (!tradeId) return <div className="text-fd-text">No trade selected</div>;

  // Show payout message if PAYOUT event exists
  if (hasPayoutEvent) {
    return (
      <div className="space-y-6">
        <div className="bg-fd-darker p-6 rounded-md border-2 border-fd-green">
          <div className="flex items-center gap-3 mb-4">
            <div className="w-12 h-12 bg-fd-green/20 rounded-full flex items-center justify-center">
              <svg
                className="w-6 h-6 text-fd-green"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
                ></path>
              </svg>
            </div>
            <div>
              <h3 className="text-fd-green font-semibold text-lg">CDS Protection Paid Out</h3>
              <p className="text-fd-text-muted text-sm">
                A credit event has triggered payout - all risk measures are now zero
              </p>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mt-6">
            <div className="bg-fd-dark rounded p-4">
              <div className="text-xs text-fd-text-muted mb-1">Net Present Value</div>
              <div className="text-2xl font-bold text-fd-green font-mono">$0.00</div>
            </div>
            <div className="bg-fd-dark rounded p-4">
              <div className="text-xs text-fd-text-muted mb-1">Jump-to-Default (JTD)</div>
              <div className="text-2xl font-bold text-fd-green font-mono">$0.00</div>
            </div>
            <div className="bg-fd-dark rounded p-4">
              <div className="text-xs text-fd-text-muted mb-1">Fair Spread</div>
              <div className="text-2xl font-bold text-fd-green font-mono">0 bps</div>
            </div>
          </div>

          <div className="mt-6 bg-fd-dark rounded p-4">
            <div className="flex items-start gap-2">
              <svg
                className="w-5 h-5 text-fd-green mt-0.5 flex-shrink-0"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
                ></path>
              </svg>
              <div className="text-sm text-fd-text-muted">
                <p className="mb-2">
                  This trade has experienced a credit event (Bankruptcy or Restructuring) resulting
                  in payout. The CDS contract has fulfilled its protection obligation.
                </p>
                <p>
                  All risk exposures have been reduced to zero as the contract has settled and no
                  further obligations remain.
                </p>
              </div>
            </div>
          </div>

          {/* Paid Coupons Information */}
          <div className="mt-6 bg-fd-dark rounded p-4">
            <h4 className="text-fd-text font-semibold mb-3 flex items-center gap-2">
              <svg
                className="w-5 h-5 text-fd-green"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2m-6 9l2 2 4-4"
                ></path>
              </svg>
              Coupon Payment History
            </h4>
            {couponPeriods.length === 0 ? (
              <p className="text-fd-text-muted text-sm">
                No coupon schedule was generated for this trade.
              </p>
            ) : (
              (() => {
                const paidCoupons = couponPeriods.filter((p) => p.paid === true);
                if (paidCoupons.length === 0) {
                  return (
                    <p className="text-fd-text-muted text-sm">
                      No coupons were paid before the credit event occurred.
                    </p>
                  );
                }
                return (
                  <div className="space-y-2">
                    <p className="text-fd-text text-sm mb-3">
                      {paidCoupons.length} coupon{paidCoupons.length > 1 ? 's' : ''} paid before
                      credit event:
                    </p>
                    <div className="space-y-2 max-h-48 overflow-y-auto">
                      {paidCoupons.map((period, index) => (
                        <div
                          key={period.id}
                          className="bg-fd-darker rounded p-3 flex items-center justify-between border border-fd-border"
                        >
                          <div className="flex-1">
                            <div className="flex items-center gap-2">
                              <svg
                                className="w-4 h-4 text-fd-green"
                                fill="currentColor"
                                viewBox="0 0 20 20"
                              >
                                <path
                                  fillRule="evenodd"
                                  d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z"
                                  clipRule="evenodd"
                                ></path>
                              </svg>
                              <span className="text-fd-text text-sm font-medium">
                                Period #{index + 1}
                              </span>
                              <span className="text-fd-text-muted text-xs">
                                ({new Date(period.periodStartDate).toLocaleDateString()} -{' '}
                                {new Date(period.periodEndDate).toLocaleDateString()})
                              </span>
                            </div>
                            <div className="text-xs text-fd-text-muted mt-1">
                              Payment Date: {new Date(period.paymentDate).toLocaleDateString()}
                              {period.paidAt && (
                                <span className="ml-2 text-fd-green">
                                   Paid: {new Date(period.paidAt).toLocaleDateString()}
                                </span>
                              )}
                            </div>
                          </div>
                          <div className="text-right">
                            <div className="text-fd-green font-mono font-semibold">
                              {new Intl.NumberFormat('en-US', {
                                style: 'currency',
                                currency: data?.currency || 'USD',
                                minimumFractionDigits: 2,
                                maximumFractionDigits: 2,
                              }).format(period.couponAmount || 0)}
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                );
              })()
            )}
          </div>
        </div>
      </div>
    );
  }

  if (loading)
    return (
      <div className="flex items-center gap-2 text-fd-text">
        <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-fd-green"></div>
        <span>Calculating risk measures with ORE...</span>
      </div>
    );
  if (error)
    return (
      <div className="text-red-400" role="alert">
        Failed: {error}
      </div>
    );
  if (!data) return null;

  const formatValue = (value: number | null | undefined, decimals: number = 2): string => {
    if (value === null || value === undefined) return '-';
    return typeof value === 'number'
      ? value.toLocaleString(undefined, {
          minimumFractionDigits: decimals,
          maximumFractionDigits: decimals,
        })
      : String(value);
  };

  const formatCurrency = (value: number | null | undefined, currency: string = 'USD'): string => {
    if (value === null || value === undefined) return '-';
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency,
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    }).format(value);
  };

  const formatBasisPoints = (value: number | null | undefined): string => {
    if (value === null || value === undefined) return '-';
    return (value * 10000).toFixed(2) + ' bps';
  };

  // Calculate Risky PV01 if ORE doesn't provide it
  // Formula: |Premium Leg NPV + Accrued Premium| / (Notional  Running Spread)
  const calculateRiskyPV01 = (): number | null => {
    // If ORE provided it, use that
    if (data.riskyAnnuity !== null && data.riskyAnnuity !== undefined) {
      return data.riskyAnnuity;
    }

    // Otherwise calculate it from available data
    const premiumLegNPV = data.premiumLegNPVClean || 0;
    const accruedPremium = data.accruedPremium || 0;
    const notional = data.currentNotional || data.originalNotional;
    const runningSpread = data.fairSpreadClean; // Fair spread in decimal form

    if (!notional || !runningSpread || runningSpread === 0) {
      return null;
    }

    const riskyPV01 = Math.abs(premiumLegNPV + accruedPremium) / (notional * runningSpread);
    return riskyPV01;
  };

  const riskyPV01 = calculateRiskyPV01();

  // Find the next unpaid coupon (earliest payment date among unpaid)
  const getNextUnpaidCoupon = (): CouponPeriod | null => {
    const unpaidCoupons = couponPeriods
      .filter((p) => !p.paid)
      .sort((a, b) => new Date(a.paymentDate).getTime() - new Date(b.paymentDate).getTime());
    return unpaidCoupons.length > 0 ? unpaidCoupons[0] : null;
  };

  // Find the most recently paid coupon (latest payment date among paid coupons)
  const getMostRecentlyPaidCoupon = (): CouponPeriod | null => {
    const paidCoupons = couponPeriods
      .filter((p) => p.paid)
      .sort((a, b) => new Date(b.paymentDate).getTime() - new Date(a.paymentDate).getTime());
    return paidCoupons.length > 0 ? paidCoupons[0] : null;
  };

  const nextUnpaidCoupon = getNextUnpaidCoupon();
  const mostRecentlyPaidCoupon = getMostRecentlyPaidCoupon();

  const canPayCoupon = (period: CouponPeriod): boolean => {
    if (period.paid) return false;
    if (!nextUnpaidCoupon) return false;
    return period.id === nextUnpaidCoupon.id;
  };

  const canUnpayCoupon = (period: CouponPeriod): boolean => {
    if (!period.paid) return false;
    if (!mostRecentlyPaidCoupon) return false;
    return period.id === mostRecentlyPaidCoupon.id;
  };

  // Calculate coupon statistics
  const totalCoupons = couponPeriods.length;
  const paidCoupons = couponPeriods.filter((p) => p.paid).length;
  const totalPaidAmount = couponPeriods
    .filter((p) => p.paid && p.couponAmount)
    .reduce((sum, p) => sum + (p.couponAmount || 0), 0);

  // Derive frequency and maturity from coupon periods
  const getScheduleDescription = () => {
    if (couponPeriods.length === 0) return '';

    // Get maturity from last period
    const lastPeriod = couponPeriods[couponPeriods.length - 1];
    const maturityDate = new Date(lastPeriod.periodEndDate);
    const maturityYear = maturityDate.getFullYear();
    const maturityMonth = maturityDate.toLocaleDateString('en-US', { month: 'short' });

    // Use trade's premium frequency if available
    let frequency = 'Periodic';
    if (trade?.premiumFrequency) {
      // Map the database values to display names
      const frequencyMap: Record<string, string> = {
        MONTHLY: 'Monthly',
        QUARTERLY: 'Quarterly',
        SEMI_ANNUAL: 'Semi-annual',
        ANNUAL: 'Annual',
      };
      frequency = frequencyMap[trade.premiumFrequency] || trade.premiumFrequency;
    } else if (couponPeriods.length > 1) {
      // Fall back to calculating from payment date intervals if trade data not available
      const firstPayment = new Date(couponPeriods[0].paymentDate);
      const secondPayment = new Date(couponPeriods[1].paymentDate);
      const daysDiff = Math.round(
        (secondPayment.getTime() - firstPayment.getTime()) / (1000 * 60 * 60 * 24)
      );

      // More lenient ranges for CDS IMM schedules
      // Quarterly: ~91 days (3 months)
      // Semi-annual: ~182 days (6 months)
      // Monthly: ~30 days
      // Annual: ~365 days
      if (daysDiff >= 25 && daysDiff <= 35) frequency = 'Monthly';
      else if (daysDiff >= 80 && daysDiff <= 100) frequency = 'Quarterly';
      else if (daysDiff >= 170 && daysDiff <= 195) frequency = 'Semi-annual';
      else if (daysDiff >= 350 && daysDiff <= 375) frequency = 'Annual';
    }

    return `${frequency} until ${maturityMonth} ${maturityYear}`;
  };

  const hasCashflows = data.cashflows && data.cashflows.length > 0;
  const hasCDSMetrics = data.fairSpreadClean !== null || data.protectionLegNPV !== null;

  return (
    <div className="space-y-6">
      {/* Core ORE Valuation */}
      <div className="bg-fd-darker p-4 rounded-md border border-fd-border">
        <div className="flex justify-between items-start mb-3">
          <h3 className="text-fd-green font-semibold flex items-center gap-2">
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M9 7h6m0 10v-3m-3 3h.01M9 17h.01M9 14h.01M12 14h.01M15 11h.01M12 11h.01M9 11h.01M7 21h10a2 2 0 002-2V5a2 2 0 00-2-2H7a2 2 0 00-2 2v14a2 2 0 002 2z"
              ></path>
            </svg>
            ORE Valuation
          </h3>

          {/* Valuation Date Quick Selector */}
          <div className="flex items-center gap-2">
            <span className="text-xs text-fd-text-muted">Valuation Date:</span>
            <div className="flex gap-1">
              <button
                onClick={() => handleQuickValuationDate('today')}
                disabled={loading || recalculating}
                className={`px-2 py-1 text-xs font-medium rounded transition-colors ${
                  valuationDate === undefined || valuationDate === ''
                    ? 'bg-fd-green text-fd-dark'
                    : 'bg-fd-dark text-fd-text hover:bg-fd-border'
                } disabled:opacity-50 disabled:cursor-not-allowed`}
              >
                Today
              </button>
              <button
                onClick={() => handleQuickValuationDate('t+1')}
                disabled={loading || recalculating}
                className={`px-2 py-1 text-xs font-medium rounded transition-colors ${
                  valuationDate === getBusinessDaysFromToday(1)
                    ? 'bg-fd-green text-fd-dark'
                    : 'bg-fd-dark text-fd-text hover:bg-fd-border'
                } disabled:opacity-50 disabled:cursor-not-allowed`}
              >
                T+1
              </button>
              <button
                onClick={() => handleQuickValuationDate('t+7')}
                disabled={loading || recalculating}
                className={`px-2 py-1 text-xs font-medium rounded transition-colors ${
                  valuationDate === getBusinessDaysFromToday(7)
                    ? 'bg-fd-green text-fd-dark'
                    : 'bg-fd-dark text-fd-text hover:bg-fd-border'
                } disabled:opacity-50 disabled:cursor-not-allowed`}
              >
                T+7
              </button>
              <button
                onClick={() => handleQuickValuationDate('t+45')}
                disabled={loading || recalculating}
                className={`px-2 py-1 text-xs font-medium rounded transition-colors ${
                  valuationDate === getBusinessDaysFromToday(45)
                    ? 'bg-fd-green text-fd-dark'
                    : 'bg-fd-dark text-fd-text hover:bg-fd-border'
                } disabled:opacity-50 disabled:cursor-not-allowed`}
              >
                T+45
              </button>
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="bg-fd-dark rounded p-3">
            <div className="text-xs text-fd-text-muted mb-1">Net Present Value</div>
            <div className="text-2xl font-bold text-fd-green font-mono">
              {formatCurrency(data.npv, data.currency)}
            </div>
          </div>
          <div className="bg-fd-dark rounded p-3">
            <div className="text-xs text-fd-text-muted mb-1">Currency</div>
            <div className="text-2xl font-bold text-fd-text font-mono">
              {data.currency || 'USD'}
            </div>
          </div>
          <div className="bg-fd-dark rounded p-3">
            <div className="text-xs text-fd-text-muted mb-1">Valuation Time</div>
            <div className="text-sm text-fd-text">
              {new Date(data.valuationTimestamp).toLocaleString()}
            </div>
          </div>
        </div>
      </div>

      {/* CDS-Specific Metrics (if available) */}
      {hasCDSMetrics && (
        <div className="bg-fd-darker p-4 rounded-md border border-fd-border">
          <h3 className="text-fd-green font-semibold mb-3 flex items-center gap-2">
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M13 7h8m0 0v8m0-8l-8 8-4-4-6 6"
              ></path>
            </svg>
            CDS Valuation Breakdown
          </h3>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-3 text-sm">
            {data.jtd !== null && data.jtd !== undefined && (
              <div className="md:col-span-2 lg:col-span-4 bg-fd-dark rounded p-3 border-l-4 border-fd-green">
                <span className="text-fd-text-muted">Jump-to-Default (JTD) Exposure:</span>
                <div className="font-mono text-fd-green font-bold text-xl mt-1">
                  {formatCurrency(data.jtd, data.currency)}
                </div>
                <div className="text-xs text-fd-text-muted mt-1">
                  Potential loss if reference entity defaults immediately
                </div>
              </div>
            )}
            <div>
              <span className="text-fd-text-muted">Fair Spread (Clean):</span>
              <div className="font-mono text-fd-text font-semibold">
                {formatBasisPoints(data.fairSpreadClean)}
              </div>
            </div>
            <div>
              <span className="text-fd-text-muted">Fair Spread (Dirty):</span>
              <div className="font-mono text-fd-text font-semibold">
                {formatBasisPoints(data.fairSpreadDirty)}
              </div>
            </div>
            <div>
              <span className="text-fd-text-muted">Protection Leg NPV:</span>
              <div className="font-mono text-fd-text font-semibold">
                {formatCurrency(data.protectionLegNPV, data.currency)}
              </div>
            </div>
            <div>
              <span className="text-fd-text-muted">Premium Leg NPV:</span>
              <div className="font-mono text-fd-text font-semibold">
                {formatCurrency(data.premiumLegNPVClean, data.currency)}
              </div>
            </div>
            <div>
              <span className="text-fd-text-muted">Accrued Premium:</span>
              <div className="font-mono text-fd-text">
                {formatCurrency(data.accruedPremium, data.currency)}
              </div>
            </div>
            <div>
              <span className="text-fd-text-muted">Upfront Premium:</span>
              <div className="font-mono text-fd-text">
                {formatCurrency(data.upfrontPremium, data.currency)}
              </div>
            </div>
            <div>
              <span className="text-fd-text-muted">Coupon Leg BPS:</span>
              <div className="font-mono text-fd-text">{formatValue(data.couponLegBPS, 2)}</div>
            </div>
            <div>
              <span className="text-fd-text-muted">Current Notional:</span>
              <div className="font-mono text-fd-text">
                {formatCurrency(data.currentNotional, data.currency)}
              </div>
            </div>
            {riskyPV01 !== null && (
              <div>
                <span className="text-fd-text-muted">Risky PV01:</span>
                <div className="font-mono text-fd-text font-semibold text-fd-green">
                  {formatValue(riskyPV01, 6)}
                </div>
                <div className="text-xs text-fd-text-muted mt-0.5">
                  {data.riskyAnnuity ? 'From ORE' : 'Calculated'}
                </div>
              </div>
            )}
            {trade?.recoveryRate !== undefined && trade?.recoveryRate !== null && (
              <div>
                <span className="text-fd-text-muted">Recovery Rate:</span>
                <div className="font-mono text-fd-text font-semibold">{trade.recoveryRate}%</div>
              </div>
            )}
            {paidCoupons > 0 && (
              <div>
                <span className="text-fd-text-muted">Total Paid Coupons:</span>
                <div className="font-mono text-fd-green font-semibold">
                  {formatCurrency(totalPaidAmount, data.currency)}
                </div>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Credit Risk Profile (if available) */}
      {data.defaultProbabilities && data.defaultProbabilities.length > 0 && (
        <div className="bg-fd-darker p-4 rounded-md border border-fd-border">
          <h3 className="text-fd-green font-semibold mb-3 flex items-center gap-2">
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-1.964-1.333-2.732 0L3.268 16c-.77 1.333.192 3 1.732 3z"
              ></path>
            </svg>
            Credit Risk Profile
          </h3>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <h4 className="text-sm font-medium text-fd-text mb-2">
                Default Probabilities by Period
              </h4>
              <div className="space-y-1 text-sm">
                {data.defaultProbabilities.slice(0, 6).map((prob, idx) => (
                  <div key={idx} className="flex justify-between">
                    <span className="text-fd-text-muted">Period {idx + 1}:</span>
                    <span className="font-mono text-fd-text">{(prob * 100).toFixed(2)}%</span>
                  </div>
                ))}
              </div>
            </div>

            {data.expectedLosses && data.expectedLosses.length > 0 && (
              <div>
                <h4 className="text-sm font-medium text-fd-text mb-2">Expected Losses by Period</h4>
                <div className="space-y-1 text-sm">
                  {data.expectedLosses.slice(0, 6).map((loss, idx) => (
                    <div key={idx} className="flex justify-between">
                      <span className="text-fd-text-muted">Period {idx + 1}:</span>
                      <span className="font-mono text-fd-text">
                        {formatCurrency(loss, data.currency)}
                      </span>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Coupon Payment Schedule */}
      {couponPeriods.length > 0 && (
        <div className="bg-fd-darker p-4 rounded-md border border-fd-border">
          <h3 className="text-fd-green font-semibold mb-3 flex items-center gap-2">
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M17 9V7a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2m2 4h10a2 2 0 002-2v-6a2 2 0 00-2-2H9a2 2 0 00-2 2v6a2 2 0 002 2zm7-5a2 2 0 11-4 0 2 2 0 014 0z"
              ></path>
            </svg>
            Coupon Payment Schedule
            <span className="ml-auto text-sm font-normal text-fd-text-muted">
              {paidCoupons} of {totalCoupons} paid
            </span>
            {recalculating && (
              <span className="ml-2 text-sm font-normal text-fd-text-muted flex items-center gap-1">
                <div className="animate-spin rounded-full h-3 w-3 border-b-2 border-fd-green"></div>
                Recalculating risk...
              </span>
            )}
          </h3>

          {/* Schedule description */}
          <div className="mb-3 text-sm text-fd-text-muted italic">{getScheduleDescription()}</div>

          <div className="overflow-x-auto">
            <table className="w-full text-sm text-fd-text">
              <thead>
                <tr className="text-left border-b-2 border-fd-border bg-fd-dark">
                  <th className="py-2 px-3 font-medium">Payment Date</th>
                  <th className="py-2 px-3 font-medium">Period</th>
                  <th className="py-2 px-3 font-medium text-right">Days</th>
                  <th className="py-2 px-3 font-medium text-right">Notional</th>
                  <th className="py-2 px-3 font-medium text-right">Coupon Amount</th>
                  <th className="py-2 px-3 font-medium">Status</th>
                  <th className="py-2 px-3 font-medium text-center">Action</th>
                </tr>
              </thead>
              <tbody>
                {couponPeriods.map((period) => (
                  <tr
                    key={period.id}
                    className={`border-b border-fd-border hover:bg-fd-dark transition-colors ${period.paid ? 'opacity-60' : ''}`}
                  >
                    <td className="py-2 px-3 font-mono">
                      {new Date(period.paymentDate).toLocaleDateString()}
                    </td>
                    <td className="py-2 px-3 text-fd-text-muted">
                      {new Date(period.periodStartDate).toLocaleDateString()} {' '}
                      {new Date(period.periodEndDate).toLocaleDateString()}
                    </td>
                    <td className="py-2 px-3 font-mono text-right">{period.accrualDays}</td>
                    <td className="py-2 px-3 font-mono text-right">
                      {formatCurrency(period.notionalAmount, data?.currency)}
                    </td>
                    <td className="py-2 px-3 font-mono text-right">
                      {period.couponAmount
                        ? formatCurrency(period.couponAmount, data?.currency)
                        : '-'}
                    </td>
                    <td className="py-2 px-3">
                      {period.paid ? (
                        <span className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-green-500/20 text-green-400">
                          <svg className="w-3 h-3 mr-1" fill="currentColor" viewBox="0 0 20 20">
                            <path
                              fillRule="evenodd"
                              d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z"
                              clipRule="evenodd"
                            />
                          </svg>
                          Paid
                        </span>
                      ) : (
                        <span className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-yellow-500/20 text-yellow-400">
                          <svg className="w-3 h-3 mr-1" fill="currentColor" viewBox="0 0 20 20">
                            <path
                              fillRule="evenodd"
                              d="M10 18a8 8 0 100-16 8 8 0 000 16zm1-12a1 1 0 10-2 0v4a1 1 0 00.293.707l2.828 2.829a1 1 0 101.415-1.415L11 9.586V6z"
                              clipRule="evenodd"
                            />
                          </svg>
                          Unpaid
                        </span>
                      )}
                    </td>
                    <td className="py-2 px-3 text-center">
                      {/* Show Cancel button for the most recently paid coupon */}
                      {period.paid && canUnpayCoupon(period) && !isTradeFinalized && (
                        <div className="flex flex-col gap-1 items-center">
                          <button
                            onClick={() => handleUnpayCoupon(period.id)}
                            disabled={payingPeriodId === period.id || recalculating}
                            title="Cancel this payment (for demo)"
                            className="px-2 py-1 text-xs bg-red-600 text-white rounded font-medium hover:bg-red-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors w-full"
                          >
                            {payingPeriodId === period.id ? (
                              <span className="flex items-center gap-1 justify-center">
                                <div className="animate-spin rounded-full h-3 w-3 border-b-2 border-white"></div>
                              </span>
                            ) : (
                              ' Cancel Payment'
                            )}
                          </button>
                          <span className="text-xs text-fd-text-muted">
                            {new Date(period.paidAt!).toLocaleString()}
                          </span>
                        </div>
                      )}

                      {/* Show payment timestamp for other paid coupons */}
                      {period.paid && !canUnpayCoupon(period) && period.paidAt && (
                        <span className="text-xs text-fd-text-muted">
                          {new Date(period.paidAt).toLocaleString()}
                        </span>
                      )}
                      
                      {/* Show payment timestamp for finalized trades (no cancel button) */}
                      {period.paid && canUnpayCoupon(period) && isTradeFinalized && (
                        <span className="text-xs text-fd-text-muted">
                          {new Date(period.paidAt!).toLocaleString()}
                        </span>
                      )}
                      
                      {/* Show payment buttons for unpaid coupons */}
                      {!period.paid && !isTradeFinalized && (
                        <div className="flex gap-1 justify-center">
                          <button
                            onClick={() => handlePayCoupon(period.id, true)}
                            disabled={
                              !canPayCoupon(period) || payingPeriodId === period.id || recalculating
                            }
                            title={
                              !canPayCoupon(period)
                                ? 'You must pay earlier coupons first'
                                : 'Pay on the scheduled payment date'
                            }
                            className="px-2 py-1 text-xs bg-fd-green text-fd-dark rounded font-medium hover:bg-fd-green-hover disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                          >
                            {payingPeriodId === period.id ? (
                              <span className="flex items-center gap-1">
                                <div className="animate-spin rounded-full h-3 w-3 border-b-2 border-fd-dark"></div>
                              </span>
                            ) : canPayCoupon(period) ? (
                              'Pay On Time'
                            ) : (
                              ' Locked'
                            )}
                          </button>
                          <button
                            onClick={() => handlePayCoupon(period.id, false)}
                            disabled={
                              !canPayCoupon(period) || payingPeriodId === period.id || recalculating
                            }
                            title={
                              !canPayCoupon(period)
                                ? 'You must pay earlier coupons first'
                                : 'Pay now (backdated)'
                            }
                            className="px-2 py-1 text-xs bg-blue-600 text-white rounded font-medium hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                          >
                            {canPayCoupon(period) ? 'Pay Now' : ''}
                          </button>
                        </div>
                      )}
                      
                      {/* Show locked message for finalized trades */}
                      {!period.paid && isTradeFinalized && (
                        <span className="text-xs text-red-400">
                           Trade {trade?.tradeStatus.replace(/_/g, ' ').toLowerCase()}
                        </span>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          <div className="mt-3 text-xs text-fd-text-muted">
            <p>
               <strong>Tip:</strong> Coupons must be paid sequentially by payment date. Only the
              next unpaid coupon can be paid  earlier coupons must be settled before later ones.
              <br /> <strong>Pay On Time:</strong> Marks the coupon as paid on its scheduled
              payment date (no accrued premium).
              <br /> <strong>Pay Now:</strong> Marks the coupon as paid today (shows accrued
              premium if applicable).
              <br /> <strong>Cancel:</strong> Reverts the most recently paid coupon (for demo
              purposes only).
              <br />
              Paying or canceling a coupon will trigger a risk recalculation.
            </p>
          </div>
        </div>
      )}

      {/* No coupon schedule message */}
      {couponPeriods.length === 0 && data && !loading && (
        <div className="bg-fd-darker p-4 rounded-md border border-dashed border-fd-border text-center">
          <svg
            className="w-12 h-12 text-fd-text-muted mx-auto mb-3"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z"
            ></path>
          </svg>
          <p className="text-fd-text-muted mb-4">
            No coupon schedule generated for this trade yet.
          </p>
          {isTradeFinalized ? (
            <p className="text-red-400 text-sm">
              Cannot generate schedule - trade is {trade?.tradeStatus.replace(/_/g, ' ').toLowerCase()}
            </p>
          ) : (
            <button
              onClick={handleGenerateSchedule}
              disabled={generatingSchedule}
              className="px-4 py-2 bg-fd-green text-fd-dark rounded font-medium hover:bg-fd-green-hover disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
            >
              {generatingSchedule ? (
                <span className="flex items-center gap-2">
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-fd-dark"></div>
                  Generating Schedule...
                </span>
              ) : (
                'Generate Coupon Schedule'
              )}
            </button>
          )}
        </div>
      )}

      {/* Cashflow Schedule - Hidden: Merged with Coupon Payment Schedule above */}
      {/* hasCashflows && (
        <div className="bg-fd-darker p-4 rounded-md border border-fd-border">
          <h3 className="text-fd-green font-semibold mb-3 flex items-center gap-2">
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1M21 12a9 9 0 11-18 0 9 9 0 0118 0z"></path>
            </svg>
            Cashflow Schedule
            <span className="ml-auto text-sm font-normal text-fd-text-muted">
              {data.cashflows!.length} payment{data.cashflows!.length !== 1 ? 's' : ''}
            </span>
          </h3>
          
          <CashflowScheduleTable cashflows={data.cashflows!} />
        </div>
      ) */}

      {/* No data message */}
      {!hasCashflows && !hasCDSMetrics && (
        <div className="bg-fd-darker p-6 rounded-md border border-dashed border-fd-border text-center">
          <svg
            className="w-12 h-12 text-fd-text-muted mx-auto mb-3"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"
            ></path>
          </svg>
          <p className="text-fd-text-muted">
            Additional CDS metrics and cashflow schedule will appear here once available.
          </p>
          <p className="text-fd-text-muted text-sm mt-2">
            Currently showing NPV calculated by ORE.
          </p>
        </div>
      )}
    </div>
  );
};

export default RiskMeasuresPanel;

import React from 'react';
import { render } from '@testing-library/react';
import RegressionStatusBadge from '../../RegressionStatusBadge';

test('renders regression status badge', () => {
  // eslint-disable-next-line testing-library/prefer-screen-queries
  const { getByText } = render(<RegressionStatusBadge status="PASS" />);
  expect(getByText(/Regression: PASS/)).toBeInTheDocument();
});
