export default BondCreationModal;

import React, { useState, useEffect } from 'react';
import {
  REFERENCE_ENTITIES,
  COUNTERPARTIES,
  CURRENCIES,
  PREMIUM_FREQUENCIES,
  DAY_COUNT_CONVENTIONS,
  RESTRUCTURING_CLAUSES,
  PAYMENT_CALENDARS,
  TRADE_STATUSES,
  CDSTrade,
} from '../../data/referenceData';
import { bondService, Bond } from '../../services/bondService';

interface FormErrors {
  [key: string]: string;
}

interface CDSTradeFormProps {
  onSubmit: (trade: CDSTrade) => void;
}

const CDSTradeForm: React.FC<CDSTradeFormProps> = ({ onSubmit }) => {
  const [formData, setFormData] = useState<Partial<CDSTrade>>({
    tradeDate: new Date().toISOString().split('T')[0],
    currency: 'USD',
    premiumFrequency: 'QUARTERLY',
    dayCountConvention: 'ACT_360',
    buySellProtection: 'BUY',
    paymentCalendar: 'NYC',
    tradeStatus: 'PENDING',
    recoveryRate: 40,
  });

  const [errors, setErrors] = useState<FormErrors>({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [availableBonds, setAvailableBonds] = useState<Bond[]>([]);
  const [loadingBonds, setLoadingBonds] = useState(false);

  // Fetch bonds when reference entity changes
  useEffect(() => {
    const fetchBonds = async () => {
      if (!formData.referenceEntity) {
        setAvailableBonds([]);
        return;
      }

      setLoadingBonds(true);
      try {
        const bonds = await bondService.getBondsByIssuer(formData.referenceEntity);
        setAvailableBonds(bonds);
      } catch (error) {
        console.error('Error fetching bonds:', error);
        setAvailableBonds([]);
      } finally {
        setLoadingBonds(false);
      }
    };

    fetchBonds();
  }, [formData.referenceEntity]);

  const validateForm = (): boolean => {
    const newErrors: FormErrors = {};

    // Required field validations
    if (!formData.referenceEntity) {
      newErrors.referenceEntity = 'Reference Entity is required';
    }

    if (formData.referenceEntity && !formData.obligation?.id) {
      newErrors.obligation = 'Obligation is required';
    }

    if (!formData.notionalAmount || formData.notionalAmount <= 0) {
      newErrors.notionalAmount = 'Notional Amount must be greater than 0';
    }

    if (!formData.spread || formData.spread < 0) {
      newErrors.spread = 'Spread must be 0 or greater';
    }

    if (!formData.recoveryRate || formData.recoveryRate < 0 || formData.recoveryRate > 100) {
      newErrors.recoveryRate = 'Recovery Rate must be between 0 and 100';
    }

    if (!formData.maturityDate) {
      newErrors.maturityDate = 'Maturity Date is required';
    }

    if (!formData.effectiveDate) {
      newErrors.effectiveDate = 'Effective Date is required';
    }

    if (!formData.counterparty) {
      newErrors.counterparty = 'Counterparty is required';
    }

    if (!formData.tradeDate) {
      newErrors.tradeDate = 'Trade Date is required';
    }

    if (!formData.accrualStartDate) {
      newErrors.accrualStartDate = 'Accrual Start Date is required';
    }

    // Date validations
    if (
      formData.effectiveDate &&
      formData.maturityDate &&
      new Date(formData.effectiveDate) >= new Date(formData.maturityDate)
    ) {
      newErrors.maturityDate = 'Maturity Date must be after Effective Date';
    }

    if (
      formData.tradeDate &&
      formData.effectiveDate &&
      new Date(formData.tradeDate) > new Date(formData.effectiveDate)
    ) {
      newErrors.effectiveDate = 'Effective Date must be on or after Trade Date';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleInputChange = (field: keyof CDSTrade, value: any) => {
    setFormData((prev) => {
      const updates: Partial<CDSTrade> = {};

      // Handle obligation specially - convert to nested object
      if (field === 'obligation') {
        updates.obligation = value ? { id: value } : undefined;
      } else {
        updates[field] = value as any;
      }

      // Clear obligation when reference entity changes
      if (field === 'referenceEntity' && prev.referenceEntity !== value) {
        updates.obligation = undefined;
      }

      return {
        ...prev,
        ...updates,
      };
    });

    // Clear error for this field when user starts typing
    if (errors[field]) {
      setErrors((prev) => ({
        ...prev,
        [field]: '',
      }));
    }
  };

  // Format number with commas for display
  const formatNumberWithCommas = (num: number | undefined): string => {
    if (!num && num !== 0) return '';
    return num.toLocaleString('en-US');
  };

  // Parse formatted string back to number
  const parseFormattedNumber = (str: string): number => {
    return parseFloat(str.replace(/,/g, '')) || 0;
  };

  // Handle notional amount input with formatting
  const handleNotionalChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const rawValue = e.target.value.replace(/,/g, ''); // Remove commas
    const numValue = parseFloat(rawValue);

    if (!isNaN(numValue) || rawValue === '') {
      handleInputChange('notionalAmount', rawValue === '' ? undefined : numValue);
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);

    // Defensive normalization
    if (formData.tradeStatus === 'SETTLED' || formData.tradeStatus === 'CONFIRMED') {
      formData.tradeStatus = 'ACTIVE';
    }

    // Simulate API call delay
    setTimeout(() => {
      onSubmit(formData as CDSTrade);
      setIsSubmitting(false);

      // Reset form
      setFormData({
        tradeDate: new Date().toISOString().split('T')[0],
        currency: 'USD',
        premiumFrequency: 'QUARTERLY',
        dayCountConvention: 'ACT_360',
        buySellProtection: 'BUY',
        paymentCalendar: 'NYC',
        tradeStatus: 'PENDING',
        recoveryRate: 40,
      });
    }, 1000);
  };

  const inputClassName = (fieldName: string) => {
    const baseClass =
      'w-full px-3 py-2 bg-fd-input border rounded-md text-fd-text placeholder-fd-text-muted focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-fd-green';
    const errorClass = 'border-red-500';
    const normalClass = 'border-fd-border';

    return `${baseClass} ${errors[fieldName] ? errorClass : normalClass}`;
  };

  const selectClassName = (fieldName: string) => {
    const baseClass =
      'w-full px-3 py-2 bg-fd-input border rounded-md text-fd-text focus:outline-none focus:ring-2 focus:ring-fd-green focus:border-fd-green';
    const errorClass = 'border-red-500';
    const normalClass = 'border-fd-border';

    return `${baseClass} ${errors[fieldName] ? errorClass : normalClass}`;
  };

  const generateRandomData = () => {
    // Helper function to get random item from array
    const getRandomItem = (array: any[]) => array[Math.floor(Math.random() * array.length)];

    // Helper function to get random date within a range (days offset from today)
    const getRandomDate = (startDays: number, endDays: number) => {
      const start = new Date();
      start.setDate(start.getDate() + startDays);
      const end = new Date();
      end.setDate(end.getDate() + endDays);

      const randomTime = start.getTime() + Math.random() * (end.getTime() - start.getTime());
      return new Date(randomTime).toISOString().split('T')[0];
    };

    // Generate trades that started in the PAST for demo purposes
    // This allows demonstrating coupon payments immediately

    // Trade date: 24-48 months ago (2-4 years in the past) for more coupons
    const tradeDate = getRandomDate(-365 * 4, -365 * 2);

    // Effective date: 1-7 days after trade date (still in the past)
    const tradeDateObj = new Date(tradeDate);
    const effectiveDaysOffset = Math.floor(Math.random() * 7) + 1;
    const effectiveDateObj = new Date(tradeDateObj);
    effectiveDateObj.setDate(effectiveDateObj.getDate() + effectiveDaysOffset);
    const effectiveDate = effectiveDateObj.toISOString().split('T')[0];

    // Maturity date: 2-5 years from TODAY (in the future)
    const maturityDate = getRandomDate(365 * 2, 365 * 5);

    // Accrual start date: same as effective date
    const accrualStartDate = effectiveDate;

    // Generate round notional amounts (5M, 10M, 20M, 50M, 100M, 200M, 500M)
    const roundNotionals = [5000000, 10000000, 20000000, 50000000, 100000000, 200000000, 500000000];
    const notionalAmount = getRandomItem(roundNotionals);

    const randomData: Partial<CDSTrade> = {
      referenceEntity: getRandomItem(REFERENCE_ENTITIES).code,
      counterparty: getRandomItem(COUNTERPARTIES).code,
      currency: getRandomItem(CURRENCIES).code,
      notionalAmount,
      spread: Math.floor(Math.random() * 500) + 50, // 50 to 550 bps
      buySellProtection: Math.random() > 0.5 ? 'BUY' : 'SELL',
      tradeDate,
      effectiveDate,
      maturityDate,
      accrualStartDate,
      premiumFrequency: getRandomItem(PREMIUM_FREQUENCIES).value,
      dayCountConvention: getRandomItem(DAY_COUNT_CONVENTIONS).value,
      restructuringClause: Math.random() > 0.3 ? getRandomItem(RESTRUCTURING_CLAUSES).value : '', // 70% chance of having a clause
      paymentCalendar: getRandomItem(PAYMENT_CALENDARS).value,
      tradeStatus: 'ACTIVE', // Always generate ACTIVE trades for demo purposes
      recoveryRate: 40, // Default recovery rate
    };
    setFormData(randomData);
    setErrors({}); // Clear any existing errors
  };

  return (
    <div className="max-w-4xl mx-auto bg-fd-darker rounded-lg shadow-fd border border-fd-border p-6">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold text-fd-text">New CDS Trade Entry</h2>
        <button
          type="button"
          onClick={generateRandomData}
          className="px-4 py-2 bg-fd-teal text-fd-dark font-medium rounded hover:bg-fd-cyan transition-colors flex items-center space-x-2"
        >
          <span>🎲</span>
          <span>Fill Random Data</span>
        </button>
      </div>

      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Row 1: Reference Entity, Counterparty, Currency */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block text-fd-text font-medium mb-2">
              Reference Entity <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.referenceEntity || ''}
              onChange={(e) => handleInputChange('referenceEntity', e.target.value)}
              className={selectClassName('referenceEntity')}
            >
              <option value="">Select Reference Entity</option>
              {REFERENCE_ENTITIES.map((entity) => (
                <option key={entity.code} value={entity.code}>
                  {entity.code} - {entity.name}
                </option>
              ))}
            </select>
            {errors.referenceEntity && (
              <p className="text-red-500 text-sm mt-1">{errors.referenceEntity}</p>
            )}
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Counterparty <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.counterparty || ''}
              onChange={(e) => handleInputChange('counterparty', e.target.value)}
              className={selectClassName('counterparty')}
            >
              <option value="">Select Counterparty</option>
              {COUNTERPARTIES.map((cp) => (
                <option key={cp.code} value={cp.code}>
                  {cp.name}
                </option>
              ))}
            </select>
            {errors.counterparty && (
              <p className="text-red-500 text-sm mt-1">{errors.counterparty}</p>
            )}
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Currency <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.currency || 'USD'}
              onChange={(e) => handleInputChange('currency', e.target.value)}
              className={selectClassName('currency')}
            >
              {CURRENCIES.map((curr) => (
                <option key={curr.code} value={curr.code}>
                  {curr.code} - {curr.name}
                </option>
              ))}
            </select>
          </div>
        </div>

        {/* Row 1.5: Obligation (conditional on reference entity) */}
        {formData.referenceEntity && (
          <div className="grid grid-cols-1 gap-4">
            <div>
              <label className="block text-fd-text font-medium mb-2">
                Obligation (Bond) <span className="text-red-500">*</span>
              </label>
              <select
                value={formData.obligation?.id || ''}
                onChange={(e) =>
                  handleInputChange(
                    'obligation',
                    e.target.value ? Number(e.target.value) : undefined
                  )
                }
                className={selectClassName('obligation')}
                disabled={loadingBonds}
              >
                <option value="">Select Obligation</option>
                {loadingBonds && <option value="">Loading bonds...</option>}
                {!loadingBonds && availableBonds.length === 0 && (
                  <option value="">No bonds available for {formData.referenceEntity}</option>
                )}
                {!loadingBonds &&
                  availableBonds.map((bond) => (
                    <option key={bond.id} value={bond.id}>
                      {bond.isin ? `${bond.isin} - ` : ''}
                      {bond.issuer} {bond.seniority} - Coupon: {bond.couponRate}% - Maturity:{' '}
                      {new Date(bond.maturityDate).toLocaleDateString()}
                    </option>
                  ))}
              </select>
              <p className="text-xs text-fd-text-muted mt-1">
                Select a specific bond from {formData.referenceEntity}
              </p>
            </div>
          </div>
        )}

        {/* Row 2: Notional Amount, Spread, Buy/Sell Protection */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block text-fd-text font-medium mb-2">
              Notional Amount <span className="text-red-500">*</span>
            </label>
            <input
              type="text"
              value={formatNumberWithCommas(formData.notionalAmount)}
              onChange={handleNotionalChange}
              className={inputClassName('notionalAmount')}
              placeholder="e.g., 10,000,000"
            />
            {errors.notionalAmount && (
              <p className="text-red-500 text-sm mt-1">{errors.notionalAmount}</p>
            )}
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Spread (bps) <span className="text-red-500">*</span>
            </label>
            <input
              type="number"
              step="0.01"
              min="0"
              value={formData.spread || ''}
              onChange={(e) => handleInputChange('spread', parseFloat(e.target.value))}
              className={inputClassName('spread')}
              placeholder="e.g., 100"
            />
            {errors.spread && <p className="text-red-500 text-sm mt-1">{errors.spread}</p>}
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Recovery Rate (%) <span className="text-red-500">*</span>
            </label>
            <input
              type="number"
              step="1"
              min="0"
              max="100"
              value={formData.recoveryRate || 40}
              onChange={(e) => handleInputChange('recoveryRate', parseFloat(e.target.value))}
              className={inputClassName('recoveryRate')}
              placeholder="e.g., 40"
            />
            {errors.recoveryRate && (
              <p className="text-red-500 text-sm mt-1">{errors.recoveryRate}</p>
            )}
          </div>
        </div>

        {/* Row 3: Buy/Sell Protection */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block text-fd-text font-medium mb-2">
              Buy/Sell Protection <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.buySellProtection || 'BUY'}
              onChange={(e) => handleInputChange('buySellProtection', e.target.value)}
              className={selectClassName('buySellProtection')}
            >
              <option value="BUY">Buy Protection</option>
              <option value="SELL">Sell Protection</option>
            </select>
          </div>
        </div>

        {/* Row 4: Trade Date, Effective Date, Maturity Date */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block text-fd-text font-medium mb-2">
              Trade Date <span className="text-red-500">*</span>
            </label>
            <input
              type="date"
              value={formData.tradeDate || ''}
              onChange={(e) => handleInputChange('tradeDate', e.target.value)}
              className={inputClassName('tradeDate')}
            />
            {errors.tradeDate && <p className="text-red-500 text-sm mt-1">{errors.tradeDate}</p>}
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Effective Date <span className="text-red-500">*</span>
            </label>
            <input
              type="date"
              value={formData.effectiveDate || ''}
              onChange={(e) => handleInputChange('effectiveDate', e.target.value)}
              className={inputClassName('effectiveDate')}
            />
            {errors.effectiveDate && (
              <p className="text-red-500 text-sm mt-1">{errors.effectiveDate}</p>
            )}
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Maturity Date <span className="text-red-500">*</span>
            </label>
            <input
              type="date"
              value={formData.maturityDate || ''}
              onChange={(e) => handleInputChange('maturityDate', e.target.value)}
              className={inputClassName('maturityDate')}
            />
            {errors.maturityDate && (
              <p className="text-red-500 text-sm mt-1">{errors.maturityDate}</p>
            )}
          </div>
        </div>

        {/* Row 4: Premium Frequency, Day Count Convention, Accrual Start Date */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block text-fd-text font-medium mb-2">
              Premium Frequency <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.premiumFrequency || 'QUARTERLY'}
              onChange={(e) => handleInputChange('premiumFrequency', e.target.value)}
              className={selectClassName('premiumFrequency')}
            >
              {PREMIUM_FREQUENCIES.map((freq) => (
                <option key={freq.value} value={freq.value}>
                  {freq.label}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Day Count Convention <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.dayCountConvention || 'ACT_360'}
              onChange={(e) => handleInputChange('dayCountConvention', e.target.value)}
              className={selectClassName('dayCountConvention')}
            >
              {DAY_COUNT_CONVENTIONS.map((dcc) => (
                <option key={dcc.value} value={dcc.value}>
                  {dcc.label}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Accrual Start Date <span className="text-red-500">*</span>
            </label>
            <input
              type="date"
              value={formData.accrualStartDate || ''}
              onChange={(e) => handleInputChange('accrualStartDate', e.target.value)}
              className={inputClassName('accrualStartDate')}
            />
            {errors.accrualStartDate && (
              <p className="text-red-500 text-sm mt-1">{errors.accrualStartDate}</p>
            )}
          </div>
        </div>

        {/* Row 5: Payment Calendar, Restructuring Clause, Trade Status */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block text-fd-text font-medium mb-2">
              Payment Calendar <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.paymentCalendar || 'NYC'}
              onChange={(e) => handleInputChange('paymentCalendar', e.target.value)}
              className={selectClassName('paymentCalendar')}
            >
              {PAYMENT_CALENDARS.map((cal) => (
                <option key={cal.value} value={cal.value}>
                  {cal.label}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">Restructuring Clause</label>
            <select
              value={formData.restructuringClause || ''}
              onChange={(e) => handleInputChange('restructuringClause', e.target.value)}
              className={selectClassName('restructuringClause')}
            >
              <option value="">None</option>
              {RESTRUCTURING_CLAUSES.map((clause) => (
                <option key={clause.value} value={clause.value}>
                  {clause.label}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-fd-text font-medium mb-2">
              Trade Status <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.tradeStatus || 'PENDING'}
              onChange={(e) => handleInputChange('tradeStatus', e.target.value)}
              className={selectClassName('tradeStatus')}
            >
              {TRADE_STATUSES.map((status) => (
                <option key={status.value} value={status.value}>
                  {status.label}
                </option>
              ))}
            </select>
          </div>
        </div>

        {/* Submit Button */}
        <div className="flex justify-end space-x-4 pt-6 border-t border-fd-border">
          <button
            type="button"
            onClick={() => {
              setFormData({
                tradeDate: new Date().toISOString().split('T')[0],
                currency: 'USD',
                premiumFrequency: 'QUARTERLY',
                dayCountConvention: 'ACT_360',
                buySellProtection: 'BUY',
                paymentCalendar: 'NYC',
                tradeStatus: 'PENDING',
                recoveryRate: 40,
              });
              setErrors({});
            }}
            className="px-6 py-2 bg-transparent border border-fd-border text-fd-text rounded hover:bg-fd-dark transition-colors"
          >
            Clear Form
          </button>
          <button
            type="submit"
            disabled={isSubmitting}
            className="px-6 py-2 bg-fd-green text-fd-dark font-medium rounded hover:bg-fd-green-hover disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
          >
            {isSubmitting ? 'Booking Trade...' : 'Book Trade'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default CDSTradeForm;

import React, { useState } from 'react';

export type CreditEventType =
  | 'BANKRUPTCY'
  | 'FAILURE_TO_PAY'
  | 'RESTRUCTURING'
  | 'OBLIGATION_DEFAULT'
  | 'REPUDIATION_MORATORIUM'
  | 'PAYOUT';
export type SettlementMethod = 'CASH' | 'PHYSICAL';

export interface CreateCreditEventRequest {
  eventType: CreditEventType;
  eventDate: string;
  noticeDate: string;
  settlementMethod: SettlementMethod;
  comments?: string;
}

interface CreditEventModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSubmit: (request: CreateCreditEventRequest) => Promise<void>;
  tradeId: number;
  isLoading?: boolean;
}

const CreditEventModal: React.FC<CreditEventModalProps> = ({
  isOpen,
  onClose,
  onSubmit,
  tradeId,
  isLoading = false,
}) => {
  // Get today's date in YYYY-MM-DD format
  const getTodayDate = () => {
    const today = new Date();
    return today.toISOString().split('T')[0];
  };

  const [formData, setFormData] = useState<CreateCreditEventRequest>({
    eventType: 'RESTRUCTURING',
    eventDate: getTodayDate(),
    noticeDate: getTodayDate(),
    settlementMethod: 'PHYSICAL',
    comments: '',
  });

  const [errors, setErrors] = useState<Record<string, string>>({});

  const eventTypeOptions: { value: CreditEventType; label: string }[] = [
    { value: 'RESTRUCTURING', label: 'Restructuring' },
    { value: 'BANKRUPTCY', label: 'Bankruptcy' },
  ];

  const settlementMethodOptions: { value: SettlementMethod; label: string }[] = [
    { value: 'PHYSICAL', label: 'Physical Settlement' },
    { value: 'CASH', label: 'Cash Settlement' },
  ];

  const validateForm = (): boolean => {
    const newErrors: Record<string, string> = {};

    if (!formData.eventType) {
      newErrors.eventType = 'Event type is required';
    }

    if (!formData.eventDate) {
      newErrors.eventDate = 'Event date is required';
    } else if (new Date(formData.eventDate) > new Date()) {
      newErrors.eventDate = 'Event date cannot be in the future';
    }

    if (!formData.noticeDate) {
      newErrors.noticeDate = 'Notice date is required';
    } else if (formData.eventDate && new Date(formData.noticeDate) < new Date(formData.eventDate)) {
      newErrors.noticeDate = 'Notice date must be on or after event date';
    }

    if (!formData.settlementMethod) {
      newErrors.settlementMethod = 'Settlement method is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    try {
      await onSubmit(formData);
      // Reset form on successful submission
      setFormData({
        eventType: 'RESTRUCTURING',
        eventDate: getTodayDate(),
        noticeDate: getTodayDate(),
        settlementMethod: 'PHYSICAL',
        comments: '',
      });
      setErrors({});
      onClose();
    } catch (error) {
      console.error('Error submitting credit event:', error);
    }
  };

  const handleInputChange = (field: keyof CreateCreditEventRequest, value: string) => {
    setFormData((prev) => ({
      ...prev,
      [field]: value,
    }));

    // Clear error when user starts typing
    if (errors[field]) {
      setErrors((prev) => ({
        ...prev,
        [field]: '',
      }));
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-fd-darker rounded-lg shadow-fd border border-fd-border p-6 w-full max-w-md max-h-[90vh] overflow-y-auto">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold text-fd-text">Record Credit Event</h2>
          <button
            onClick={onClose}
            className="text-fd-text-muted hover:text-fd-text transition-colors"
            disabled={isLoading}
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M6 18L18 6M6 6l12 12"
              />
            </svg>
          </button>
        </div>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label htmlFor="eventType" className="block text-sm font-medium text-fd-text mb-1">
              Event Type *
            </label>
            <select
              id="eventType"
              value={formData.eventType}
              onChange={(e) => handleInputChange('eventType', e.target.value)}
              className={`w-full px-3 py-2 bg-fd-dark border rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green text-fd-text ${
                errors.eventType ? 'border-red-500' : 'border-fd-border'
              }`}
              disabled={isLoading}
            >
              {eventTypeOptions.map((option) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
            {errors.eventType && <p className="mt-1 text-sm text-red-400">{errors.eventType}</p>}
          </div>

          <div>
            <label htmlFor="eventDate" className="block text-sm font-medium text-fd-text mb-1">
              Event Date *
            </label>
            <input
              type="date"
              id="eventDate"
              value={formData.eventDate}
              onChange={(e) => handleInputChange('eventDate', e.target.value)}
              className={`w-full px-3 py-2 bg-fd-dark border rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green text-fd-text ${
                errors.eventDate ? 'border-red-500' : 'border-fd-border'
              }`}
              disabled={isLoading}
            />
            {errors.eventDate && <p className="mt-1 text-sm text-red-400">{errors.eventDate}</p>}
          </div>

          <div>
            <label htmlFor="noticeDate" className="block text-sm font-medium text-fd-text mb-1">
              Notice Date *
            </label>
            <input
              type="date"
              id="noticeDate"
              value={formData.noticeDate}
              onChange={(e) => handleInputChange('noticeDate', e.target.value)}
              className={`w-full px-3 py-2 bg-fd-dark border rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green text-fd-text ${
                errors.noticeDate ? 'border-red-500' : 'border-fd-border'
              }`}
              disabled={isLoading}
            />
            {errors.noticeDate && <p className="mt-1 text-sm text-red-400">{errors.noticeDate}</p>}
          </div>

          <div>
            <label
              htmlFor="settlementMethod"
              className="block text-sm font-medium text-fd-text mb-1"
            >
              Settlement Method *
            </label>
            <select
              id="settlementMethod"
              value={formData.settlementMethod}
              onChange={(e) => handleInputChange('settlementMethod', e.target.value)}
              className={`w-full px-3 py-2 bg-fd-dark border rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green text-fd-text ${
                errors.settlementMethod ? 'border-red-500' : 'border-fd-border'
              }`}
              disabled={isLoading}
            >
              {settlementMethodOptions.map((option) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
            {errors.settlementMethod && (
              <p className="mt-1 text-sm text-red-400">{errors.settlementMethod}</p>
            )}
          </div>

          <div>
            <label htmlFor="comments" className="block text-sm font-medium text-fd-text mb-1">
              Comments
            </label>
            <textarea
              id="comments"
              value={formData.comments}
              onChange={(e) => handleInputChange('comments', e.target.value)}
              rows={3}
              className="w-full px-3 py-2 bg-fd-dark border border-fd-border rounded-md focus:outline-none focus:ring-2 focus:ring-fd-green text-fd-text placeholder-fd-text-muted"
              placeholder="Optional comments about the credit event..."
              disabled={isLoading}
            />
          </div>

          <div className="flex justify-end space-x-3 pt-4">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 text-sm font-medium text-fd-text bg-fd-dark border border-fd-border rounded-md hover:bg-fd-border focus:outline-none focus:ring-2 focus:ring-fd-green transition-colors"
              disabled={isLoading}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="px-4 py-2 text-sm font-medium text-fd-dark bg-fd-green border border-transparent rounded-md hover:bg-fd-green-hover focus:outline-none focus:ring-2 focus:ring-fd-green disabled:opacity-50 transition-colors"
              disabled={isLoading}
            >
              {isLoading ? 'Recording...' : 'Record Credit Event'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreditEventModal;

import React, { useEffect, useState } from 'react';
import { lifecycleService } from '../../services/lifecycleService';
import { AccrualEvent } from '../../types/lifecycle';

interface Props {
  tradeId: number;
}

export const AccrualHistoryPanel: React.FC<Props> = ({ tradeId }) => {
  const [accruals, setAccruals] = useState<AccrualEvent[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [range, setRange] = useState({ start: '', end: '' });
  const [cumulative, setCumulative] = useState<number | null>(null);

  const loadAccruals = async () => {
    if (!range.start || !range.end) return;
    setLoading(true);
    setError(null);
    try {
      const data = await lifecycleService.getAccrualEvents(tradeId, range.start, range.end);
      setAccruals(data);
    } catch (e: any) {
      setError(e.message || 'Failed to load accrual events');
    } finally {
      setLoading(false);
    }
  };

  const loadCumulative = async () => {
    try {
      const cum = await lifecycleService.getCumulativeAccrual(tradeId);
      setCumulative(cum);
    } catch {}
  };

  useEffect(() => {
    loadCumulative();
  }, [tradeId]);

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <h4 className="text-fd-text font-semibold">Accrual History</h4>
        <div className="flex items-center space-x-2">
          <input
            type="date"
            value={range.start}
            onChange={(e) => setRange((r) => ({ ...r, start: e.target.value }))}
            className="bg-fd-dark border border-fd-border rounded px-2 py-1 text-xs text-fd-text"
          />
          <span className="text-fd-text-muted text-xs">to</span>
          <input
            type="date"
            value={range.end}
            onChange={(e) => setRange((r) => ({ ...r, end: e.target.value }))}
            className="bg-fd-dark border border-fd-border rounded px-2 py-1 text-xs text-fd-text"
          />
          <button
            onClick={loadAccruals}
            disabled={!range.start || !range.end}
            className="px-3 py-1 text-xs bg-fd-green text-fd-dark rounded font-medium disabled:opacity-40"
          >
            Load
          </button>
        </div>
      </div>
      {cumulative !== null && (
        <div className="bg-fd-dark border border-fd-border rounded p-3 flex items-center justify-between">
          <span className="text-fd-text-muted text-xs uppercase tracking-wide">
            Cumulative Accrual
          </span>
          <span className="text-fd-green font-semibold">
            {cumulative.toLocaleString(undefined, {
              minimumFractionDigits: 2,
              maximumFractionDigits: 2,
            })}
          </span>
        </div>
      )}
      {loading && <div className="text-fd-text-muted text-sm">Loading accruals...</div>}
      {error && <div className="text-red-400 text-sm">{error}</div>}
      {!loading && accruals.length === 0 && !error && range.start && range.end && (
        <div className="text-fd-text-muted text-sm">No accruals in range.</div>
      )}
      {accruals.length > 0 && (
        <div className="overflow-x-auto border border-fd-border rounded max-h-72 overflow-y-auto">
          <table className="min-w-full text-xs">
            <thead className="bg-fd-dark sticky top-0">
              <tr className="text-fd-text-muted">
                <th className="px-3 py-2 text-left font-medium">Date</th>
                <th className="px-3 py-2 text-left font-medium">Accrual</th>
                <th className="px-3 py-2 text-left font-medium">Cumulative</th>
                <th className="px-3 py-2 text-left font-medium">Notional</th>
                <th className="px-3 py-2 text-left font-medium">Version</th>
              </tr>
            </thead>
            <tbody>
              {accruals.map((a) => (
                <tr key={a.id} className="border-t border-fd-border hover:bg-fd-darker/40">
                  <td className="px-3 py-1 text-fd-text font-mono">{a.accrualDate}</td>
                  <td className="px-3 py-1 text-fd-green font-medium">
                    {a.accrualAmount.toLocaleString(undefined, { minimumFractionDigits: 2 })}
                  </td>
                  <td className="px-3 py-1 text-fd-text-muted">
                    {a.cumulativeAccrual.toLocaleString(undefined, { minimumFractionDigits: 2 })}
                  </td>
                  <td className="px-3 py-1 text-fd-text-muted">
                    {a.notionalAmount.toLocaleString()}
                  </td>
                  <td className="px-3 py-1 text-fd-text-muted">{a.tradeVersion}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

import React, { useState, useEffect, useCallback } from 'react';
import {
  BondPortfolioConstituent,
  BasketPortfolioConstituent,
  CdsPortfolioConstituent,
} from '../../services/portfolioService';

interface EnhancedOverviewProps {
  portfolioId: number;
  cdsConstituents: CdsPortfolioConstituent[];
  bondConstituents: BondPortfolioConstituent[];
  basketConstituents: BasketPortfolioConstituent[];
  pricingData: any;
}

interface IssuerExposure {
  issuer: string;
  sector: string;
  bondNotional: number;
  cdsProtectionBought: number;
  cdsProtectionSold: number;
  netCreditExposure: number;
  hedgeRatio: number;
  hedgeStatus: 'OVER_HEDGED' | 'UNDER_HEDGED' | 'BALANCED' | 'UNHEDGED';
  recommendation: string;
}

interface SectorExposure {
  sector: string;
  notional: number;
  percentage: number;
  instrumentCount: number;
}

const EnhancedOverview: React.FC<EnhancedOverviewProps> = ({
  portfolioId,
  cdsConstituents,
  bondConstituents,
  basketConstituents,
  pricingData,
}) => {
  const [issuerExposures, setIssuerExposures] = useState<IssuerExposure[]>([]);
  const [sectorExposures, setSectorExposures] = useState<SectorExposure[]>([]);

  useEffect(() => {
    calculateExposures();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [cdsConstituents, bondConstituents, basketConstituents]);

  const calculateExposures = useCallback(() => {
    // Map to aggregate by issuer
    const issuerMap = new Map<string, IssuerExposure>();

    // Process bonds
    bondConstituents.forEach((bc) => {
      const issuer = bc.bond.issuer;
      if (!issuerMap.has(issuer)) {
        issuerMap.set(issuer, {
          issuer,
          sector: getSectorFromIssuer(issuer),
          bondNotional: 0,
          cdsProtectionBought: 0,
          cdsProtectionSold: 0,
          netCreditExposure: 0,
          hedgeRatio: 0,
          hedgeStatus: 'UNHEDGED',
          recommendation: '',
        });
      }
      const exposure = issuerMap.get(issuer)!;
      exposure.bondNotional += bc.bond.notional;
    });

    // Process CDS
    cdsConstituents.forEach((cc) => {
      const issuer = cc.trade.referenceEntity;
      if (!issuerMap.has(issuer)) {
        issuerMap.set(issuer, {
          issuer,
          sector: getSectorFromIssuer(issuer),
          bondNotional: 0,
          cdsProtectionBought: 0,
          cdsProtectionSold: 0,
          netCreditExposure: 0,
          hedgeRatio: 0,
          hedgeStatus: 'UNHEDGED',
          recommendation: '',
        });
      }
      const exposure = issuerMap.get(issuer)!;

      // Assuming BUY = buying protection (short credit), SELL = selling protection (long credit)
      // This is a simplification - you may need to check actual buySell field
      exposure.cdsProtectionBought += cc.trade.notionalAmount;
    });

    // Calculate net exposures and recommendations
    const exposures: IssuerExposure[] = [];
    issuerMap.forEach((exp) => {
      exp.netCreditExposure = exp.bondNotional - exp.cdsProtectionBought + exp.cdsProtectionSold;

      if (exp.bondNotional > 0) {
        exp.hedgeRatio = (exp.cdsProtectionBought / exp.bondNotional) * 100;

        if (exp.hedgeRatio > 110) {
          exp.hedgeStatus = 'OVER_HEDGED';
          exp.recommendation = `Consider reducing CDS protection by $${((exp.cdsProtectionBought - exp.bondNotional) / 1000000).toFixed(1)}M`;
        } else if (exp.hedgeRatio >= 90 && exp.hedgeRatio <= 110) {
          exp.hedgeStatus = 'BALANCED';
          exp.recommendation = 'Well hedged';
        } else if (exp.hedgeRatio > 0) {
          exp.hedgeStatus = 'UNDER_HEDGED';
          exp.recommendation = `Consider buying $${((exp.bondNotional - exp.cdsProtectionBought) / 1000000).toFixed(1)}M CDS protection`;
        } else {
          exp.hedgeStatus = 'UNHEDGED';
          exp.recommendation = `⚠️ Buy $${(exp.bondNotional / 1000000).toFixed(1)}M CDS protection to hedge bond exposure`;
        }
      } else if (exp.cdsProtectionBought > 0) {
        exp.hedgeStatus = 'UNHEDGED';
        exp.recommendation = `Consider buying $${(exp.cdsProtectionBought / 1000000).toFixed(1)}M in bonds to capture basis`;
      }

      exposures.push(exp);
    });

    setIssuerExposures(
      exposures.sort((a, b) => Math.abs(b.netCreditExposure) - Math.abs(a.netCreditExposure))
    );

    // Calculate sector exposures
    const sectorMap = new Map<string, { notional: number; count: number }>();

    bondConstituents.forEach((bc) => {
      const sector = getSectorFromIssuer(bc.bond.issuer);
      if (!sectorMap.has(sector)) {
        sectorMap.set(sector, { notional: 0, count: 0 });
      }
      const sectorData = sectorMap.get(sector)!;
      sectorData.notional += bc.bond.notional;
      sectorData.count++;
    });

    cdsConstituents.forEach((cc) => {
      const sector = getSectorFromIssuer(cc.trade.referenceEntity);
      if (!sectorMap.has(sector)) {
        sectorMap.set(sector, { notional: 0, count: 0 });
      }
      const sectorData = sectorMap.get(sector)!;
      sectorData.notional += cc.trade.notionalAmount;
      sectorData.count++;
    });

    // Add baskets as "BASKET" sector for now
    // TODO: In future, unwind basket constituents to their actual sectors
    basketConstituents.forEach((bc) => {
      const sector = 'BASKET';
      if (!sectorMap.has(sector)) {
        sectorMap.set(sector, { notional: 0, count: 0 });
      }
      const sectorData = sectorMap.get(sector)!;
      // Use basket.notional (backend field name)
      const notional = bc.basket?.notional || bc.weightValue || 0;
      sectorData.notional += notional;
      sectorData.count++;
    });

    const totalNotional = Array.from(sectorMap.values()).reduce((sum, s) => sum + s.notional, 0);
    const sectors: SectorExposure[] = [];

    sectorMap.forEach((data, sector) => {
      sectors.push({
        sector,
        notional: data.notional,
        percentage: totalNotional > 0 ? (data.notional / totalNotional) * 100 : 0,
        instrumentCount: data.count,
      });
    });

    setSectorExposures(sectors.sort((a, b) => b.notional - a.notional));
  }, []); // useCallback dependency array

  const getSectorFromIssuer = (issuer: string): string => {
    const sectorMap: Record<string, string> = {
      AAPL: 'TECH',
      MSFT: 'TECH',
      AMZN: 'TECH',
      GOOGL: 'TECH',
      TSLA: 'TECH',
      NFLX: 'TECH',
      META: 'TECH',
      NVDA: 'TECH',
      AMD: 'TECH',
      JPM: 'FINANCIALS',
      BAC: 'FINANCIALS',
      WFC: 'FINANCIALS',
      GS: 'FINANCIALS',
      MS: 'FINANCIALS',
      C: 'FINANCIALS',
    };
    return sectorMap[issuer] || 'OTHER';
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const getHedgeStatusColor = (status: string) => {
    switch (status) {
      case 'BALANCED':
        return 'text-fd-green';
      case 'UNDER_HEDGED':
        return 'text-yellow-400';
      case 'OVER_HEDGED':
        return 'text-blue-400';
      case 'UNHEDGED':
        return 'text-red-400';
      default:
        return 'text-fd-text-muted';
    }
  };

  const getHedgeStatusBadge = (status: string) => {
    switch (status) {
      case 'BALANCED':
        return 'bg-green-900/30 text-green-400 border-green-500';
      case 'UNDER_HEDGED':
        return 'bg-yellow-900/30 text-yellow-400 border-yellow-500';
      case 'OVER_HEDGED':
        return 'bg-blue-900/30 text-blue-400 border-blue-500';
      case 'UNHEDGED':
        return 'bg-red-900/30 text-red-400 border-red-500';
      default:
        return 'bg-fd-dark text-fd-text-muted border-fd-border';
    }
  };

  const getSectorColor = (index: number) => {
    const colors = [
      'bg-fd-green',
      'bg-blue-500',
      'bg-yellow-500',
      'bg-purple-500',
      'bg-pink-500',
      'bg-indigo-500',
      'bg-teal-500',
      'bg-orange-500',
    ];
    return colors[index % colors.length];
  };

  const totalCdsNotional = cdsConstituents.reduce((sum, c) => sum + c.trade.notionalAmount, 0);
  const totalBondNotional = bondConstituents.reduce((sum, c) => sum + c.bond.notional, 0);
  const totalBasketNotional = basketConstituents.reduce((sum, c) => {
    // Use basket.notional (backend field name)
    const notional = c.basket?.notional || c.weightValue || 0;
    return sum + notional;
  }, 0);
  const totalNotional = totalCdsNotional + totalBondNotional + totalBasketNotional;

  return (
    <div className="space-y-6">
      {/* Instrument Distribution */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Instrument Type Breakdown */}
        <div className="bg-fd-darker border border-fd-border rounded-lg p-6">
          <h3 className="text-lg font-semibold text-fd-text mb-4">Instrument Distribution</h3>
          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <div className="w-4 h-4 bg-fd-green rounded"></div>
                <span className="text-fd-text">🛡️ CDS Trades</span>
              </div>
              <div className="text-right">
                <div className="text-fd-text font-medium">{formatCurrency(totalCdsNotional)}</div>
                <div className="text-sm text-fd-text-muted">
                  {totalNotional > 0 ? ((totalCdsNotional / totalNotional) * 100).toFixed(1) : 0}% •{' '}
                  {cdsConstituents.length} positions
                </div>
              </div>
            </div>
            <div className="w-full bg-fd-dark rounded-full h-2">
              <div
                className="bg-fd-green h-2 rounded-full transition-all"
                style={{
                  width: `${totalNotional > 0 ? (totalCdsNotional / totalNotional) * 100 : 0}%`,
                }}
              ></div>
            </div>

            <div className="flex items-center justify-between mt-4">
              <div className="flex items-center space-x-3">
                <div className="w-4 h-4 bg-blue-500 rounded"></div>
                <span className="text-fd-text">📜 Corporate Bonds</span>
              </div>
              <div className="text-right">
                <div className="text-fd-text font-medium">{formatCurrency(totalBondNotional)}</div>
                <div className="text-sm text-fd-text-muted">
                  {totalNotional > 0 ? ((totalBondNotional / totalNotional) * 100).toFixed(1) : 0}%
                  • {bondConstituents.length} positions
                </div>
              </div>
            </div>
            <div className="w-full bg-fd-dark rounded-full h-2">
              <div
                className="bg-blue-500 h-2 rounded-full transition-all"
                style={{
                  width: `${totalNotional > 0 ? (totalBondNotional / totalNotional) * 100 : 0}%`,
                }}
              ></div>
            </div>

            <div className="flex items-center justify-between mt-4">
              <div className="flex items-center space-x-3">
                <div className="w-4 h-4 bg-purple-500 rounded"></div>
                <span className="text-fd-text">🗂️ Credit Baskets</span>
              </div>
              <div className="text-right">
                <div className="text-fd-text font-medium">
                  {formatCurrency(totalBasketNotional)}
                </div>
                <div className="text-sm text-fd-text-muted">
                  {totalNotional > 0 ? ((totalBasketNotional / totalNotional) * 100).toFixed(1) : 0}
                  % • {basketConstituents.length} positions
                </div>
              </div>
            </div>
            <div className="w-full bg-fd-dark rounded-full h-2">
              <div
                className="bg-purple-500 h-2 rounded-full transition-all"
                style={{
                  width: `${totalNotional > 0 ? (totalBasketNotional / totalNotional) * 100 : 0}%`,
                }}
              ></div>
            </div>
          </div>
        </div>

        {/* Sector Exposure */}
        <div className="bg-fd-darker border border-fd-border rounded-lg p-6">
          <h3 className="text-lg font-semibold text-fd-text mb-4">Sector Allocation</h3>
          <div className="space-y-3">
            {sectorExposures.slice(0, 5).map((sector, idx) => (
              <div key={sector.sector} className="space-y-1">
                <div className="flex items-center justify-between text-sm">
                  <div className="flex items-center space-x-2">
                    <div className={`w-3 h-3 rounded ${getSectorColor(idx)}`}></div>
                    <span className="text-fd-text">{sector.sector}</span>
                  </div>
                  <div className="text-fd-text-muted">
                    {sector.percentage.toFixed(1)}% • {sector.instrumentCount} instruments
                  </div>
                </div>
                <div className="w-full bg-fd-dark rounded-full h-1.5">
                  <div
                    className={`${getSectorColor(idx)} h-1.5 rounded-full transition-all`}
                    style={{ width: `${sector.percentage}%` }}
                  ></div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Net Exposure by Issuer */}
      <div className="bg-fd-darker border border-fd-border rounded-lg p-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-fd-text">Net Credit Exposure by Issuer</h3>
          <div className="text-sm text-fd-text-muted">Showing bond positions vs CDS hedges</div>
        </div>

        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-fd-border">
            <thead>
              <tr className="text-left text-xs font-medium text-fd-text-muted uppercase tracking-wider">
                <th className="px-4 py-3">Issuer</th>
                <th className="px-4 py-3">Sector</th>
                <th className="px-4 py-3 text-right">Bond Exposure</th>
                <th className="px-4 py-3 text-right">CDS Protection</th>
                <th className="px-4 py-3 text-right">Net Exposure</th>
                <th className="px-4 py-3 text-center">Hedge Ratio</th>
                <th className="px-4 py-3 text-center">Status</th>
                <th className="px-4 py-3">Recommendation</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-fd-border">
              {issuerExposures.map((exp) => (
                <tr key={exp.issuer} className="hover:bg-fd-dark transition-colors">
                  <td className="px-4 py-3 text-sm font-medium text-fd-text">{exp.issuer}</td>
                  <td className="px-4 py-3 text-sm text-fd-text-muted">{exp.sector}</td>
                  <td className="px-4 py-3 text-sm text-right text-fd-text">
                    {exp.bondNotional > 0 ? formatCurrency(exp.bondNotional) : '-'}
                  </td>
                  <td className="px-4 py-3 text-sm text-right text-fd-text">
                    {exp.cdsProtectionBought > 0 ? formatCurrency(exp.cdsProtectionBought) : '-'}
                  </td>
                  <td
                    className={`px-4 py-3 text-sm text-right font-medium ${
                      exp.netCreditExposure > 0
                        ? 'text-red-400'
                        : exp.netCreditExposure < 0
                          ? 'text-fd-green'
                          : 'text-fd-text'
                    }`}
                  >
                    {formatCurrency(Math.abs(exp.netCreditExposure))}
                    {exp.netCreditExposure > 0
                      ? ' Long'
                      : exp.netCreditExposure < 0
                        ? ' Short'
                        : ''}
                  </td>
                  <td className="px-4 py-3 text-sm text-center text-fd-text">
                    {exp.bondNotional > 0 ? `${exp.hedgeRatio.toFixed(0)}%` : 'N/A'}
                  </td>
                  <td className="px-4 py-3 text-center">
                    <span
                      className={`inline-flex items-center px-2 py-1 rounded text-xs font-medium border ${getHedgeStatusBadge(exp.hedgeStatus)}`}
                    >
                      {exp.hedgeStatus.replace('_', ' ')}
                    </span>
                  </td>
                  <td className={`px-4 py-3 text-sm ${getHedgeStatusColor(exp.hedgeStatus)}`}>
                    {exp.recommendation}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {issuerExposures.length === 0 && (
          <div className="text-center py-8 text-fd-text-muted">
            No exposures to analyze. Add some CDS trades and bonds to see hedge recommendations.
          </div>
        )}
      </div>

      {/* Key Metrics Summary */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <div className="bg-fd-darker border border-fd-border rounded-lg p-4">
          <div className="text-sm text-fd-text-muted mb-1">Total Instruments</div>
          <div className="text-2xl font-semibold text-fd-text">
            {cdsConstituents.length + bondConstituents.length + basketConstituents.length}
          </div>
          <div className="text-xs text-fd-text-muted mt-1">
            {cdsConstituents.length} CDS • {bondConstituents.length} Bonds •{' '}
            {basketConstituents.length} Baskets
          </div>
        </div>

        <div className="bg-fd-darker border border-fd-border rounded-lg p-4">
          <div className="text-sm text-fd-text-muted mb-1">Unique Issuers</div>
          <div className="text-2xl font-semibold text-fd-text">{issuerExposures.length}</div>
          <div className="text-xs text-fd-text-muted mt-1">{sectorExposures.length} sectors</div>
        </div>

        <div className="bg-fd-darker border border-fd-border rounded-lg p-4">
          <div className="text-sm text-fd-text-muted mb-1">Hedge Coverage</div>
          <div className="text-2xl font-semibold text-fd-text">
            {totalBondNotional > 0 ? ((totalCdsNotional / totalBondNotional) * 100).toFixed(0) : 0}%
          </div>
          <div className="text-xs text-fd-text-muted mt-1">CDS / Bond ratio</div>
        </div>

        <div className="bg-fd-darker border border-fd-border rounded-lg p-4">
          <div className="text-sm text-fd-text-muted mb-1">Unhedged Exposure</div>
          <div className="text-2xl font-semibold text-red-400">
            {
              issuerExposures.filter(
                (e) => e.hedgeStatus === 'UNHEDGED' || e.hedgeStatus === 'UNDER_HEDGED'
              ).length
            }
          </div>
          <div className="text-xs text-fd-text-muted mt-1">issuers need attention</div>
        </div>
      </div>
    </div>
  );
};

export default EnhancedOverview;

import React, { useState, useEffect } from 'react';
import {
  portfolioService,
  CdsPortfolio,
  CdsPortfolioConstituent,
  BondPortfolioConstituent,
  BasketPortfolioConstituent,
  PortfolioPricingResponse,
} from '../../services/portfolioService';
import { cdsTradeService, CDSTradeResponse } from '../../services/cdsTradeService';
import AttachInstrumentsModal from './AttachInstrumentsModal';
import SimulationPanel from './simulation/SimulationPanel';
import EnhancedOverview from './EnhancedOverview';

interface PortfolioDetailProps {
  portfolioId: number;
  onBack?: () => void;
}

const PortfolioDetail: React.FC<PortfolioDetailProps> = ({ portfolioId, onBack }) => {
  const [portfolio, setPortfolio] = useState<CdsPortfolio | null>(null);
  const [constituents, setConstituents] = useState<CdsPortfolioConstituent[]>([]);
  const [bondConstituents, setBondConstituents] = useState<BondPortfolioConstituent[]>([]);
  const [basketConstituents, setBasketConstituents] = useState<BasketPortfolioConstituent[]>([]);
  const [riskSummary, setRiskSummary] = useState<PortfolioPricingResponse | null>(null);
  const [loading, setLoading] = useState(true);
  const [pricingLoading, setPricingLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [activeTab, setActiveTab] = useState<
    'overview' | 'constituents' | 'concentration' | 'simulation'
  >('overview');
  const [showAttachModal, setShowAttachModal] = useState(false);
  const [valuationDate, setValuationDate] = useState<string>('');

  useEffect(() => {
    loadPortfolioData();
  }, [portfolioId]);

  const loadPortfolioData = async () => {
    try {
      setLoading(true);
      setError(null);

      const [portfolioData, constituentsData, bondsData, basketsData] = await Promise.all([
        portfolioService.getPortfolioById(portfolioId),
        portfolioService.getConstituents(portfolioId),
        portfolioService.getPortfolioBonds(portfolioId),
        portfolioService.getPortfolioBaskets(portfolioId),
      ]);

      setPortfolio(portfolioData);
      setConstituents(constituentsData);
      setBondConstituents(bondsData);
      setBasketConstituents(basketsData);

      // Try to load cached risk summary
      try {
        const cachedRisk = await portfolioService.getRiskSummary(portfolioId);
        setRiskSummary(cachedRisk);
        setValuationDate(cachedRisk.valuationDate);
      } catch (err) {
        // No cached risk available, that's okay
        console.log('No cached risk summary available');
      }
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to load portfolio';
      setError(errorMessage);
      console.error('Error loading portfolio:', err);
    } finally {
      setLoading(false);
    }
  };

  const getBusinessDaysFromToday = (days: number): string => {
    const today = new Date();
    const target = new Date(today);
    let addedDays = 0;

    while (addedDays < days) {
      target.setDate(target.getDate() + 1);
      const dayOfWeek = target.getDay();
      if (dayOfWeek !== 0 && dayOfWeek !== 6) {
        addedDays++;
      }
    }

    return target.toISOString().split('T')[0];
  };

  const handleQuickValuationDate = async (option: 'today' | 't+1' | 't+7' | 't+45') => {
    let newDate: string;

    switch (option) {
      case 'today':
        newDate = new Date().toISOString().split('T')[0];
        break;
      case 't+1':
        newDate = getBusinessDaysFromToday(1);
        break;
      case 't+7':
        newDate = getBusinessDaysFromToday(7);
        break;
      case 't+45':
        newDate = getBusinessDaysFromToday(45);
        break;
    }

    setValuationDate(newDate);
    await handlePriceWithDate(newDate);
  };

  const handlePriceWithDate = async (date: string) => {
    try {
      setPricingLoading(true);
      setError(null);
      const result = await portfolioService.pricePortfolio(portfolioId, date);
      setRiskSummary(result);
    } catch (err: any) {
      const errorMessage = err.response?.data?.error || err.message || 'Failed to price portfolio';
      setError(errorMessage);
      console.error('Error pricing portfolio:', err);
    } finally {
      setPricingLoading(false);
    }
  };

  const handlePriceNow = async () => {
    const today = new Date().toISOString().split('T')[0];
    setValuationDate(today);
    await handlePriceWithDate(today);
  };

  const handleDetachConstituent = async (constituentId: number) => {
    if (!window.confirm('Are you sure you want to remove this trade from the portfolio?')) {
      return;
    }

    try {
      await portfolioService.detachConstituent(portfolioId, constituentId);
      loadPortfolioData();
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to detach constituent';
      alert(errorMessage);
    }
  };

  const handleDetachBond = async (bondId: number) => {
    if (!window.confirm('Are you sure you want to remove this bond from the portfolio?')) {
      return;
    }

    try {
      await portfolioService.removeBond(portfolioId, bondId);
      loadPortfolioData();
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to remove bond';
      alert(errorMessage);
    }
  };

  const handleDetachBasket = async (basketId: number) => {
    if (!window.confirm('Are you sure you want to remove this basket from the portfolio?')) {
      return;
    }

    try {
      await portfolioService.removeBasket(portfolioId, basketId);
      loadPortfolioData();
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to remove basket';
      alert(errorMessage);
    }
  };

  const handleAttachSuccess = () => {
    setShowAttachModal(false);
    loadPortfolioData();
  };

  const formatCurrency = (amount: number | undefined) => {
    if (amount === undefined || amount === null) return '-';
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    }).format(amount);
  };

  const formatNumber = (num: number | undefined, decimals: number = 2) => {
    if (num === undefined || num === null) return '-';
    return num.toFixed(decimals);
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-fd-green"></div>
      </div>
    );
  }

  if (error || !portfolio) {
    return (
      <div className="bg-red-50 border border-red-200 rounded-md p-4">
        <div className="flex">
          <div className="flex-shrink-0">
            <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
              <path
                fillRule="evenodd"
                d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z"
                clipRule="evenodd"
              />
            </svg>
          </div>
          <div className="ml-3">
            <h3 className="text-sm font-medium text-red-800">Error</h3>
            <div className="mt-2 text-sm text-red-700">
              <p>{error || 'Portfolio not found'}</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-fd-darker rounded-lg shadow-fd border border-fd-border p-6">
        <div className="flex justify-between items-center">
          <div className="flex items-center space-x-4">
            {onBack && (
              <button
                onClick={onBack}
                className="text-fd-green hover:text-fd-green-hover font-medium"
              >
                ← Back
              </button>
            )}
            <div>
              <h2 className="text-2xl font-semibold text-fd-text">{portfolio.name}</h2>
              {portfolio.description && (
                <p className="text-sm text-fd-text-muted mt-1">{portfolio.description}</p>
              )}
            </div>
          </div>
          <div className="flex items-center space-x-3">
            {/* Valuation Date Quick Selector */}
            <div className="flex items-center space-x-2">
              <span className="text-xs text-fd-text-muted">Valuation:</span>
              <div className="flex space-x-1">
                <button
                  onClick={() => handleQuickValuationDate('today')}
                  disabled={pricingLoading}
                  className={`px-2 py-1 text-xs font-medium rounded transition-colors ${
                    valuationDate === new Date().toISOString().split('T')[0]
                      ? 'bg-fd-green text-fd-dark'
                      : 'bg-fd-dark text-fd-text hover:bg-fd-border'
                  } disabled:opacity-50 disabled:cursor-not-allowed`}
                >
                  Today
                </button>
                <button
                  onClick={() => handleQuickValuationDate('t+1')}
                  disabled={pricingLoading}
                  className={`px-2 py-1 text-xs font-medium rounded transition-colors ${
                    valuationDate === getBusinessDaysFromToday(1)
                      ? 'bg-fd-green text-fd-dark'
                      : 'bg-fd-dark text-fd-text hover:bg-fd-border'
                  } disabled:opacity-50 disabled:cursor-not-allowed`}
                >
                  T+1
                </button>
                <button
                  onClick={() => handleQuickValuationDate('t+7')}
                  disabled={pricingLoading}
                  className={`px-2 py-1 text-xs font-medium rounded transition-colors ${
                    valuationDate === getBusinessDaysFromToday(7)
                      ? 'bg-fd-green text-fd-dark'
                      : 'bg-fd-dark text-fd-text hover:bg-fd-border'
                  } disabled:opacity-50 disabled:cursor-not-allowed`}
                >
                  T+7
                </button>
                <button
                  onClick={() => handleQuickValuationDate('t+45')}
                  disabled={pricingLoading}
                  className={`px-2 py-1 text-xs font-medium rounded transition-colors ${
                    valuationDate === getBusinessDaysFromToday(45)
                      ? 'bg-fd-green text-fd-dark'
                      : 'bg-fd-dark text-fd-text hover:bg-fd-border'
                  } disabled:opacity-50 disabled:cursor-not-allowed`}
                >
                  T+45
                </button>
              </div>
            </div>

            <button
              onClick={() => setShowAttachModal(true)}
              className="bg-fd-green hover:bg-fd-green-hover text-fd-dark font-medium py-2 px-4 rounded transition-colors"
            >
              + Add Instruments
            </button>
            <button
              onClick={handlePriceNow}
              disabled={pricingLoading || constituents.length === 0}
              className="bg-fd-green hover:bg-fd-green-hover text-fd-dark font-medium py-2 px-4 rounded transition-colors disabled:bg-fd-text-muted/50 disabled:cursor-not-allowed"
            >
              {pricingLoading ? 'Pricing...' : 'Reprice Now'}
            </button>
          </div>
        </div>
      </div>

      {/* Tabs */}
      <div className="bg-fd-darker rounded-lg shadow-fd border border-fd-border">
        <div className="border-b border-fd-border px-6">
          <nav className="-mb-px flex space-x-8">
            <button
              onClick={() => setActiveTab('overview')}
              className={`py-4 px-1 border-b-2 font-medium text-sm ${
                activeTab === 'overview'
                  ? 'border-fd-green text-fd-green'
                  : 'border-transparent text-fd-text-muted hover:text-fd-text hover:border-fd-border'
              }`}
            >
              Overview
            </button>
            <button
              onClick={() => setActiveTab('constituents')}
              className={`py-4 px-1 border-b-2 font-medium text-sm ${
                activeTab === 'constituents'
                  ? 'border-fd-green text-fd-green'
                  : 'border-transparent text-fd-text-muted hover:text-fd-text hover:border-fd-border'
              }`}
            >
              Constituents (
              {constituents.length + bondConstituents.length + basketConstituents.length})
            </button>
            <button
              onClick={() => setActiveTab('concentration')}
              className={`py-4 px-1 border-b-2 font-medium text-sm ${
                activeTab === 'concentration'
                  ? 'border-fd-green text-fd-green'
                  : 'border-transparent text-fd-text-muted hover:text-fd-text hover:border-fd-border'
              }`}
            >
              Concentration
            </button>
            <button
              onClick={() => setActiveTab('simulation')}
              className={`py-4 px-1 border-b-2 font-medium text-sm ${
                activeTab === 'simulation'
                  ? 'border-fd-green text-fd-green'
                  : 'border-transparent text-fd-text-muted hover:text-fd-text hover:border-fd-border'
              }`}
            >
              Monte Carlo Simulation
            </button>
          </nav>
        </div>

        {/* Tab Content */}
        <div className="p-6">
          {activeTab === 'overview' && (
            <EnhancedOverview
              portfolioId={portfolioId}
              cdsConstituents={constituents}
              bondConstituents={bondConstituents}
              basketConstituents={basketConstituents}
              pricingData={riskSummary}
            />
          )}

          {activeTab === 'constituents' && (
            <>
              {constituents.length === 0 &&
              bondConstituents.length === 0 &&
              basketConstituents.length === 0 ? (
                <div className="text-center py-12">
                  <svg
                    className="mx-auto h-12 w-12 text-fd-text-muted"
                    fill="none"
                    viewBox="0 0 24 24"
                    stroke="currentColor"
                    aria-hidden="true"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"
                    />
                  </svg>
                  <h3 className="mt-2 text-sm font-medium text-fd-text">
                    No instruments in portfolio
                  </h3>
                  <p className="mt-1 text-sm text-fd-text-muted">
                    Add CDS trades or bonds to get started.
                  </p>
                </div>
              ) : (
                <div className="overflow-x-auto">
                  <table className="min-w-full divide-y divide-fd-border">
                    <thead className="bg-fd-dark">
                      <tr>
                        <th
                          scope="col"
                          className="px-6 py-3 text-left text-xs font-medium text-fd-text-muted uppercase tracking-wider"
                        >
                          Type
                        </th>
                        <th
                          scope="col"
                          className="px-6 py-3 text-left text-xs font-medium text-fd-text-muted uppercase tracking-wider"
                        >
                          ID
                        </th>
                        <th
                          scope="col"
                          className="px-6 py-3 text-left text-xs font-medium text-fd-text-muted uppercase tracking-wider"
                        >
                          Reference / Issuer
                        </th>
                        <th
                          scope="col"
                          className="px-6 py-3 text-left text-xs font-medium text-fd-text-muted uppercase tracking-wider"
                        >
                          Notional
                        </th>
                        <th
                          scope="col"
                          className="px-6 py-3 text-left text-xs font-medium text-fd-text-muted uppercase tracking-wider"
                        >
                          Weight Type
                        </th>
                        <th
                          scope="col"
                          className="px-6 py-3 text-left text-xs font-medium text-fd-text-muted uppercase tracking-wider"
                        >
                          Weight Value
                        </th>
                        <th
                          scope="col"
                          className="px-6 py-3 text-left text-xs font-medium text-fd-text-muted uppercase tracking-wider"
                        >
                          Actions
                        </th>
                      </tr>
                    </thead>
                    <tbody className="bg-fd-darker divide-y divide-fd-border">
                      {constituents.map((constituent) => (
                        <tr
                          key={`cds-${constituent.id}`}
                          className="hover:bg-fd-dark transition-colors"
                        >
                          <td className="px-6 py-4 whitespace-nowrap text-sm">
                            <span className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-blue-900 text-blue-200">
                              🛡️ CDS
                            </span>
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-fd-text">
                            CDS-{constituent.trade.id}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-fd-text">
                            {constituent.trade.referenceEntity}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-fd-text">
                            {formatCurrency(constituent.trade.notionalAmount)}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-fd-text-muted">
                            {constituent.weightType}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-fd-text">
                            {constituent.weightType === 'PERCENT'
                              ? `${(constituent.weightValue * 100).toFixed(2)}%`
                              : formatCurrency(constituent.weightValue)}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm">
                            <button
                              onClick={() => handleDetachConstituent(constituent.id)}
                              className="text-red-400 hover:text-red-300 font-medium"
                            >
                              Remove
                            </button>
                          </td>
                        </tr>
                      ))}
                      {bondConstituents.map((bondConstituent) => (
                        <tr
                          key={`bond-${bondConstituent.id}`}
                          className="hover:bg-fd-dark transition-colors"
                        >
                          <td className="px-6 py-4 whitespace-nowrap text-sm">
                            <span className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-green-900 text-green-200">
                              📜 Bond
                            </span>
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-fd-text">
                            {bondConstituent.bond.isin}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-fd-text">
                            {bondConstituent.bond.issuer}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-fd-text">
                            {formatCurrency(bondConstituent.bond.notional)}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-fd-text-muted">
                            {bondConstituent.weightType}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-fd-text">
                            {formatCurrency(bondConstituent.weightValue)}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm">
                            <button
                              onClick={() => handleDetachBond(bondConstituent.bond.id)}
                              className="text-red-400 hover:text-red-300 font-medium"
                            >
                              Remove
                            </button>
                          </td>
                        </tr>
                      ))}
                      {basketConstituents.map((basketConstituent) => (
                        <tr
                          key={`basket-${basketConstituent.id}`}
                          className="hover:bg-fd-dark transition-colors"
                        >
                          <td className="px-6 py-4 whitespace-nowrap text-sm">
                            <span className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-purple-900 text-purple-200">
                              🗂️ Basket
                            </span>
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-fd-text">
                            {basketConstituent.basket.name}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-fd-text">
                            {basketConstituent.basket.basketType === 'FIRST_TO_DEFAULT'
                              ? 'FTD'
                              : basketConstituent.basket.basketType === 'NTH_TO_DEFAULT'
                                ? `${basketConstituent.basket.kthToDefault}th-to-Default`
                                : 'Tranchette'}{' '}
                            ({basketConstituent.basket.numberOfConstituents} names)
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-fd-text">
                            {formatCurrency(basketConstituent.basket.notional)}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-fd-text-muted">
                            {basketConstituent.weightType}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-fd-text">
                            {formatCurrency(basketConstituent.weightValue)}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm">
                            <button
                              onClick={() => handleDetachBasket(basketConstituent.basket.id)}
                              className="text-red-400 hover:text-red-300 font-medium"
                            >
                              Remove
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </>
          )}

          {activeTab === 'concentration' && (
            <div className="space-y-4">
              {riskSummary?.concentration ? (
                <>
                  <div className="bg-fd-dark p-6 rounded-lg border border-fd-border">
                    <h3 className="text-lg font-semibold text-fd-text mb-4">
                      Top 5 CS01 Concentration
                    </h3>
                    <div className="text-3xl font-bold text-fd-green">
                      {formatNumber(riskSummary.concentration.top5PctCs01, 1)}%
                    </div>
                    <p className="text-sm text-fd-text-muted mt-2">
                      of total CS01 is concentrated in the top 5 contributors
                    </p>
                  </div>

                  <div className="bg-fd-dark p-6 rounded-lg border border-fd-border">
                    <h3 className="text-lg font-semibold text-fd-text mb-4">Sector Breakdown</h3>
                    <div className="space-y-3">
                      {riskSummary.concentration.sectorBreakdown.map((sector) => (
                        <div key={sector.sector} className="flex items-center justify-between">
                          <div className="flex items-center space-x-3 flex-1">
                            <span className="text-sm font-medium text-fd-text w-32">
                              {sector.sector}
                            </span>
                            <div className="flex-1 bg-fd-darker rounded-full h-4">
                              <div
                                className={`h-4 rounded-full ${
                                  sector.cs01Pct > 25 ? 'bg-red-500' : 'bg-fd-green'
                                }`}
                                style={{ width: `${Math.min(sector.cs01Pct, 100)}%` }}
                              ></div>
                            </div>
                          </div>
                          <span className="text-sm font-semibold text-fd-text ml-3">
                            {formatNumber(sector.cs01Pct, 1)}%
                          </span>
                        </div>
                      ))}
                    </div>
                  </div>
                </>
              ) : (
                <div className="bg-fd-dark p-6 rounded-lg border border-fd-border text-center">
                  <p className="text-fd-text-muted">
                    No concentration metrics available. Price the portfolio first.
                  </p>
                </div>
              )}
            </div>
          )}

          {activeTab === 'simulation' && <SimulationPanel portfolioId={portfolioId} />}
        </div>
      </div>

      {showAttachModal && (
        <AttachInstrumentsModal
