import React, { useState } from 'react';
import { Contributor } from '../../../services/simulationService';

interface ContributorsTableProps {
  contributors: Contributor[];
}

type SortField = 'entity' | 'marginalElPct' | 'beta';
type SortDirection = 'asc' | 'desc';

const ContributorsTable: React.FC<ContributorsTableProps> = ({ contributors }) => {
  const [sortField, setSortField] = useState<SortField>('marginalElPct');
  const [sortDirection, setSortDirection] = useState<SortDirection>('desc');

  const handleSort = (field: SortField) => {
    if (sortField === field) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortField(field);
      setSortDirection(field === 'entity' ? 'asc' : 'desc');
    }
  };

  const sortedContributors = [...contributors].sort((a, b) => {
    let aVal = a[sortField];
    let bVal = b[sortField];

    if (typeof aVal === 'string') {
      aVal = aVal.toLowerCase();
      bVal = (bVal as string).toLowerCase();
    }

    if (aVal < bVal) return sortDirection === 'asc' ? -1 : 1;
    if (aVal > bVal) return sortDirection === 'asc' ? 1 : -1;
    return 0;
  });

  const SortIcon = ({ field }: { field: SortField }) => {
    if (sortField !== field) {
      return (
        <svg
          className="w-4 h-4 text-fd-text-muted"
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M7 16V4m0 0L3 8m4-4l4 4m6 0v12m0 0l4-4m-4 4l-4-4"
          />
        </svg>
      );
    }

    return sortDirection === 'asc' ? (
      <svg className="w-4 h-4 text-fd-green" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 15l7-7 7 7" />
      </svg>
    ) : (
      <svg className="w-4 h-4 text-fd-green" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
      </svg>
    );
  };

  return (
    <div className="bg-fd-darker rounded-lg border border-fd-border overflow-hidden">
      <div className="overflow-x-auto">
        <table className="w-full">
          <thead className="bg-fd-dark border-b border-fd-border">
            <tr>
              <th
                className="px-4 py-3 text-left text-sm font-medium text-fd-text cursor-pointer hover:bg-fd-border transition-colors"
                onClick={() => handleSort('entity')}
              >
                <div className="flex items-center gap-2">
                  Entity Name
                  <SortIcon field="entity" />
                </div>
              </th>
              <th
                className="px-4 py-3 text-right text-sm font-medium text-fd-text cursor-pointer hover:bg-fd-border transition-colors"
                onClick={() => handleSort('marginalElPct')}
              >
                <div className="flex items-center justify-end gap-2">
                  Marginal EL %
                  <SortIcon field="marginalElPct" />
                </div>
              </th>
              <th
                className="px-4 py-3 text-right text-sm font-medium text-fd-text cursor-pointer hover:bg-fd-border transition-colors"
                onClick={() => handleSort('beta')}
              >
                <div className="flex items-center justify-end gap-2">
                  Beta (β)
                  <SortIcon field="beta" />
                </div>
              </th>
            </tr>
          </thead>
          <tbody className="divide-y divide-fd-border">
            {sortedContributors.map((contributor, index) => (
              <tr key={index} className="hover:bg-fd-dark/50 transition-colors">
                <td className="px-4 py-3 text-sm text-fd-text">{contributor.entity}</td>
                <td className="px-4 py-3 text-sm text-fd-text text-right font-medium">
                  {contributor.marginalElPct.toFixed(2)}%
                </td>
                <td className="px-4 py-3 text-sm text-fd-text-muted text-right">
                  {contributor.beta.toFixed(3)}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {sortedContributors.length === 0 && (
        <div className="p-8 text-center text-fd-text-muted">No contributors data available</div>
      )}
    </div>
  );
};

export default ContributorsTable;

import React, { useState } from 'react';
import { simulationService, SimulationRequest } from '../../../services/simulationService';
import { useSimulationPolling } from '../../../hooks/useSimulationPolling';
import SimulationConfigForm from './SimulationConfigForm';
import SimulationResults from './SimulationResults';
import MetricsGlossaryModal from './MetricsGlossaryModal';

interface SimulationPanelProps {
  portfolioId: number;
}

const SimulationPanel: React.FC<SimulationPanelProps> = ({ portfolioId }) => {
  const [runId, setRunId] = useState<string | null>(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitError, setSubmitError] = useState<string | null>(null);
  const [showGlossary, setShowGlossary] = useState(false);

  const {
    simulation,
    loading: polling,
    error: pollingError,
  } = useSimulationPolling(runId, !!runId);

  const handleSubmit = async (request: SimulationRequest) => {
    try {
      setIsSubmitting(true);
      setSubmitError(null);

      const response = await simulationService.runSimulation(portfolioId, request);
      setRunId(response.runId);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to start simulation';
      setSubmitError(errorMessage);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleCancel = async () => {
    if (runId && window.confirm('Are you sure you want to cancel this simulation?')) {
      try {
        await simulationService.cancelSimulation(runId);
      } catch (err) {
        console.error('Failed to cancel simulation:', err);
      }
    }
  };

  const handleDownload = () => {
    if (simulation) {
      simulationService.downloadResults(simulation);
    }
  };

  const handleReset = () => {
    setRunId(null);
    setSubmitError(null);
  };

  return (
    <div className="space-y-6">
      {/* Header with Help Button */}
      <div className="flex justify-between items-center">
        <div>
          <h3 className="text-lg font-semibold text-fd-text">Monte Carlo Simulation</h3>
          <p className="text-sm text-fd-text-muted mt-1">
            Correlated default risk analysis with portfolio metrics
          </p>
        </div>
        <button
          onClick={() => setShowGlossary(true)}
          className="flex items-center space-x-2 px-3 py-2 text-sm font-medium text-fd-green hover:text-fd-green-hover transition-colors"
        >
          <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
            />
          </svg>
          <span>Help & Glossary</span>
        </button>
      </div>

      {/* Error Display */}
      {(submitError || pollingError) && (
        <div className="bg-red-900/20 border border-red-500/50 rounded-md p-4">
          <div className="flex">
            <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
              <path
                fillRule="evenodd"
                d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z"
                clipRule="evenodd"
              />
            </svg>
            <div className="ml-3">
              <h3 className="text-sm font-medium text-red-400">Error</h3>
              <div className="mt-2 text-sm text-red-300">
                <p>{submitError || pollingError}</p>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Configuration Form or Results */}
      {!runId || !simulation ? (
        <SimulationConfigForm onSubmit={handleSubmit} isSubmitting={isSubmitting} />
      ) : (
        <SimulationResults
          simulation={simulation}
          onCancel={handleCancel}
          onDownload={handleDownload}
          onReset={handleReset}
        />
      )}

      {/* Glossary Modal */}
      <MetricsGlossaryModal isOpen={showGlossary} onClose={() => setShowGlossary(false)} />
    </div>
  );
};

export default SimulationPanel;

import React from 'react';
import { SimulationResponse } from '../../../services/simulationService';
import SimulationStatusBadge from './SimulationStatusBadge';
import MetricsCard from './MetricsCard';
import ContributorsTable from './ContributorsTable';

interface SimulationResultsProps {
  simulation: SimulationResponse;
  onCancel: () => void;
  onDownload: () => void;
  onReset: () => void;
}

const SimulationResults: React.FC<SimulationResultsProps> = ({
  simulation,
  onCancel,
  onDownload,
  onReset,
}) => {
  const isRunning = simulation.status === 'RUNNING' || simulation.status === 'QUEUED';
  const isComplete = simulation.status === 'COMPLETE';
  const isFailed = simulation.status === 'FAILED';

  // For display, use the horizon with most data (prefer 5Y, then 3Y, then 1Y)
  const displayHorizon =
    simulation.horizons?.find((h) => h.tenor === '5Y') ||
    simulation.horizons?.find((h) => h.tenor === '3Y') ||
    simulation.horizons?.[0];

  return (
    <div className="space-y-6">
      {/* Status Header */}
      <div className="bg-fd-darker rounded-lg border border-fd-border p-4">
        <div className="flex items-center justify-between">
          <div>
            <h3 className="text-lg font-medium text-fd-text">Simulation Status</h3>
            <p className="text-sm text-fd-text-muted mt-1">Run ID: {simulation.runId}</p>
          </div>
          <SimulationStatusBadge status={simulation.status} />
        </div>

        {simulation.errorMessage && (
          <div className="mt-4 p-3 bg-red-500/10 border border-red-500/20 rounded text-red-400 text-sm">
            <strong>Error:</strong> {simulation.errorMessage}
          </div>
        )}

        <div className="mt-4 grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
          <div>
            <span className="text-fd-text-muted">Valuation Date:</span>
            <p className="text-fd-text font-medium">{simulation.valuationDate || 'N/A'}</p>
          </div>
          <div>
            <span className="text-fd-text-muted">Paths:</span>
            <p className="text-fd-text font-medium">
              {simulation.paths?.toLocaleString() || 'N/A'}
            </p>
          </div>
          <div>
            <span className="text-fd-text-muted">Horizons:</span>
            <p className="text-fd-text font-medium">
              {simulation.horizons?.map((h) => h.tenor).join(', ') || 'N/A'}
            </p>
          </div>
          <div>
            <span className="text-fd-text-muted">Seed:</span>
            <p className="text-fd-text font-medium">{simulation.seedUsed ?? 'Random'}</p>
          </div>
        </div>

        {/* Action Buttons */}
        <div className="mt-4 flex gap-3">
          {isRunning && (
            <button
              onClick={onCancel}
              className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded text-sm font-medium transition-colors"
            >
              Cancel Simulation
            </button>
          )}
          {isComplete && (
            <>
              <button
                onClick={onDownload}
                className="bg-fd-green hover:bg-fd-green-hover text-fd-dark px-4 py-2 rounded text-sm font-medium transition-colors"
              >
                Download Results (JSON)
              </button>
              <button
                onClick={onReset}
                className="bg-fd-border hover:bg-fd-text-muted text-fd-text px-4 py-2 rounded text-sm font-medium transition-colors"
              >
                Run New Simulation
              </button>
            </>
          )}
          {isFailed && (
            <button
              onClick={onReset}
              className="bg-fd-border hover:bg-fd-text-muted text-fd-text px-4 py-2 rounded text-sm font-medium transition-colors"
            >
              Try Again
            </button>
          )}
        </div>
      </div>

      {/* Metrics Grid - Show ALL Horizons */}
      {isComplete && simulation.horizons && simulation.horizons.length > 0 && (
        <>
          {simulation.horizons.map((horizon, idx) => (
            <div key={horizon.tenor}>
              <h3 className="text-lg font-medium text-fd-text mb-4">
                Risk Metrics ({horizon.tenor})
              </h3>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                <MetricsCard
                  label="Probability of Any Default"
                  value={`${((horizon.pAnyDefault || horizon.panyDefault || 0) * 100).toFixed(2)}%`}
                  description="Likelihood that at least one entity defaults"
                />
                <MetricsCard
                  label="Expected Loss"
                  value={`$${horizon.loss.mean.toLocaleString(undefined, {
                    minimumFractionDigits: 2,
                    maximumFractionDigits: 2,
                  })}`}
                  description="Mean loss across all scenarios"
                />
                <MetricsCard
                  label="VaR 95%"
                  value={`$${horizon.loss.var95.toLocaleString(undefined, {
                    minimumFractionDigits: 2,
                    maximumFractionDigits: 2,
                  })}`}
                  description="Loss not exceeded in 95% of scenarios"
                />
                <MetricsCard
                  label="VaR 99%"
                  value={`$${horizon.loss.var99.toLocaleString(undefined, {
                    minimumFractionDigits: 2,
                    maximumFractionDigits: 2,
                  })}`}
                  description="Loss not exceeded in 99% of scenarios"
                />
                <MetricsCard
                  label="Expected Shortfall 97.5%"
                  value={`$${horizon.loss.es97_5.toLocaleString(undefined, {
                    minimumFractionDigits: 2,
                    maximumFractionDigits: 2,
                  })}`}
                  description="Mean loss in worst 2.5% of scenarios"
                />
                <MetricsCard
                  label="Diversification Benefit"
                  value={`${horizon.diversification.benefitPct.toFixed(2)}%`}
                  description="Risk reduction from portfolio diversification"
                />
              </div>
            </div>
          ))}

          {/* Contributors Table */}
          {simulation.contributors && simulation.contributors.length > 0 && (
            <div>
              <h3 className="text-lg font-medium text-fd-text mb-4">
                Top Contributors to Expected Loss
              </h3>
              <ContributorsTable contributors={simulation.contributors} />
            </div>
          )}
        </>
      )}

      {/* Loading State */}
      {isRunning && (
        <div className="bg-fd-darker rounded-lg border border-fd-border p-8 text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-fd-green mx-auto mb-4"></div>
          <p className="text-fd-text-muted">
            {simulation.status === 'QUEUED'
              ? 'Simulation queued...'
              : 'Running Monte Carlo simulation...'}
          </p>
          <p className="text-fd-text-muted text-sm mt-2">
            This may take a minute or two depending on the number of paths
          </p>
        </div>
      )}
    </div>
  );
};

